    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-points">number of points:</label>
                <input type="range" id="slider-points" min="2" max="200" value="20" step="1">
                <span id="label-points">+ 20</span>
            </div>
            <div class="control-group">
                <label>plot type:</label>
                <button id="btn-3d" class="active">3D</button>
                <button id="btn-2d">2D</button>
            </div>
        </div>

        <div id="visualization">
            <canvas id="canvas-3d"></canvas>
            <div id="div-2d-plot" style="display: none;">
                <canvas id="canvas-2d" width="800" height="400"></canvas>
                <div id="div-2d-text"></div>
            </div>
        </div>
    </div>

    <script>
        // Simplified points generator for demo
        function generatePoints(n) {
            const points = [];
            for(let i = 0; i < n; i++) {
                const theta = Math.random() * 2 * Math.PI;
                const phi = Math.acos(2 * Math.random() - 1);
                const x = Math.sin(phi) * Math.cos(theta);
                const y = Math.sin(phi) * Math.sin(theta);
                const z = Math.cos(phi);
                points.push([x, y, z]);
            }
            return points;
        }

        // Three.js setup
        let scene, camera, renderer, controls, pointGroup;

        function init3D() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.z = 3;

            renderer = new THREE.WebGLRenderer({canvas: document.getElementById('canvas-3d')});
            renderer.setSize(window.innerWidth, window.innerHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);

            const ambient = new THREE.AmbientLight(0xcccccc);
            scene.add(ambient);

            const directional = new THREE.DirectionalLight(0xffffff, 0.7);
            directional.position.set(1, 1, 1);
            scene.add(directional);

            const sphere = new THREE.Mesh(
                new THREE.SphereGeometry(1, 32, 32),
                new THREE.MeshLambertMaterial({
                    color: 0xFFFFE0,
                    transparent: true,
                    opacity: 0.6
                })
            );
            scene.add(sphere);

            pointGroup = new THREE.Group();
            scene.add(pointGroup);

            animate();
        }

        function updatePoints(points) {
            while(pointGroup.children.length > 0) {
                pointGroup.remove(pointGroup.children[0]);
            }

            points.forEach(([x, y, z]) => {
                const solid = new THREE.Mesh(
                    new THREE.SphereGeometry(0.025),
                    new THREE.MeshBasicMaterial({color: 0x0000ff})
                );
                const hollow = new THREE.Mesh(
                    new THREE.RingGeometry(0.02, 0.025),
                    new THREE.MeshBasicMaterial({
                        color: 0x0000ff,
                        side: THREE.DoubleSide
                    })
                );
                solid.position.set(x, y, z);
                hollow.position.set(x, y, z);
                pointGroup.add(solid);
                pointGroup.add(hollow);
            });
        }

        function animate() {
            requestAnimationFrame(animate);

            for(let i = 0; i < pointGroup.children.length; i += 2) {
                const solid = pointGroup.children[i];
                const hollow = pointGroup.children[i + 1];

                const vector = new THREE.Vector3();
                solid.getWorldPosition(vector);

                if(vector.z > 0) {
                    solid.visible = true;
                    hollow.visible = false;
                } else {
                    solid.visible = false;
                    hollow.visible = true;
                    hollow.lookAt(camera.position);
                }
            }

            renderer.render(scene, camera);
        }

        function update2D(points) {
            const canvas = document.getElementById('canvas-2d');
            const ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            let minDist = Infinity;

            points.forEach(([x, y, z], i) => {
                const theta = Math.atan2(y, x);
                const phi = Math.acos(z);

                const canvasX = (theta + Math.PI) * canvas.width / (2 * Math.PI);
                const canvasY = phi * canvas.height / Math.PI;

                ctx.beginPath();
                ctx.arc(canvasX, canvasY, 3, 0, 2 * Math.PI);
                ctx.fillStyle = '#0000ff';
                ctx.fill();

                // Calculate minimum distance
                for(let j = i + 1; j < points.length; j++) {
                    const [x2, y2, z2] = points[j];
                    const dist = Math.sqrt(
                        Math.pow(x - x2, 2) +
                        Math.pow(y - y2, 2) +
                        Math.pow(z - z2, 2)
                    );
                    minDist = Math.min(minDist, dist);
                }
            });

            document.getElementById('div-2d-text').innerHTML = 
                `minimum distance between points = ${minDist.toFixed(5)}\n` +
                `maximum distance between points = 2.00000`;
        }

        // Initialize
        init3D();
        let currentPoints = generatePoints(20);
        updatePoints(currentPoints);

        // Event listeners
        document.getElementById('slider-points').addEventListener('input', (e) => {
            const n = parseInt(e.target.value);
            document.getElementById('label-points').textContent = `+ ${n}`;
            currentPoints = generatePoints(n);
            updatePoints(currentPoints);
            if(document.getElementById('div-2d-plot').style.display !== 'none') {
                update2D(currentPoints);
            }
        });

        document.getElementById('btn-3d').addEventListener('click', () => {
            document.getElementById('btn-3d').classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'block';
            document.getElementById('div-2d-plot').style.display = 'none';
        });

        document.getElementById('btn-2d').addEventListener('click', () => {
            document.getElementById('btn-2d').classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'none';
            document.getElementById('div-2d-plot').style.display = 'block';
            update2D(currentPoints);
        });

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
</body>
</html>
