            display: block;
            margin: 10px 0;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-angle">angle relative to ecliptic</label>
                <input type="range" id="slider-angle" min="0" max="60" step="0.01" value="23.45">
                <span id="value-angle">23.45</span>
            </div>
            <div class="control-group">
                <label for="slider-longitude">longitude of periapsis</label>
                <input type="range" id="slider-longitude" min="0" max="360" step="0.01" value="102.95">
                <span id="value-longitude">102.95</span>
            </div>
            <div class="control-group">
                <label for="slider-eccentricity">orbit eccentricity</label>
                <input type="range" id="slider-eccentricity" min="0" max="0.99" step="0.000001" value="0.016722">
                <span id="value-eccentricity">0.016722</span>
            </div>
        </div>
        <div id="visualization">
            <canvas id="orbit-canvas" width="600" height="300"></canvas>
            <canvas id="plot-canvas" width="600" height="300"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let orbitSketch = new p5((p) => {
            p.setup = function() {
                p.createCanvas(600, 300);
            };

            p.draw = function() {
                p.background('#f0f0f0');
                p.translate(p.width/2, p.height/2);

                let e = parseFloat(document.getElementById('slider-eccentricity').value);
                let omega = parseFloat(document.getElementById('slider-longitude').value) * Math.PI / 180;

                let a = p.width * 0.4;
                let b = a * Math.sqrt(1 - e*e);

                p.push();
                p.rotate(omega);
                p.stroke('#a0522d');
                p.noFill();
                p.ellipse(0, 0, 2*a, 2*b);
                p.pop();
            };
        }, 'orbit-canvas');

        let plotSketch = new p5((p) => {
            function solveKepler(M, e) {
                let E = M;
                for(let i = 0; i < 8; i++) {
                    E = E - (E - e*Math.sin(E) - M)/(1 - e*Math.cos(E));
                }
                return E;
            }

            p.setup = function() {
                p.createCanvas(600, 300);
            };

            p.draw = function() {
                p.background(255);

                let epsilon = parseFloat(document.getElementById('slider-angle').value) * Math.PI / 180;
                let omega = parseFloat(document.getElementById('slider-longitude').value) * Math.PI / 180;
                let e = parseFloat(document.getElementById('slider-eccentricity').value);

                let points = [];
                let prevEOT = 0;
                let unwrapCount = 0;

                for(let M = 0; M <= 2*Math.PI; M += 0.05) {
                    let E = solveKepler(M, e);
                    let v = 2 * Math.atan2(Math.sqrt(1+e)*Math.sin(E/2), Math.sqrt(1-e)*Math.cos(E/2));
                    let lambda = v + omega;
                    let alpha = Math.atan2(Math.cos(epsilon) * Math.sin(lambda), Math.cos(lambda));

                    let EOT = M - alpha;

                    if(points.length > 0) {
                        let diff = EOT - prevEOT;
                        if(Math.abs(diff) > Math.PI) {
                            unwrapCount += diff > 0 ? -1 : 1;
                        }
                    }
                    EOT += unwrapCount * 2*Math.PI;
                    prevEOT = EOT;

                    points.push({
                        x: M,
                        y: EOT * (720 / Math.PI)
                    });
                }

                let minY = Math.min(...points.map(p => p.y));
                let maxY = Math.max(...points.map(p => p.y));
                let padding = 30;

                // Draw axes and grid
                p.stroke(200);
                p.strokeWeight(1);
                for(let i = 1; i <= 6; i++) {
                    let x = p.map(i, 0, 2*Math.PI, padding, p.width-padding);
                    p.line(x, padding, x, p.height-padding);
                }

                p.stroke(0);
                p.strokeWeight(2);
                p.line(padding, p.height-padding, p.width-padding, p.height-padding);
                p.line(padding, padding, padding, p.height-padding);

                // Plot curve
                p.stroke('#ff8c00');
                p.noFill();
                p.beginShape();
                for(let point of points) {
                    let x = p.map(point.x, 0, 2*Math.PI, padding, p.width-padding);
                    let y = p.map(point.y, maxY, minY, padding, p.height-padding);
                    p.vertex(x, y);
                }
                p.endShape();
            };
        }, 'plot-canvas');

        // Update display values and redraw when sliders change
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', function() {
                document.getElementById('value-' + this.id.split('-')[1]).textContent = 
                    parseFloat(this.value).toFixed(this.id.includes('eccentricity') ? 6 : 2);
                orbitSketch.draw();
                plotSketch.draw();
            });
        });
    </script>
</body>
</html>
