                    <label for="step3">3</label>
                    <input type="radio" name="steps" id="step4" value="4">
                    <label for="step4">4</label>
                    <input type="radio" name="steps" id="step5" value="5" checked>
                    <label for="step5">5</label>
                </div>

                <div class="slider-container">
                    <label>
                        <input type="checkbox" id="check-verification" checked>
                        Verification
                    </label>
                </div>

                <div class="slider-container">
                    <label for="slider-plot-range">Plot Range:</label>
                    <input type="range" id="slider-plot-range" min="0.5" max="2" step="0.01" value="1">
                    <span id="plot-range-value" class="value-display">1</span>
                </div>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let canvas;

        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            noLoop();
            redrawScene();
        }

        // Add event listeners to all controls
        document.querySelectorAll('input').forEach(input => {
            input.addEventListener('input', function() {
                if (this.type === 'range') {
                    document.getElementById(this.id + '-value').textContent = this.value;
                }
                redrawScene();
            });
        });

        function redrawScene() {
            clear();
            background(224);

            const c = parseFloat(document.getElementById('slider-c').value);
            const h_C = parseFloat(document.getElementById('slider-h_C').value);
            const delta = parseFloat(document.getElementById('slider-delta').value);
            const plotRange = parseFloat(document.getElementById('slider-plot-range').value);
            const step = parseInt(document.querySelector('input[name="steps"]:checked').value);
            const verification = document.getElementById('check-verification').checked;

            // Set up coordinate system
            translate(width/2, height * 0.6);
            scale(50 * plotRange, -50 * plotRange);

            // Calculate points
            const F = createVector(0, 0);
            const C = createVector(0, h_C);
            const D = createVector(c/2, 0);
            const K = createVector(c, 0);

            // Calculate M (simplified calculation for demo)
            const x_M = c/2;
            const y_M = -Math.sqrt(h_C * h_C - (c*c)/4);
            const M = createVector(x_M, y_M);

            // Calculate B and A
            const x_B = 2*x_M - c/2;
            const B = createVector(x_B, 0);
            const A = createVector(x_B - c, 0);

            // Draw based on step
            if (step >= 1) {
                // Draw line λ
                line(-width/(100*plotRange), 0, width/(100*plotRange), 0);

                // Draw points F, C, D
                drawPoint(F, 'F');
                drawPoint(C, 'C');
                drawPoint(D, 'D');

                // Draw FC
                line(F.x, F.y, C.x, C.y);
            }

            if (step >= 2) {
                drawPoint(K, 'K');
                drawDashedLine(C, D);
                drawDashedLine(D, K);
                drawPoint(M, 'M');
                drawDashedLine(M, C);
                drawDashedLine(M, D);
                drawDashedLine(M, K);

                // Draw arc κ
                const radius = p5.Vector.dist(M, C);
                noFill();
                arc(M.x, M.y, 2*radius, 2*radius, -PI, PI);
            }

            if (step >= 3) {
                drawPoint(B, 'B');
            }

            if (step >= 4) {
                drawPoint(A, 'A');
            }

            if (step >= 5) {
                // Draw triangle ABC
                strokeWeight(2/plotRange);
                line(A.x, A.y, B.x, B.y);
                line(B.x, B.y, C.x, C.y);
                line(C.x, C.y, A.x, A.y);
                strokeWeight(1/plotRange);
            }

            if (verification) {
                const E = createVector(c - x_B, 0);
                drawPoint(E, 'E');
                drawDashedLine(C, E);
                drawDashedLine(K, B);
            }
        }

        function drawPoint(p, label) {
            push();
            strokeWeight(3/plotRange);
            point(p.x, p.y);

            // Reset transformation for text
            resetMatrix();
            translate(width/2, height * 0.6);
            textAlign(CENTER, CENTER);
            text(label, p.x * 50 * plotRange, -p.y * 50 * plotRange - 15);
            pop();
        }

        function drawDashedLine(p1, p2) {
            push();
            drawingContext.setLineDash([5/plotRange, 5/plotRange]);
            line(p1.x, p1.y, p2.x, p2.y);
            drawingContext.setLineDash([]);
            pop();
        }
    </script>
</body>
</html>
