</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-z">z (m)</label>
            <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0">
            <span id="label-z">0.</span>
        </div>
        <div id="plots-container">
            <div id="plot-intensity" class="plot-div"></div>
            <div id="plot-population" class="plot-div"></div>
            <div id="plot-coherence" class="plot-div"></div>
        </div>
    </div>

    <script>
        // Generate time array
        const t_values = Array.from({length: 301}, (_, i) => i - 50);

        // Data generation functions
        function calculateRedData() {
            const I_red = t_values.map(t => 
                (t < -15 || t > 170) ? 0 : (t > 15 ? 1 : 0.5 * (1 + Math.tanh(t/4)))
            );
            const P_red = t_values.map(t => 
                (t < -15) ? 0 : 4.5 * (1 - Math.exp(-(t+15)/50))
            );
            const C_red = t_values.map(t => 
                (t < -15) ? 0 : 2.2 * (1 - Math.exp(-(t+15)/50))
            );
            return {I_red, P_red, C_red};
        }

        function calculateBlueData(z) {
            const t_delay = z / 0.3;
            return t_values.map(t => {
                const t_prime = t - t_delay;
                const I_base = (t_prime < -15) ? 0 : (t_prime > 15 ? 1 : 0.5 * (1 + Math.tanh(t_prime/4)));
                const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
                const I_blue = (t > 170 + t_delay) ? 0 : I_base * envelope;

                const scaling_factor = 1 - 0.45 * (z/32);
                const P_blue = scaling_factor * ((t_prime < -15) ? 0 : 4.5 * (1 - Math.exp(-(t_prime+15)/50)));
                const C_blue = scaling_factor * ((t_prime < -15) ? 0 : 2.2 * (1 - Math.exp(-(t_prime+15)/50)));

                return {I_blue, P_blue, C_blue};
            });
        }

        // Plot initialization
        const redData = calculateRedData();
        let plots = {};

        function initializePlots() {
            const commonLayout = {
                showlegend: true,
                legend: {x: 0.7, y: 0.9},
                margin: {l: 80, r: 20, t: 40, b: 40},
                xaxis: {
                    title: 'time (ns)',
                    range: [-50, 250],
                    zeroline: true
                },
                shapes: [
                    {
                        type: 'line',
                        x0: 0, x1: 0,
                        y0: 0, y1: 1,
                        yref: 'paper',
                        line: {color: 'black', width: 2, dash: 'dash'}
                    },
                    {
                        type: 'line',
                        x0: 0, x1: 0,
                        y0: 0, y1: 1,
                        yref: 'paper',
                        line: {color: 'black', width: 2, dash: 'dash'}
                    }
                ]
            };

            const blueData = calculateBlueData(0);

            plots.intensity = Plotly.newPlot('plot-intensity', [
                {
                    x: t_values,
                    y: redData.I_red,
                    name: 'z = 0 meters',
                    mode: 'lines',
                    line: {color: 'red', width: 2}
                },
                {
                    x: t_values,
                    y: blueData.map(d => d.I_blue),
                    name: 'z = 0. meters',
                    mode: 'lines',
                    line: {color: 'blue', width: 2, dash: 'dash'}
                }
            ], {
                ...commonLayout,
                yaxis: {title: 'intensity (arb)', range: [-0.1, 1.1]},
                annotations: [{x: -45, y: 0.9, text: '(a)', showarrow: false}]
            });

            plots.population = Plotly.newPlot('plot-population', [
                {
                    x: t_values,
                    y: redData.P_red,
                    name: 'z = 0 meters',
                    mode: 'lines',
                    line: {color: 'red', width: 2}
                },
                {
                    x: t_values,
                    y: blueData.map(d => d.P_blue),
                    name: 'z = 0. meters',
                    mode: 'lines',
                    line: {color: 'blue', width: 2, dash: 'dash'}
                }
            ], {
                ...commonLayout,
                yaxis: {title: 'excited-state population × 10<sup>4</sup>', range: [-0.5, 5.5]},
                annotations: [{x: -45, y: 4.9, text: '(b)', showarrow: false}]
            });

            plots.coherence = Plotly.newPlot('plot-coherence', [
                {
                    x: t_values,
                    y: redData.C_red,
                    name: 'z = 0 meters',
                    mode: 'lines',
                    line: {color: 'red', width: 2}
                },
                {
                    x: t_values,
                    y: blueData.map(d => d.C_blue),
                    name: 'z = 0. meters',
                    mode: 'lines',
                    line: {color: 'blue', width: 2, dash: 'dash'}
                }
            ], {
                ...commonLayout,
                yaxis: {title: 'coherence × 10<sup>2</sup>', range: [-0.2, 2.4]},
                annotations: [{x: -45, y: 2.1, text: '(c)', showarrow: false}]
            });
        }

        function updatePlots(z) {
            const blueData = calculateBlueData(z);
            const t_delay = z / 0.3;
            const z_text = z.toFixed(Number.isInteger(z) ? 1 : 5);

            const updateData = {
                'y': [blueData.map(d => d.I_blue)],
                'name': [`z = ${z_text} meters`]
            };
            Plotly.update('plot-intensity', updateData, {
                shapes: [{x0: 0, x1: 0}, {x0: t_delay, x1: t_delay}]
            });

            updateData.y = [blueData.map(d => d.P_blue)];
            Plotly.update('plot-population', updateData, {
                shapes: [{x0: 0, x1: 0}, {x0: t_delay, x1: t_delay}]
            });

            updateData.y = [blueData.map(d => d.C_blue)];
            Plotly.update('plot-coherence', updateData, {
                shapes: [{x0: 0, x1: 0}, {x0: t_delay, x1: t_delay}]
            });
        }

        // Initialize plots
        initializePlots();

        // Event listener for slider
        document.getElementById('slider-z').addEventListener('input', function(e) {
            const z = parseFloat(e.target.value);
            document.getElementById('label-z').textContent = z.toFixed(Number.isInteger(z) ? 1 : 5);
            updatePlots(z);
        });
    </script>
</body>
</html>
