            </div>
            <div>
                <label for="slider-tau">residence time (s)</label>
                <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
                <span id="tau-value">400</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // Constants
        const C_A0 = 10; // mmol/dm³
        const R = 1.987; // cal/mol K
        const E_f = 25000; // cal/mol
        const dH_rxn = -200000; // cal/mol
        const E_r = E_f - dH_rxn; // cal/mol
        const k_f0 = 1e15; // 1/s
        const rhoCp = 20000; // cal/(dm³ K)

        // Get DOM elements
        const sliders = {
            ua: document.getElementById('slider-ua'),
            kr0: document.getElementById('slider-kr0'),
            tf: document.getElementById('slider-tf'),
            tau: document.getElementById('slider-tau')
        };

        const values = {
            ua: document.getElementById('ua-value'),
            kr0: document.getElementById('kr0-value'),
            tf: document.getElementById('tf-value'),
            tau: document.getElementById('tau-value')
        };

        function calculateCurves() {
            const UA = parseFloat(sliders.ua.value);
            const kr0_base = parseFloat(sliders.kr0.value);
            const T_f = parseFloat(sliders.tf.value);
            const tau = parseFloat(sliders.tau.value);

            // Update value displays
            values.ua.textContent = UA.toFixed(1);
            values.kr0.textContent = `${kr0_base.toFixed(1)} × 10¹²`;
            values.tf.textContent = T_f;
            values.tau.textContent = tau;

            // Generate temperature points
            const T = Array.from({length: 151}, (_, i) => 250 + i);

            // Calculate mass balance curve
            const CB_mass = T.map(temp => {
                const k_f = k_f0 * Math.exp(-E_f/(R * temp));
                const k_r = kr0_base * 1e12 * Math.exp(-E_r/(R * temp));
                return (tau * k_f * C_A0)/(1 + tau * (k_f + k_r));
            });

            // Calculate energy balance curve
            const CB_energy = T.map(temp => 
                (rhoCp * (temp - T_f) + UA * tau * (temp - T_f))/(-dH_rxn)
            );

            // Update plot
            const data = [
                {
                    x: T,
                    y: CB_mass,
                    mode: 'lines',
                    name: 'Mass Balance',
                    line: {color: '#008000'}
                },
                {
                    x: T,
                    y: CB_energy,
                    mode: 'lines',
                    name: 'Energy Balance',
                    line: {color: '#0000FF'}
                }
            ];

            const layout = {
                xaxis: {
                    title: 'temperature (K)',
                    range: [250, 400]
                },
                yaxis: {
                    title: 'product concentration (mmol/dm³)',
                    range: [0, 12]
                },
                annotations: [
                    {
                        x: 360,
                        y: CB_mass[110],
                        text: 'mass balance',
                        showarrow: false,
                        font: {color: '#008000'}
                    },
                    {
                        x: 280,
                        y: CB_energy[30],
                        text: 'energy balance',
                        showarrow: false,
                        font: {color: '#0000FF'}
                    }
                ],
                showgrid: true,
                gridcolor: '#e0e0e0'
            };

            Plotly.react('plot-container', data, layout);
        }

        // Add event listeners to all sliders
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', calculateCurves);
        });

        // Initial plot
        calculateCurves();
    </script>
</body>
</html>
