<!DOCTYPE html>
<html>
<head>
    <title>Wankel Rotary Engine: Epitrochoidal Envelopes</title>
    <style>
        #demo-container {
            max-width: 800px;
            margin: 0 auto;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            margin: 20px 0;
            padding: 15px;
            background: #f5f5f5;
            border-radius: 5px;
        }
        #control-panel > div {
            margin: 10px 0;
        }
        #canvas-container {
            text-align: center;
        }
        input[type="range"] {
            width: 200px;
            margin: 0 10px;
        }
        input[type="radio"] {
            margin: 0 5px 0 15px;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <h1>Wankel Rotary Engine: Epitrochoidal Envelopes</h1>
        <p>This Demonstration gives an animation of an epitrochoid and associated planetary-motion envelope curve. The configuration shown has applications in the internal combustion engines invented by Felix Wankel and popularized by Mazda in RX-7 and RX-8 cars. The "eccentricity ratio" changes the shapes of the curves. The "reference frame" determines what is held stationary in the animation: either the epitrochoid (blue), the envelope curve (purple), or the centers of rotation of both curves. The "inner" envelope is the triangular rotor shape used in place of a piston in a Wankel rotary engine, whereas the "outer" envelope is the continuation of the envelope curve along the opposite extreme of motion.</p>

        <div id="control-panel">
            <div>
                <label for="slider-eccentricity">eccentricity ratio</label>
                <input type="range" id="slider-eccentricity" min="0" max="0.3" step="0.0001" value="0.14">
                <span id="label-eccentricity">0.14</span>
            </div>
            <div>
                <span>reference frame</span>
                <input type="radio" name="ref-frame" id="radio-epitrochoid" value="epitrochoid" checked>
                <label for="radio-epitrochoid">epitrochoid</label>
                <input type="radio" name="ref-frame" id="radio-envelope" value="envelope">
                <label for="radio-envelope">envelope</label>
                <input type="radio" name="ref-frame" id="radio-fixed-centers" value="fixed-centers">
                <label for="radio-fixed-centers">fixed centers</label>
            </div>
            <div>
                <span>envelope to show</span>
                <input type="radio" name="envelope-type" id="radio-inner" value="inner" checked>
                <label for="radio-inner">inner</label>
                <input type="radio" name="envelope-type" id="radio-outer" value="outer">
                <label for="radio-outer">outer</label>
                <input type="radio" name="envelope-type" id="radio-both" value="both">
                <label for="radio-both">both</label>
            </div>
            <div>
                <label for="slider-rotation">rotation angle</label>
                <input type="range" id="slider-rotation" min="0" max="6.28318" step="0.0001" value="0">
                <span id="label-rotation">0</span>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let k = 0.14; // eccentricity ratio
        let phi = 0; // rotation angle
        let scale = 150; // pixels per unit

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');

            // Set up event listeners
            document.getElementById('slider-eccentricity').addEventListener('input', function(e) {
                k = parseFloat(e.target.value);
                document.getElementById('label-eccentricity').textContent = k.toFixed(4);
            });

            document.getElementById('slider-rotation').addEventListener('input', function(e) {
                phi = parseFloat(e.target.value);
                document.getElementById('label-rotation').textContent = phi.toFixed(4);
            });
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            // Draw coordinate system
            drawAxes();

            // Get current reference frame
            const refFrame = document.querySelector('input[name="ref-frame"]:checked').value;
            const envelopeType = document.querySelector('input[name="envelope-type"]:checked').value;

            // Calculate transformations
            const phi_shaft = 2 * phi;
            const theta = -phi_shaft / 3;
            const C = createVector(k * cos(phi_shaft), k * sin(phi_shaft));

            push();
            switch(refFrame) {
                case 'epitrochoid':
                    drawEpitrochoid();
                    translate(C.x * scale, C.y * scale);
                    rotate(theta);
                    drawEnvelope(envelopeType);
                    break;

                case 'envelope':
                    drawEnvelope(envelopeType);
                    translate(-C.x * scale, -C.y * scale);
                    rotate(-theta);
                    drawEpitrochoid();
                    break;

                case 'fixed-centers':
                    drawEpitrochoid();
                    translate(C.x * scale, C.y * scale);
                    rotate(theta);
                    drawEnvelope(envelopeType);
                    break;
            }
            pop();
        }

        function drawAxes() {
            stroke(150);
            strokeWeight(1);
            line(-width/2, 0, width/2, 0);
            line(0, -height/2, 0, height/2);

            // Draw tick marks
            for(let i = -2; i <= 2; i++) {
                const x = i * scale;
                line(x, -5, x, 5);
                line(-5, x, 5, x);
                if(i !== 0) {
                    textAlign(CENTER);
                    text(i.toFixed(1), x, 20);
                    textAlign(RIGHT);
                    text((-i).toFixed(1), -10, x + 5);
                }
            }
        }

        function drawEpitrochoid() {
            stroke(50, 100, 200);
            strokeWeight(2);
            noFill();

            beginShape();
            for(let t = 0; t <= TWO_PI; t += TWO_PI/200) {
                const x = (cos(t) - k * cos(3*t)) * scale;
                const y = (sin(t) - k * sin(3*t)) * scale;
                vertex(x, y);
            }
            endShape(CLOSE);
        }

        function drawEnvelope(type) {
            stroke(230, 150, 0);
            strokeWeight(2);
            noFill();

            const R = 1;

            if(type === 'inner' || type === 'both') {
                beginShape();
                for(let t = 0; t <= TWO_PI; t += TWO_PI/200) {
                    const x = ((2/3)*R*cos(t) + k*cos(2*t)) * scale;
                    const y = ((2/3)*R*sin(t) - k*sin(2*t)) * scale;
                    vertex(x, y);
                }
                endShape(CLOSE);
            }

            if(type === 'outer' || type === 'both') {
                beginShape();
                for(let t = 0; t <= TWO_PI; t += TWO_PI/200) {
                    const x = ((2/3)*R*cos(t) - k*cos(2*t)) * scale;
                    const y = ((2/3)*R*sin(t) + k*sin(2*t)) * scale;
                    vertex(x, y);
                }
                endShape(CLOSE);
            }
        }
    </script>
</body>
</html>
