<!DOCTYPE html>
<html>
<head>
    <title>Stability of Multilayered Emulsions</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        #container {
            display: flex;
            min-height: 100vh;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background: #f5f5f5;
        }
        #vis-area {
            flex: 1;
            padding: 20px;
        }
        .slider-group {
            margin: 15px 0;
        }
        input[type="range"] {
            width: 100%;
        }
        hr {
            margin: 20px 0;
        }
        #plot-container {
            width: 100%;
            height: 600px;
        }
        .value-display {
            float: right;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <h4>Stability of Multilayered Emulsions</h4>

            <div class="slider-group">
                <label for="slider-phi">\(\phi\)</label>
                <span id="phi-value-display" class="value-display"></span>
                <input type="range" id="slider-phi" min="0.0001" max="0.05" step="0.0001" value="0.005">
            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-gamma-sat">\(\Gamma_{sat}\) (mg/m²)</label>
                <span id="gamma-sat-value-display" class="value-display"></span>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
            </div>

            <div class="slider-group">
                <label for="slider-r">r (nm)</label>
                <span id="r-value-display" class="value-display"></span>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
            </div>

            <div class="slider-group">
                <label for="slider-rpe">r<sub>pe</sub> (nm)</label>
                <span id="rpe-value-display" class="value-display"></span>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
            </div>

            <div class="slider-group">
                <label for="slider-mw">M<sub>W</sub> (kDa)</label>
                <span id="mw-value-display" class="value-display"></span>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
            </div>

            <hr>

            <div class="slider-group">
                <label for="slider-phi-axis">\(\phi\) axis max.</label>
                <span id="phi-axis-value-display" class="value-display"></span>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
            </div>

            <div class="slider-group">
                <label for="slider-c-axis">C axis max.</label>
                <span id="c-axis-value-display" class="value-display"></span>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
            </div>
        </div>

        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display">
                    <span style="color:blue;">y₁(ϕ)</span>,
                    <span style="color:red;">y₂(ϕ)</span>, and
                    <span style="color:green;">y₃(ϕ)</span>
                </p>
                <p id="phimax-display"></p>
                <p id="range-display"></p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        function y1(phi, params) {
            return (3 * params.gamma_sat / params.r) * C_UNIT_CONVERSION * phi;
        }

        function y2(phi, params) {
            return y1(phi, params) / (1 - phi);
        }

        function y3(phi, params) {
            return y1(phi, params) + (K_DEPLETION * params.mw / (params.rpe ** 3)) / phi;
        }

        function calculatePhiMax(params) {
            const A = (3 * params.gamma_sat / params.r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * params.mw / (params.rpe ** 3);
            return Math.sqrt(B / A);
        }

        function generateData(params, phiMax, phiAxisMax) {
            const points = 200;
            const phi = Array.from({length: points}, (_, i) => i * phiAxisMax / points);

            const y1Data = phi.map(p => y1(p, params));
            const y2Data = phi.map(p => y2(p, params));
            const y3Data = phi.map(p => y3(p, params));

            return {phi, y1Data, y2Data, y3Data};
        }

        function updatePlot() {
            const params = {
                gamma_sat: parseFloat(document.getElementById('slider-gamma-sat').value),
                r: parseFloat(document.getElementById('slider-r').value),
                rpe: parseFloat(document.getElementById('slider-rpe').value),
                mw: parseFloat(document.getElementById('slider-mw').value)
            };

            const phi = parseFloat(document.getElementById('slider-phi').value);
            const phiAxisMax = parseFloat(document.getElementById('slider-phi-axis').value);
            const cAxisMax = parseFloat(document.getElementById('slider-c-axis').value);

            const phiMax = calculatePhiMax(params);
            const data = generateData(params, phiMax, phiAxisMax);

            const traces = [
                {
                    x: data.phi,
                    y: data.y1Data,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'blue'},
                    name: 'y₁'
                },
                {
                    x: data.phi,
                    y: data.y2Data,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'red'},
                    name: 'y₂'
                },
                {
                    x: data.phi,
                    y: data.y3Data,
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'green'},
                    name: 'y₃'
                },
                {
                    x: [phi],
                    y: [y1(phi, params)],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {color: 'blue', size: 10},
                    showlegend: false
                },
                {
                    x: [phi],
                    y: [y2(phi, params)],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {color: 'red', size: 10},
                    showlegend: false
                },
                {
                    x: [phi],
                    y: [y3(phi, params)],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {color: 'green', size: 10},
                    showlegend: false
                }
            ];

            const layout = {
                xaxis: {
                    title: 'ϕ (volume fraction)',
                    range: [0, phiAxisMax]
                },
                yaxis: {
                    title: 'C (kg/m³)',
                    range: [0, cAxisMax]
                },
                shapes: [{
                    type: 'line',
                    x0: phi,
                    x1: phi,
                    y0: 0,
                    y1: cAxisMax,
                    line: {
                        color: 'grey',
                        dash: 'dash'
                    }
                }],
                showlegend: false
            };

            Plotly.react('plot-container', traces, layout);

            // Update info display
            document.getElementById('phimax-display').innerHTML = `ϕ<sub>max</sub> = ${phiMax.toFixed(4)}`;
            document.getElementById('range-display').innerHTML = 
                `Stability range at ϕ = ${phi}: ${y2(phi, params).toFixed(2)} - ${y3(phi, params).toFixed(2)} kg/m³`;
        }

        // Initialize displays and attach event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            const displayId = slider.id.replace('slider-', '') + '-value-display';
            document.getElementById(displayId).textContent = slider.value;

            slider.addEventListener('input', (e) => {
                document.getElementById(displayId).textContent = e.target.value;
                if (slider.id === 'slider-phi-axis') {
                    document.getElementById('slider-phi').max = e.target.value;
                }
                updatePlot();
            });
        });

        // Initial plot
        window.addEventListener('load', updatePlot);
    </script>
</body>
</html>
