        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-time">Time relative to collision (s)</label>
                <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
                <span id="label-time">-10.0</span>
            </div>

            <h4>Object 1</h4>
            <div class="slider-container">
                <label for="slider-m1">Mass (kg)</label>
                <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
                <span id="label-m1">5.00</span>
            </div>
            <div class="slider-container">
                <label for="slider-v1">Speed (m/s)</label>
                <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
                <span id="label-v1">10.0</span>
            </div>

            <h4>Object 2</h4>
            <div class="slider-container">
                <label for="slider-m2">Mass (kg)</label>
                <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
                <span id="label-m2">3.00</span>
            </div>
            <div class="slider-container">
                <label for="slider-v2">Speed (m/s)</label>
                <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
                <span id="label-v2">5.0</span>
            </div>

            <label>Observer velocity (m/s)</label>
            <div id="joystick-container">
                <div id="joystick-knob"></div>
            </div>
            <div>vx: <span id="label-v0x">1.10</span></div>
            <div>vy: <span id="label-v0y">1.10</span></div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let v0x = 1.1;
        let v0y = 1.1;
        let isDragging = false;

        // Joystick handling
        const joystick = document.getElementById('joystick-container');
        const knob = document.getElementById('joystick-knob');

        function updateKnobPosition(x, y) {
            const bounds = joystick.getBoundingClientRect();
            const centerX = bounds.width / 2;
            const centerY = bounds.height / 2;

            // Convert to -5 to 5 range
            v0x = (x - centerX) / (bounds.width / 2) * 5;
            v0y = (centerY - y) / (bounds.height / 2) * 5;

            // Constrain values
            v0x = Math.max(-5, Math.min(5, v0x));
            v0y = Math.max(-5, Math.min(5, v0y));

            // Update knob position
            knob.style.left = `${x}px`;
            knob.style.top = `${y}px`;

            // Update labels
            document.getElementById('label-v0x').textContent = v0x.toFixed(2);
            document.getElementById('label-v0y').textContent = v0y.toFixed(2);

            updateSimulation();
        }

        joystick.addEventListener('mousedown', (e) => {
            isDragging = true;
            updateKnobPosition(e.offsetX, e.offsetY);
        });

        document.addEventListener('mousemove', (e) => {
            if (isDragging) {
                const bounds = joystick.getBoundingClientRect();
                const x = e.clientX - bounds.left;
                const y = e.clientY - bounds.top;
                updateKnobPosition(x, y);
            }
        });

        document.addEventListener('mouseup', () => {
            isDragging = false;
        });

        // Initialize knob position
        const bounds = joystick.getBoundingClientRect();
        const initX = (v0x / 5 + 1) * bounds.width / 2;
        const initY = (-v0y / 5 + 1) * bounds.height / 2;
        knob.style.left = `${initX}px`;
        knob.style.top = `${initY}px`;

        // Slider event handlers
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', (e) => {
                document.getElementById(`label-${e.target.id.split('-')[1]}`).textContent = 
                    parseFloat(e.target.value).toFixed(1);
                updateSimulation();
            });
        });

        // P5.js sketch
        function sketch(p) {
            const width = 800;
            const height = 600;

            p.setup = function() {
                p.createCanvas(width, height);
            }

            p.draw = function() {
                p.background(255);

                // Get current parameters
                const time = parseFloat(document.getElementById('slider-time').value);
                const m1 = parseFloat(document.getElementById('slider-m1').value);
                const m2 = parseFloat(document.getElementById('slider-m2').value);
                const v1 = parseFloat(document.getElementById('slider-v1').value);

                // Calculate CM frame velocities
                const v2 = (m1/m2) * v1;

                // Draw particles
                const centerX = width/2;
                const centerY = height/2;

                // Transform to observer frame and draw
                p.push();
                p.translate(centerX, centerY);

                // Draw trajectories
                p.drawingContext.setLineDash([5, 5]);

                // Particle 1 (red)
                p.stroke(255, 0, 0);
                p.noFill();
                drawParticleTrajectory(p, time, v1, m1, true);

                // Particle 2 (blue)
                p.stroke(0, 0, 255);
                drawParticleTrajectory(p, time, v2, m2, false);

                // Observer velocity vector
                p.stroke(0);
                p.drawingContext.setLineDash([]);
                p.line(0, 0, -v0x*20, -v0y*20);

                // Display metrics
                p.noStroke();
                p.fill(0);
                p.textAlign(p.LEFT);
                p.text(`Observer speed: ${Math.sqrt(v0x*v0x + v0y*v0y).toFixed(2)} m/s`, -width/2 + 10, -height/2 + 20);
                p.text(`v₁: ${v1.toFixed(2)} m/s`, -width/2 + 10, -height/2 + 40);
                p.text(`v₂: ${v2.toFixed(2)} m/s`, -width/2 + 10, -height/2 + 60);

                const energy = 0.5*m1*v1*v1 + 0.5*m2*v2*v2;
                p.text(`Total Energy: ${energy.toFixed(2)} J`, -width/2 + 10, -height/2 + 80);

                p.pop();
            }

            function drawParticleTrajectory(p, time, speed, mass, isFirst) {
                const sign = isFirst ? 1 : -1;
                const radius = Math.sqrt(mass) * 5;

                // Pre-collision trajectory
                if (time < 0) {
                    p.line(-1000, 0, 0, 0);
                    p.circle(time * speed * sign + v0x*time, v0y*time, radius*2);
                } else {
                    // Post-collision trajectory (45-degree scatter)
                    const angle = isFirst ? -Math.PI/4 : Math.PI*3/4;
                    const vx = speed * Math.cos(angle);
                    const vy = speed * Math.sin(angle);
                    p.line(0, 0, 1000*vx, 1000*vy);
                    p.circle(time * vx + v0x*time, time * vy + v0y*time, radius*2);
                }
            }
        }

        function updateSimulation() {
            // This function would be called whenever parameters change
            // The p5 draw loop will handle the actual updates
        }

        new p5(sketch, 'canvas-container');
    </script>
</body>
</html>
