<body>
    <div class="main-container">
        <div class="control-panel">
            <div class="slider-container">
                <label for="shift-slider">shift</label>
                <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
            </div>
            <input type="text" id="text-input" list="phrase-list" value="abcd">
            <datalist id="phrase-list">
                <option value="abcd">
                <option value="test phrase">
                <option value="to be or not to be">
                <option value="the quick brown fox jumped over the lazy dog">
            </datalist>
        </div>

        <div class="display-area">
            <div class="alphabet-container">
                <div class="alphabet-row" id="plaintext-alphabet"></div>
                <div class="alphabet-row" id="ciphertext-alphabet"></div>
            </div>

            <div class="key-section">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>

            <div class="text-output">
                <p id="plaintext-output"><span class="plaintext-label">plaintext :</span> abcd</p>
                <p id="ciphertext-output"><span class="ciphertext-label">ciphertext:</span> MNOP</p>
            </div>
        </div>
    </div>

    <script>
        function encrypt(text, shift) {
            let result = '';
            for (let i = 0; i < text.length; i++) {
                const char = text[i];
                if (char >= 'a' && char <= 'z') {
                    const index = char.charCodeAt(0) - 97;
                    const shiftedIndex = (index + shift) % 26;
                    result += String.fromCharCode(65 + shiftedIndex);
                } else {
                    result += char;
                }
            }
            return result;
        }

        function updateCipher() {
            const shiftValue = parseInt(document.getElementById('shift-slider').value);
            const textValue = document.getElementById('text-input').value;

            // Update alphabet mappings
            const plaintextAlphabet = document.getElementById('plaintext-alphabet');
            const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');

            // Clear existing content
            plaintextAlphabet.innerHTML = '';
            ciphertextAlphabet.innerHTML = '';

            // Generate alphabet rows
            for (let i = 0; i < 26; i++) {
                const plainSpan = document.createElement('span');
                plainSpan.textContent = String.fromCharCode(97 + i);
                plaintextAlphabet.appendChild(plainSpan);

                const cipherSpan = document.createElement('span');
                const shiftedIndex = (i + shiftValue) % 26;
                cipherSpan.textContent = String.fromCharCode(65 + shiftedIndex);
                ciphertextAlphabet.appendChild(cipherSpan);
            }

            // Update key displays
            document.getElementById('encryption-key-display').textContent = 'encryption key: ' + shiftValue;
            const decryptionKey = (26 - shiftValue) % 26;
            document.getElementById('decryption-key-display').textContent = 'decryption key: ' + decryptionKey;

            // Update text outputs
            document.getElementById('plaintext-output').innerHTML = '<span class="plaintext-label">plaintext :</span> ' + textValue;
            const ciphertext = encrypt(textValue, shiftValue);
            document.getElementById('ciphertext-output').innerHTML = '<span class="ciphertext-label">ciphertext:</span> ' + ciphertext;
        }

        // Event listeners
        document.getElementById('shift-slider').addEventListener('input', updateCipher);
        document.getElementById('text-input').addEventListener('input', updateCipher);

        // Initialize on page load
        updateCipher();
    </script>
</body>
</html>
