<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Inclined Plane Physics Simulation</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.7.0/p5.min.js"></script>
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
            display: flex;
            height: 100vh;
            overflow: hidden;
        }

        #control-panel {
            width: 200px;
            background-color: #f0f0f0;
            padding: 20px;
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 20px;
            box-shadow: 2px 0 5px rgba(0,0,0,0.1);
        }

        #slider-angle {
            writing-mode: bt-lr; /* IE */
            -webkit-appearance: slider-vertical; /* WebKit */
            width: 150px;
            height: 30px;
            margin: 20px 0;
        }

        @supports (writing-mode: vertical-lr) {
            #slider-angle {
                writing-mode: vertical-lr;
                direction: rtl;
            }
        }

        label[for="slider-angle"] {
            font-weight: bold;
            margin-top: 10px;
        }

        #animation-controls {
            display: flex;
            flex-direction: column;
            align-items: center;
            gap: 10px;
        }

        #animation-controls p {
            margin: 0 0 10px 0;
            font-weight: bold;
        }

        button {
            width: 60px;
            height: 30px;
            font-size: 16px;
            cursor: pointer;
            border: 1px solid #333;
            background-color: #fff;
            border-radius: 4px;
        }

        button:hover {
            background-color: #e0e0e0;
        }

        #visualization-area {
            flex: 1;
            display: flex;
            flex-direction: column;
            padding: 20px;
        }

        #info-display {
            font-size: 18px;
            margin-bottom: 20px;
            padding: 10px;
            background-color: #f9f9f9;
            border: 1px solid #ddd;
            border-radius: 4px;
        }

        #p5-canvas {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <label for="slider-angle">slope θ</label>
        <input type="range" id="slider-angle" min="10" max="80" value="30" step="1" orient="vertical">
        <div id="animation-controls">
            <p>release block</p>
            <button id="btn-play">▶</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|<</button>
        </div>
    </div>
    <div id="visualization-area">
        <p id="info-display">slope = \( \theta \) = 30°; acceleration = \( g \sin(\theta) \) = 4.905 m/s²</p>
        <div id="p5-canvas"></div>
    </div>

    <script>
        // Configure MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };

        // Global variables
        let theta = 30; // degrees
        let g = 9.81; // m/s²
        let isPlaying = false;
        let blockPosition = 0;
        let blockVelocity = 0;
        let animationTime = 0;
        let lastFrameTime = 0;

        // Get DOM elements
        const sliderAngle = document.getElementById('slider-angle');
        const btnPlay = document.getElementById('btn-play');
        const btnPause = document.getElementById('btn-pause');
        const btnReset = document.getElementById('btn-reset');
        const infoDisplay = document.getElementById('info-display');

        // Update info display
        function updateInfoDisplay() {
            const acceleration = g * Math.sin(theta * Math.PI / 180);
            infoDisplay.innerHTML = `slope = \\( \\theta \\) = ${theta}°; acceleration = \\( g \\sin(\\theta) \\) = ${acceleration.toFixed(3)} m/s²`;
            MathJax.typeset([infoDisplay]);
        }

        // Event listeners
        sliderAngle.addEventListener('input', (e) => {
            theta = parseInt(e.target.value);
            resetAnimation();
            updateInfoDisplay();
        });

        btnPlay.addEventListener('click', () => {
            isPlaying = true;
            lastFrameTime = millis();
        });

        btnPause.addEventListener('click', () => {
            isPlaying = false;
        });

        btnReset.addEventListener('click', () => {
            resetAnimation();
        });

        function resetAnimation() {
            isPlaying = false;
            blockPosition = 0;
            blockVelocity = 0;
            animationTime = 0;
        }

        // p5.js setup and draw
        function setup() {
            let canvas = createCanvas(800, 600);
            canvas.parent('p5-canvas');
            textAlign(CENTER, CENTER);
        }

        function draw() {
            background(255);

            // Calculate dimensions
            const rampLength = 400;
            const rampHeight = rampLength * Math.sin(theta * Math.PI / 180);
            const rampBase = rampLength * Math.cos(theta * Math.PI / 180);

            // Translate to bottom-left corner of ramp
            push();
            translate(100, height - 100);

            // Draw inclined plane
            fill('lightblue');
            stroke('yellow');
            strokeWeight(2);
            triangle(0, 0, rampBase, 0, rampBase, -rampHeight);

            // Draw angle marker
            noFill();
            stroke(0);
            strokeWeight(1);
            arc(0, 0, 60, 60, -theta * Math.PI / 180, 0);
            push();
            translate(40 * Math.cos(-theta * Math.PI / 360), 40 * Math.sin(-theta * Math.PI / 360));
            fill(0);
            noStroke();
            text('θ', 0, 0);
            pop();

            // Draw coordinate axes
            stroke(0);
            strokeWeight(2);
            drawArrow(0, 0, 80, 0, 'black', 'x');
            drawArrow(0, 0, 0, -80, 'black', 'y');

            // Calculate block position
            const blockSize = 40;
            let blockX = blockPosition * Math.cos(theta * Math.PI / 180);
            let blockY = -blockPosition * Math.sin(theta * Math.PI / 180);

            // Ensure block stays on ramp
            if (blockPosition > rampLength - blockSize / Math.cos(theta * Math.PI / 180)) {
                blockPosition = rampLength - blockSize / Math.cos(theta * Math.PI / 180);
                isPlaying = false;
            }

            // Draw block
            push();
            translate(blockX, blockY);
            rotate(-theta * Math.PI / 180);
            fill('darkgrey');
            stroke('black');
            strokeWeight(1);
            rectMode(CENTER);
            rect(0, -blockSize/2, blockSize, blockSize);

            // Draw force vectors from center of block
            const vectorScale = 100;
            const mg = vectorScale;

            // Gravity force (red, pointing down)
            push();
            rotate(theta * Math.PI / 180);
            drawArrow(0, -blockSize/2, 0, -blockSize/2 + mg, 'red', 'F_grav=mg');

            // Normal force (deepskyblue, perpendicular to slope)
            const normalForce = mg * Math.cos(theta * Math.PI / 180);
            push();
            rotate(-theta * Math.PI / 180);
            drawArrow(0, -blockSize/2, 0, -blockSize/2 - normalForce, 'deepskyblue', '"normal" force');
            pop();

            // Net force (purple, along slope)
            const netForce = mg * Math.sin(theta * Math.PI / 180);
            push();
            rotate(-theta * Math.PI / 180);
            drawArrow(0, -blockSize/2, netForce, -blockSize/2, 'purple', 'F_net = m g sin(θ)');
            pop();

            // Draw force decomposition
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);

            // Perpendicular component
            line(0, -blockSize/2, normalForce * Math.sin(theta * Math.PI / 180), 
                 -blockSize/2 - normalForce * Math.cos(theta * Math.PI / 180));

            // Parallel component
            line(0, -blockSize/2 + mg, 
                 normalForce * Math.sin(theta * Math.PI / 180), 
                 -blockSize/2 - normalForce * Math.cos(theta * Math.PI / 180));

            drawingContext.setLineDash([]);

            // Label perpendicular component
            push();
            translate(normalForce * Math.sin(theta * Math.PI / 180) / 2, 
                     -blockSize/2 - normalForce * Math.cos(theta * Math.PI / 180) / 2);
            fill(0);
            noStroke();
            textSize(12);
            text('m g cos(θ)', 0, -15);
            pop();

            // Draw angle between F_grav and perpendicular component
            noFill();
            stroke(0);
            strokeWeight(1);
            arc(0, -blockSize/2, 40, 40, Math.PI/2 - theta * Math.PI / 180, Math.PI/2);
            push();
            translate(25 * Math.cos(Math.PI/2 - theta * Math.PI / 360), 
                     -blockSize/2 + 25 * Math.sin(Math.PI/2 - theta * Math.PI / 360));
            fill(0);
            noStroke();
            textSize(12);
            text('θ', 0, 0);
            pop();

            pop();
            pop();
            pop();

            // Update physics if playing
            if (isPlaying) {
                const currentTime = millis();
                const deltaTime = (currentTime - lastFrameTime) / 1000; // Convert to seconds
                lastFrameTime = currentTime;
                animationTime += deltaTime;

                const acceleration = g * Math.sin(theta * Math.PI / 180);
                blockPosition = 0.5 * acceleration * animationTime * animationTime * 50; // Scale for visualization
            }
        }

        function drawArrow(x1, y1, x2, y2, color, label) {
            push();
            stroke(color);
            strokeWeight(2);
            line(x1, y1, x2, y2);

            // Arrowhead
            const angle = atan2(y2 - y1, x2 - x1);
            const arrowSize = 10;
            push();
            translate(x2, y2);
            rotate(angle);
            line(0, 0, -arrowSize, -arrowSize/2);
            line(0, 0, -arrowSize, arrowSize/2);
            pop();

            // Label
            if (label) {
                push();
                translate((x1 + x2) / 2, (y1 + y2) / 2);
                fill(color);
                noStroke();
                textSize(12);
                text(label, 15, 0);
                pop();
            }
            pop();
        }
    </script>
</body>
</html>
