        function binaryToHex(binary) {
            let hex = '';
            for (let i = 0; i < binary.length; i += 4) {
                hex += parseInt(binary.substr(i, 4), 2).toString(16);
            }
            return hex;
        }

        function permute(block, table) {
            return table.map(pos => block[pos - 1]).join('');
        }

        function leftShift(bits, shifts) {
            return bits.substring(shifts) + bits.substring(0, shifts);
        }

        function xor(a, b) {
            return a.split('').map((bit, i) => bit === b[i] ? '0' : '1').join('');
        }

        function sBoxSubstitution(input) {
            let output = '';
            for (let i = 0; i < 8; i++) {
                const chunk = input.substr(i * 6, 6);
                const row = parseInt(chunk[0] + chunk[5], 2);
                const col = parseInt(chunk.substring(1, 5), 2);
                const val = S_BOXES[i][row][col];
