    <script>
        // Global state
        let state = {
            currentAlgorithm: 'shellsort',
            numElements: 10,
            sortHistory: [],
            currentStep: 1,
            isPlaying: false,
            animationInterval: null
        };

        // p5.js sketch
        let sketch = function(p) {
            const margin = 40;
            const canvasWidth = 800;
            const canvasHeight = 400;

            p.setup = function() {
                let canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('bar-chart-canvas');
                p.colorMode(p.HSB);
                p.noLoop();

                // Initialize with random data
                initializeData();
            };

            p.draw = function() {
                p.background(255);

                if (state.sortHistory.length === 0) return;

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(margin, margin, margin, canvasHeight - margin);
                p.line(margin, canvasHeight - margin, canvasWidth - margin, canvasHeight - margin);

                // Draw Y-axis labels
                p.textAlign(p.RIGHT, p.CENTER);
                p.noStroke();
                for (let i = 0; i <= 20; i += 5) {
                    let y = p.map(i, 0, 20, canvasHeight - margin, margin);
                    p.text(i, margin - 10, y);
                    p.stroke(200);
                    p.strokeWeight(1);
                    p.line(margin, y, canvasWidth - margin, y);
                    p.noStroke();
                }

                // Draw bars
                let currentArray = state.sortHistory[state.currentStep - 1];
                if (!currentArray) return;

                let barWidth = (canvasWidth - 2 * margin) / state.numElements;

                for (let i = 0; i < currentArray.length; i++) {
                    let value = currentArray[i];
                    let x = margin + i * barWidth;
                    let barHeight = p.map(value, 1, state.numElements, 0, canvasHeight - 2 * margin);
                    let y = canvasHeight - margin - barHeight;

                    // Color based on value
                    let hue = p.map(value, 1, state.numElements, 240, 0);
                    p.fill(hue, 100, 100);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.rect(x, y, barWidth - 2, barHeight);
                }
            };
        };

        let myp5 = new p5(sketch);

        // Sorting algorithms
        function shellSort(arr) {
            let history = [arr.slice()];
            let n = arr.length;

            for (let gap = Math.floor(n/2); gap > 0; gap = Math.floor(gap/2)) {
                for (let i = gap; i < n; i++) {
                    let temp = arr[i];
                    let j;
                    for (j = i; j >= gap && arr[j - gap] > temp; j -= gap) {
                        arr[j] = arr[j - gap];
                    }
                    arr[j] = temp;
                    history.push(arr.slice());
                }
            }
            return history;
        }

        function bubbleSort(arr) {
            let history = [arr.slice()];
            let n = arr.length;

            for (let i = 0; i < n - 1; i++) {
                for (let j = 0; j < n - i - 1; j++) {
                    if (arr[j] > arr[j + 1]) {
                        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
                        history.push(arr.slice());
                    }
                }
            }
            return history;
        }

        function insertionSort(arr) {
            let history = [arr.slice()];
            let n = arr.length;

            for (let i = 1; i < n; i++) {
                let key = arr[i];
                let j = i - 1;
                while (j >= 0 && arr[j] > key) {
                    arr[j + 1] = arr[j];
                    j--;
                }
                arr[j + 1] = key;
                history.push(arr.slice());
            }
            return history;
        }

        function cocktailSort(arr) {
            let history = [arr.slice()];
            let swapped = true;
            let start = 0;
            let end = arr.length - 1;

            while (swapped) {
                swapped = false;

                for (let i = start; i < end; i++) {
                    if (arr[i] > arr[i + 1]) {
                        [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
                        swapped = true;
                        history.push(arr.slice());
                    }
                }

                if (!swapped) break;

                end--;
                swapped = false;

                for (let i = end; i > start; i--) {
                    if (arr[i] < arr[i - 1]) {
                        [arr[i], arr[i - 1]] = [arr[i - 1], arr[i]];
                        swapped = true;
                        history.push(arr.slice());
                    }
                }

                start++;
            }
            return history;
        }

        function bogoSort(arr) {
            let history = [arr.slice()];
            let maxSteps = 100; // Limit steps for bogosort
            let steps = 0;

            function isSorted(arr) {
                for (let i = 1; i < arr.length; i++) {
                    if (arr[i] < arr[i - 1]) return false;
                }
                return true;
            }

            while (!isSorted(arr) && steps < maxSteps) {
                for (let i = arr.length - 1; i > 0; i--) {
                    let j = Math.floor(Math.random() * (i + 1));
                    [arr[i], arr[j]] = [arr[j], arr[i]];
                }
                history.push(arr.slice());
                steps++;
            }

            return history;
        }

        // Initialize data
        function initializeData() {
            generateRandomArray();
        }

        function generateRandomArray() {
            // Create array with values 1 to N
            let arr = [];
            for (let i = 1; i <= state.numElements; i++) {
                arr.push(i);
            }

            // Shuffle array
            for (let i = arr.length - 1; i > 0; i--) {
                let j = Math.floor(Math.random() * (i + 1));
                [arr[i], arr[j]] = [arr[j], arr[i]];
            }

            // Run selected algorithm
            switch (state.currentAlgorithm) {
                case 'shellsort':
                    state.sortHistory = shellSort(arr.slice());
                    break;
                case 'bubblesort':
                    state.sortHistory = bubbleSort(arr.slice());
                    break;
                case 'insertionsort':
                    state.sortHistory = insertionSort(arr.slice());
                    break;
                case 'cocktail':
                    state.sortHistory = cocktailSort(arr.slice());
                    break;
                case 'bogosort':
                    state.sortHistory = bogoSort(arr.slice());
                    break;
            }

            // Update step slider
            document.getElementById('slider-step').max = state.sortHistory.length;
            document.getElementById('slider-step').value = 1;
            document.getElementById('step-value').textContent = '1';
            state.currentStep = 1;

            // Stop animation
            stopAnimation();

            // Redraw
            myp5.redraw();
        }

        function stopAnimation() {
            state.isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            if (state.animationInterval) {
                clearInterval(state.animationInterval);
                state.animationInterval = null;
            }
        }

        // Event listeners
        document.getElementById('slider-elements').addEventListener('input', function(e) {
            document.getElementById('elements-value').textContent = e.target.value;
        });

        document.getElementById('slider-step').addEventListener('input', function(e) {
            state.currentStep = parseInt(e.target.value);
            document.getElementById('step-value').textContent = state.currentStep;
            myp5.redraw();
        });

        document.getElementById('btn-randomize').addEventListener('click', function() {
            state.numElements = parseInt(document.getElementById('slider-elements').value);
            generateRandomArray();
        });

        // Sort algorithm buttons
        document.querySelectorAll('.sort-btn').forEach(btn => {
            btn.addEventListener('click', function() {
                // Update active class
                document.querySelectorAll('.sort-btn').forEach(b => b.classList.remove('active'));
                this.classList.add('active');

                // Update algorithm
                state.currentAlgorithm = this.id.replace('btn-', '');

                // Handle bogosort special case
                if (state.currentAlgorithm === 'bogosort') {
                    document.getElementById('slider-elements').disabled = true;
                    document.getElementById('slider-elements').value = 7;
                    document.getElementById('elements-value').textContent = '7';
                    state.numElements = 7;
                } else {
                    document.getElementById('slider-elements').disabled = false;
                }

                // Generate new array
                generateRandomArray();
            });
        });

        // Animation controls
        document.getElementById('btn-play-pause').addEventListener('click', function() {
            if (state.isPlaying) {
                stopAnimation();
            } else {
                state.isPlaying = true;
                this.textContent = '❚❚';
                state.animationInterval = setInterval(function() {
                    if (state.currentStep < state.sortHistory.length) {
                        state.currentStep++;
                        document.getElementById('slider-step').value = state.currentStep;
                        document.getElementById('step-value').textContent = state.currentStep;
                        myp5.redraw();
                    } else {
                        stopAnimation();
                    }
                }, 200);
            }
        });

        document.getElementById('btn-step-backward').addEventListener('click', function() {
            if (state.currentStep > 1) {
                state.currentStep--;
                document.getElementById('slider-step').value = state.currentStep;
                document.getElementById('step-value').textContent = state.currentStep;
                myp5.redraw();
            }
        });

        document.getElementById('btn-step-forward').addEventListener('click', function() {
            if (state.currentStep < state.sortHistory.length) {
                state.currentStep++;
                document.getElementById('slider-step').value = state.currentStep;
                document.getElementById('step-value').textContent = state.currentStep;
                myp5.redraw();
            }
        });

        document.getElementById('btn-reset-step').addEventListener('click', function() {
            state.currentStep = 1;
            document.getElementById('slider-step').value = 1;
            document.getElementById('step-value').textContent = '1';
            myp5.redraw();
        });
    </script>
</body>
</html>
