                <input type="range" id="slider-v2" min="-15" max="15" step="0.01" value="5">
                <span id="value-v2">5.00</span>
            </div>
        </div>

        <!-- Visualization Section -->
        <div id="visualization-area">
            <h2 id="result-text"></h2>
            <div id="plot-container"></div>
        </div>

    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Get DOM elements
        const sliderX1 = document.getElementById('slider-x1');
        const sliderV1 = document.getElementById('slider-v1');
        const sliderX2 = document.getElementById('slider-x2');
        const sliderV2 = document.getElementById('slider-v2');

        const valueX1 = document.getElementById('value-x1');
        const valueV1 = document.getElementById('value-v1');
        const valueX2 = document.getElementById('value-x2');
        const valueV2 = document.getElementById('value-v2');

        const btnReset = document.getElementById('btn-reset');
        const resultText = document.getElementById('result-text');
        const plotContainer = document.getElementById('plot-container');

        // Default values
        const defaults = {
            x1: 500,
            v1: -10,
            x2: 100,
            v2: 5
        };

        // Update value displays
        function updateValueDisplays() {
            valueX1.textContent = sliderX1.value;
            valueV1.textContent = parseFloat(sliderV1.value).toFixed(2);
            valueX2.textContent = sliderX2.value;
            valueV2.textContent = parseFloat(sliderV2.value).toFixed(2);
        }

        // Calculate intersection
        function calculateIntersection() {
            const x1 = parseFloat(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseFloat(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            let T = null;
            let X = null;
            let hasIntersection = false;

            if (v1 === v2) {
                // Parallel lines
                resultText.textContent = "The runners' paths never intersect";
            } else {
                T = (x1 - x2) / (v2 - v1);
                X = x1 + v1 * T;

                if (T < 0 || T > 1000) {
                    resultText.textContent = "The runners' paths never intersect";
                } else {
                    hasIntersection = true;
                    resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
                }
            }

            return { T, X, hasIntersection, x1, v1, x2, v2 };
        }

        // Update plot
        function updatePlot() {
            const result = calculateIntersection();
            const { T, X, hasIntersection, x1, v1, x2, v2 } = result;

            // Time array
            const t = [0, 1000];

            // Runner 1 trace
            const runner1 = {
                x: t,
                y: [x1, x1 + v1 * 1000],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'rgb(70, 130, 180)',
                    width: 2
                },
                name: 'Runner 1'
            };

            // Runner 2 trace
            const runner2 = {
                x: t,
                y: [x2, x2 + v2 * 1000],
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: 'rgb(255, 165, 0)',
                    width: 2
                },
                name: 'Runner 2'
            };

            // Intersection point trace
            const intersectionPoint = {
                x: hasIntersection ? [T] : [],
                y: hasIntersection ? [X] : [],
                type: 'scatter',
                mode: 'markers',
                marker: {
                    color: 'rgb(0, 128, 0)',
                    size: 10,
                    line: {
                        color: 'rgb(0, 100, 0)',
                        width: 2
                    }
                },
                name: 'Intersection'
            };

            const data = [runner1, runner2, intersectionPoint];

            const layout = {
                xaxis: {
                    title: 't',
                    range: [0, 1000],
                    gridcolor: '#e0e0e0',
                    zerolinecolor: '#808080'
                },
                yaxis: {
                    title: 'distance',
                    autorange: true,
                    gridcolor: '#e0e0e0',
                    zerolinecolor: '#808080'
                },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 60 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            const config = {
                responsive: true,
                displayModeBar: false
            };

            Plotly.react(plotContainer, data, layout, config);
        }

        // Reset function
        function reset() {
            sliderX1.value = defaults.x1;
            sliderV1.value = defaults.v1;
            sliderX2.value = defaults.x2;
            sliderV2.value = defaults.v2;
            updateValueDisplays();
            updatePlot();
        }

        // Event listeners
        sliderX1.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot();
        });

        sliderV1.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot();
        });

        sliderX2.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot();
        });

        sliderV2.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot();
        });

        btnReset.addEventListener('click', reset);

        // Initial setup
        updateValueDisplays();
        updatePlot();
    </script>
</body>
</html>
