        }
    </style>
</head>
<body>
    <div>
        <div id="control-panel">
            <label for="slider-phase">phosphorus cycle</label>
            <input type="range" id="slider-phase" min="0" max="8" step="1" value="0">
            <span id="slider-value">0</span>
            <button id="btn-reset">↻</button>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let slider;
        let sliderValueDisplay;
        let resetButton;
        let hoveredLabel = null;

        const tooltips = {
            'Clouds': 'Water vapor condensed in the atmosphere.',
            'Rocks': 'A primary reservoir of phosphorus in the form of phosphate.',
            'Animals': 'Obtain phosphorus by eating plants or other animals.',
            'Plants': 'Absorb phosphate from the soil to build organic molecules.',
            'Water': 'Phosphorus is carried into water bodies by runoff and leaching.',
            'Erosion': 'Weathering of rocks by wind and water releases phosphate.',
            'Runoff': 'Phosphate from fertilizers and rocks is washed into rivers and oceans.',
            'Soil': 'Phosphorus in the soil is available for plants.',
            'Leaching': 'Phosphates in the soil are dissolved in water and carried away.',
            'Plant uptake': 'Plants absorb inorganic phosphate from the soil through their roots.',
            'Animals eat plants': 'Transfers phosphorus from plants to animals along the food chain.',
            'Decomposition': 'Bacteria break down dead organic matter, returning phosphate to the soil and water.',
            'Geologic uplift': 'Over millions of years, ocean sediments containing phosphate are lifted up to form new land.'
        };

        const labelBounds = [];

        function setup() {
            let canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');

            slider = document.getElementById('slider-phase');
            sliderValueDisplay = document.getElementById('slider-value');
            resetButton = document.getElementById('btn-reset');

            slider.addEventListener('input', function() {
                sliderValueDisplay.textContent = this.value;
            });

            resetButton.addEventListener('click', function() {
                slider.value = 0;
                sliderValueDisplay.textContent = '0';
            });
        }

        function draw() {
            background(255);
            labelBounds.length = 0;

            // Sky
            fill('#CAE9FF');
            noStroke();
            rect(0, 0, width, height/2);

            // Ground
            fill('#9B7653');
            rect(0, height/2, width, height/2);

            // Sun
            fill('#FFDD00');
            circle(500, 80, 60);
            // Sun rays
            stroke('#FFDD00');
            strokeWeight(2);
            for (let i = 0; i < 8; i++) {
                let angle = i * PI / 4;
                let x1 = 500 + cos(angle) * 35;
                let y1 = 80 + sin(angle) * 35;
                let x2 = 500 + cos(angle) * 50;
                let y2 = 80 + sin(angle) * 50;
                line(x1, y1, x2, y2);
            }

            // Clouds
            noStroke();
            fill('#E0E0E0');
            ellipse(100, 60, 80, 40);
            ellipse(130, 55, 70, 35);
            ellipse(70, 55, 60, 30);
            fill('#505050');
            textAlign(CENTER);
            text('Clouds', 100, 65);
            addLabelBounds('Clouds', 100, 65, 50, 20);

            // Rain
            stroke('#0000FF');
            strokeWeight(1);
            for (let i = 0; i < 10; i++) {
                let x = 80 + i * 5;
                let y = 90 + i * 2;
                line(x, y, x - 5, y + 15);
            }

            // Rocks
            noStroke();
            fill('#5A5A5A');
            ellipse(150, 280, 60, 40);
            ellipse(170, 275, 50, 35);
            ellipse(130, 275, 40, 30);
            fill('#000000');
            textAlign(CENTER);
            text('Rocks', 150, 310);
            addLabelBounds('Rocks', 150, 310, 40, 20);

            // Water
            fill(0, 0, 255, 150);
            ellipse(80, 420, 120, 80);
            ellipse(120, 430, 100, 70);
            fill('#00008B');
            text('Water', 100, 430);
            addLabelBounds('Water', 100, 430, 40, 20);

            // Plants
            fill('#90EE90');
            triangle(430, 300, 420, 280, 440, 280);
            triangle(450, 300, 440, 280, 460, 280);
            triangle(470, 300, 460, 280, 480, 280);
            fill('#006400');
            text('Plants', 450, 315);
            addLabelBounds('Plants', 450, 315, 40, 20);

            // Animal (cow)
            fill('#FFFFFF');
            stroke('#000000');
            strokeWeight(1);
            // Body
            ellipse(300, 270, 80, 50);
            // Head
            ellipse(260, 260, 40, 35);
            // Spots
            fill('#000000');
            noStroke();
            ellipse(290, 265, 15, 10);
            ellipse(310, 270, 12, 8);
            ellipse(275, 275, 10, 8);
            // Legs
            rect(275, 285, 8, 20);
            rect(290, 285, 8, 20);
            rect(305, 285, 8, 20);
            rect(320, 285, 8, 20);
            // Eye
            fill('#0000FF');
            ellipse(255, 255, 5, 5);
            fill('#000000');
            textAlign(CENTER);
            text('Animals', 300, 320);
            addLabelBounds('Animals', 300, 320, 50, 20);

            // Dynamic elements based on slider value
            let sliderValue = parseInt(slider.value);
            stroke('#000000');
            strokeWeight(2);
            fill('#000000');

            if (sliderValue >= 1) {
                drawArrow(100, 100, 150, 250);
                text('Erosion', 125, 175);
                addLabelBounds('Erosion', 125, 175, 50, 20);
            }

            if (sliderValue >= 2) {
                drawArrow(150, 290, 100, 380);
                text('Runoff', 125, 340);
                addLabelBounds('Runoff', 125, 340, 50, 20);
            }

            if (sliderValue >= 3) {
                drawArrow(140, 420, 200, 350);
                text('Soil', 170, 385);
                addLabelBounds('Soil', 170, 385, 30, 20);
            }

            if (sliderValue >= 4) {
                drawArrow(200, 300, 120, 380);
                text('Leaching', 160, 340);
                addLabelBounds('Leaching', 160, 340, 60, 20);
            }

            if (sliderValue >= 5) {
                drawArrow(250, 340, 420, 290);
                text('Plant uptake', 335, 315);
                addLabelBounds('Plant uptake', 335, 315, 80, 20);
            }

            if (sliderValue >= 6) {
                drawArrow(410, 280, 340, 260);
                text('Animals eat plants', 375, 250);
                addLabelBounds('Animals eat plants', 375, 250, 120, 20);
            }

            if (sliderValue >= 7) {
                drawArrow(300, 295, 350, 340);
                text('Decomposition', 325, 355);
                addLabelBounds('Decomposition', 325, 355, 100, 20);
            }

            if (sliderValue >= 8) {
                // Curved arrow for geologic uplift
                noFill();
                beginShape();
                curveVertex(100, 460);
                curveVertex(100, 460);
                curveVertex(50, 400);
                curveVertex(60, 300);
                curveVertex(150, 250);
                curveVertex(150, 250);
                endShape();
                // Arrow head
                push();
                translate(150, 250);
                rotate(radians(-45));
                fill('#000000');
                triangle(0, 0, -10, -5, -10, 5);
                pop();
                text('Geologic uplift', 80, 350);
                addLabelBounds('Geologic uplift', 80, 350, 100, 20);
            }

            // Hover tooltip
            hoveredLabel = null;
            for (let label of labelBounds) {
                if (mouseX > label.x - label.w/2 && mouseX < label.x + label.w/2 &&
                    mouseY > label.y - label.h/2 && mouseY < label.y + label.h/2) {
                    hoveredLabel = label;
                    break;
                }
            }

            if (hoveredLabel && tooltips[hoveredLabel.text]) {
                drawTooltip(mouseX + 10, mouseY - 10, tooltips[hoveredLabel.text]);
            }
        }

        function drawArrow(x1, y1, x2, y2) {
            line(x1, y1, x2, y2);

            let angle = atan2(y2 - y1, x2 - x1);
            push();
            translate(x2, y2);
            rotate(angle);
            fill('#000000');
            triangle(0, 0, -10, -5, -10, 5);
            pop();
        }

        function addLabelBounds(text, x, y, w, h) {
            labelBounds.push({text, x, y, w, h});
        }

        function drawTooltip(x, y, text) {
            let padding = 5;
            let boxWidth = textWidth(text) + padding * 2;
            let boxHeight = 20;

            // Adjust position if tooltip goes off screen
            if (x + boxWidth > width) {
                x = width - boxWidth - 5;
            }
            if (y - boxHeight < 0) {
                y = boxHeight + 5;
            }

            fill(255, 255, 200, 230);
            stroke(100);
            strokeWeight(1);
            rect(x, y - boxHeight, boxWidth, boxHeight, 3);

            fill(0);
            noStroke();
            textAlign(LEFT);
            text(text, x + padding, y - 5);
        }
    </script>
</body>
</html>
