                const stepData = data.steps[currentStep - 1];

                // Draw unmatched edges
                p.stroke(204);
                p.strokeWeight(2);
                data.edges.forEach((edge, index) => {
                    if (!stepData.matching.includes(index)) {
                        const v1 = data.vertices[edge.u];
