                }
                p.strokeWeight(6);
                stepData.matching.forEach(edgeIndex => {
                    const edge = data.edges[edgeIndex];
                    const v1 = data.vertices[edge.u];
                    const v2 = data.vertices[edge.v];
                    p.line(v1.x, v1.y, v2.x, v2.y);
                });

                // Draw blossom if present
                if (stepData.blossom) {
                    p.fill(0, 0, 255, 77);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.beginShape();
                    stepData.blossom.vertices.forEach(vIndex => {
                        const v = data.vertices[vIndex];
                        p.vertex(v.x, v.y);
                    });
                    p.endShape(p.CLOSE);

                    // Draw super-vertex
                    const topV = data.vertices[stepData.blossom.topVertex];
                    p.fill(255, 255, 0);
                    p.stroke(0);
                    p.strokeWeight(2);
                    p.circle(topV.x, topV.y, 16);
                }

                // Draw vertices
                p.fill(0);
                p.noStroke();
                data.vertices.forEach(v => {
                    p.circle(v.x, v.y, 8);
                });
            };

            window.redraw = () => p.redraw();
        });
