    <script type="module">
        import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
        import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';
        import { SUBTRACTION, Brush, Evaluator } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';

        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        // Camera setup
        const camera = new THREE.PerspectiveCamera(
            75,
            window.innerWidth / window.innerHeight,
            0.1,
            1000
        );
        camera.position.set(10, 10, 10);
        camera.lookAt(0, 0, 0);

        // Renderer setup
        const canvas = document.getElementById('main-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
        renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        renderer.shadowMap.enabled = true;

        // Controls setup
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Lighting setup
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(10, 10, 5);
        directionalLight1.castShadow = true;
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight2.position.set(-10, 10, -5);
        scene.add(directionalLight2);

        // Current meshes
        let csgMesh = null;
        let ghostCylinder = null;
        let boundaryCurveFront = null;
        let boundaryCurveBack = null;

        // Get slider values
        function getSliderValues() {
            return {
                cylinderRadius: parseFloat(document.getElementById('slider-cylinder-radius').value),
                opacity: parseFloat(document.getElementById('slider-opacity').value),
                drillRadius: parseFloat(document.getElementById('slider-drill-radius').value),
                inclination: parseFloat(document.getElementById('slider-inclination').value),
                rotation: parseFloat(document.getElementById('slider-rotation').value),
                offset: parseFloat(document.getElementById('slider-offset').value),
                boundaryOnly: document.getElementById('checkbox-boundary-only').checked
            };
        }

        // Calculate boundary curve points
        function calculateBoundaryCurve(cylinderRadius, drillRadius, inclination, rotation, offset) {
            const frontPoints = [];
            const backPoints = [];
            const numPoints = 100;

            for (let i = 0; i <= numPoints; i++) {
                const t = (i / numPoints) * Math.PI * 2;

                // Point on drill cylinder edge in local coordinates
                const localX = drillRadius * Math.cos(t);
                const localY = 0;
                const localZ = drillRadius * Math.sin(t);

                // Create transformation matrix for drill cylinder
                const matrix = new THREE.Matrix4();
                matrix.makeRotationZ(Math.PI / 2); // Initial 90 degree rotation

                const rotMatrix = new THREE.Matrix4();
                rotMatrix.makeRotationX(inclination);
                matrix.premultiply(rotMatrix);

                const rotYMatrix = new THREE.Matrix4();
                rotYMatrix.makeRotationY(rotation);
                matrix.premultiply(rotYMatrix);

                const transMatrix = new THREE.Matrix4();
                transMatrix.makeTranslation(0, offset, 0);
                matrix.premultiply(transMatrix);

                // Transform the point
                const point = new THREE.Vector3(localX, localY, localZ);
                point.applyMatrix4(matrix);

                // Find intersection with main cylinder
                // We need to find where the drill cylinder intersects the main cylinder
                // This is a simplified approach - for a more accurate result, we'd need
                // to solve the intersection equation properly
                const angle = Math.atan2(point.z, point.x);
                const intersectX = cylinderRadius * Math.cos(angle);
                const intersectZ = cylinderRadius * Math.sin(angle);

                const intersectionPoint = new THREE.Vector3(intersectX, point.y, intersectZ);

                // Separate front and back based on local z coordinate
                if (localZ > 0) {
                    frontPoints.push(intersectionPoint);
                } else {
                    backPoints.push(intersectionPoint);
                }
            }

            return { frontPoints, backPoints };
        }

        // Create tube geometry from points
        function createTubeFromPoints(points, color) {
            if (points.length < 2) return null;

            const curve = new THREE.CatmullRomCurve3(points, true);
            const tubeGeometry = new THREE.TubeGeometry(curve, points.length * 2, 0.05, 8, true);
            const material = new THREE.MeshBasicMaterial({ color: color });
            return new THREE.Mesh(tubeGeometry, material);
        }

        // Update scene function
        function updateScene() {
            // Clear previous geometries
            if (csgMesh) {
                scene.remove(csgMesh);
                csgMesh.geometry.dispose();
                csgMesh.material.dispose();
            }
            if (ghostCylinder) {
                scene.remove(ghostCylinder);
                ghostCylinder.geometry.dispose();
                ghostCylinder.material.dispose();
            }
            if (boundaryCurveFront) {
                scene.remove(boundaryCurveFront);
                boundaryCurveFront.geometry.dispose();
                boundaryCurveFront.material.dispose();
            }
            if (boundaryCurveBack) {
                scene.remove(boundaryCurveBack);
                boundaryCurveBack.geometry.dispose();
                boundaryCurveBack.material.dispose();
            }

            const values = getSliderValues();

            // Create main cylinder
            const cylinderGeometry = new THREE.CylinderGeometry(
                values.cylinderRadius,
                values.cylinderRadius,
                8,
                32
            );
            const cylinderMaterial = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                opacity: values.boundaryOnly ? 0.15 : values.opacity,
                transparent: true
            });
            const cylinderMesh = new THREE.Mesh(cylinderGeometry, cylinderMaterial);

            // Create drill cylinder
            const drillGeometry = new THREE.CylinderGeometry(
                values.drillRadius,
                values.drillRadius,
                10,
                32
            );
            const drillMaterial = new THREE.MeshStandardMaterial({ color: 0x808080 });
            const drillMesh = new THREE.Mesh(drillGeometry, drillMaterial);

            // Apply transformations to drill cylinder
            drillMesh.rotation.z = Math.PI / 2;
            drillMesh.rotation.x = values.inclination;
            drillMesh.rotation.y = values.rotation;
            drillMesh.position.y = values.offset;

            // Perform CSG operation
            const cylinderBrush = new Brush(cylinderGeometry);
            cylinderBrush.updateMatrixWorld();

            const drillBrush = new Brush(drillGeometry);
            drillBrush.rotation.z = Math.PI / 2;
            drillBrush.rotation.x = values.inclination;
            drillBrush.rotation.y = values.rotation;
            drillBrush.position.y = values.offset;
            drillBrush.updateMatrixWorld();

            const evaluator = new Evaluator();
            const resultGeometry = evaluator.evaluate(cylinderBrush, drillBrush, SUBTRACTION);

            const resultMaterial = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                opacity: values.opacity,
                transparent: true
            });
            csgMesh = new THREE.Mesh(resultGeometry, resultMaterial);
            csgMesh.castShadow = true;
            csgMesh.receiveShadow = true;

            // Calculate and create boundary curves
            const { frontPoints, backPoints } = calculateBoundaryCurve(
                values.cylinderRadius,
                values.drillRadius,
                values.inclination,
                values.rotation,
                values.offset
            );

            boundaryCurveFront = createTubeFromPoints(frontPoints, 0xFFA500);
            boundaryCurveBack = createTubeFromPoints(backPoints, 0x4169E1);

            // Add geometries to scene based on checkbox state
            if (values.boundaryOnly) {
                // Create ghost cylinder
                ghostCylinder = new THREE.Mesh(cylinderGeometry.clone(), cylinderMaterial);
                scene.add(ghostCylinder);
            } else {
                scene.add(csgMesh);
            }

            if (boundaryCurveFront) scene.add(boundaryCurveFront);
            if (boundaryCurveBack) scene.add(boundaryCurveBack);

            // Clean up temporary geometries
            cylinderGeometry.dispose();
            drillGeometry.dispose();
            drillMaterial.dispose();
        }

        // Update label functions
        function updateLabel(sliderId, labelId) {
            const slider = document.getElementById(sliderId);
            const label = document.getElementById(labelId);
            label.textContent = parseFloat(slider.value).toFixed(2);
        }

        // Add event listeners
        document.getElementById('slider-cylinder-radius').addEventListener('input', (e) => {
            updateLabel('slider-cylinder-radius', 'label-cylinder-radius');
            updateScene();
        });

        document.getElementById('slider-opacity').addEventListener('input', (e) => {
            updateLabel('slider-opacity', 'label-opacity');
            updateScene();
        });

        document.getElementById('slider-drill-radius').addEventListener('input', (e) => {
            updateLabel('slider-drill-radius', 'label-drill-radius');
            updateScene();
        });

        document.getElementById('slider-inclination').addEventListener('input', (e) => {
            updateLabel('slider-inclination', 'label-inclination');
            updateScene();
        });

        document.getElementById('slider-rotation').addEventListener('input', (e) => {
            updateLabel('slider-rotation', 'label-rotation');
            updateScene();
        });

        document.getElementById('slider-offset').addEventListener('input', (e) => {
            updateLabel('slider-offset', 'label-offset');
            updateScene();
        });

        document.getElementById('checkbox-boundary-only').addEventListener('change', updateScene);

        // Handle window resize
        function onWindowResize() {
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }
        window.addEventListener('resize', onWindowResize);

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize scene
        updateScene();
        animate();
    </script>
</body>
</html>
