    <script>
        let hueCanvas, saturationCanvas, brightnessCanvas, resultCanvas;
        let canvasWidth = 560;
        let canvasHeight = 40;

        function setup() {
            colorMode(HSB, 1, 1, 1);

            // Create canvases
            hueCanvas = createGraphics(canvasWidth, canvasHeight);
            hueCanvas.parent('canvas-hue-parent');
            hueCanvas.colorMode(HSB, 1, 1, 1);

            saturationCanvas = createGraphics(canvasWidth, canvasHeight);
            saturationCanvas.parent('canvas-saturation-parent');
            saturationCanvas.colorMode(HSB, 1, 1, 1);

            brightnessCanvas = createGraphics(canvasWidth, canvasHeight);
            brightnessCanvas.parent('canvas-brightness-parent');
            brightnessCanvas.colorMode(HSB, 1, 1, 1);

            resultCanvas = createGraphics(canvasWidth, canvasHeight);
            resultCanvas.parent('canvas-result-parent');
            resultCanvas.colorMode(HSB, 1, 1, 1);

            // Draw initial hue bar (static)
            drawHueBar();

            // Initial update
            updateVisuals();

            // Add event listeners
            document.getElementById('slider-hue').addEventListener('input', function() {
                document.getElementById('value-hue').textContent = this.value;
                updateVisuals();
            });

            document.getElementById('slider-saturation').addEventListener('input', function() {
                document.getElementById('value-saturation').textContent = this.value;
                updateVisuals();
            });

            document.getElementById('slider-brightness').addEventListener('input', function() {
                document.getElementById('value-brightness').textContent = this.value;
                updateVisuals();
            });

            document.getElementById('btn-reset').addEventListener('click', function() {
                document.getElementById('slider-hue').value = 0;
                document.getElementById('slider-saturation').value = 0.5;
                document.getElementById('slider-brightness').value = 1;
                document.getElementById('value-hue').textContent = '0';
                document.getElementById('value-saturation').textContent = '0.5';
                document.getElementById('value-brightness').textContent = '1';
                updateVisuals();
            });

            noLoop();
        }

        function drawHueBar() {
            hueCanvas.noStroke();
            for (let x = 0; x < canvasWidth; x++) {
                let hue = x / canvasWidth;
                hueCanvas.fill(hue, 1, 1);
                hueCanvas.rect(x, 0, 1, canvasHeight);
            }
            // Apply rounded corners
            hueCanvas.erase();
            hueCanvas.rect(0, 0, 20, canvasHeight, 20, 0, 0, 20);
            hueCanvas.rect(canvasWidth - 20, 0, 20, canvasHeight, 0, 20, 20, 0);
            hueCanvas.noErase();
        }

        function drawSaturationBar(hue, brightness) {
            saturationCanvas.clear();
            saturationCanvas.noStroke();
            for (let x = 0; x < canvasWidth; x++) {
                let saturation = x / canvasWidth;
                saturationCanvas.fill(hue, saturation, brightness);
                saturationCanvas.rect(x, 0, 1, canvasHeight);
            }
            // Apply rounded corners
            saturationCanvas.erase();
            saturationCanvas.rect(0, 0, 20, canvasHeight, 20, 0, 0, 20);
            saturationCanvas.rect(canvasWidth - 20, 0, 20, canvasHeight, 0, 20, 20, 0);
            saturationCanvas.noErase();
        }

        function drawBrightnessBar(hue, saturation) {
            brightnessCanvas.clear();
            brightnessCanvas.noStroke();
            for (let x = 0; x < canvasWidth; x++) {
                let brightness = x / canvasWidth;
                brightnessCanvas.fill(hue, saturation, brightness);
                brightnessCanvas.rect(x, 0, 1, canvasHeight);
            }
            // Apply rounded corners
            brightnessCanvas.erase();
            brightnessCanvas.rect(0, 0, 20, canvasHeight, 20, 0, 0, 20);
            brightnessCanvas.rect(canvasWidth - 20, 0, 20, canvasHeight, 0, 20, 20, 0);
            brightnessCanvas.noErase();
        }

        function drawResultBar(hue, saturation, brightness) {
            resultCanvas.clear();
            resultCanvas.noStroke();
            resultCanvas.fill(hue, saturation, brightness);
            resultCanvas.rect(0, 0, canvasWidth, canvasHeight, 20);
        }

        function updateVisuals() {
            let hue = parseFloat(document.getElementById('slider-hue').value);
            let saturation = parseFloat(document.getElementById('slider-saturation').value);
            let brightness = parseFloat(document.getElementById('slider-brightness').value);

            // Update bars
            drawSaturationBar(hue, brightness);
            drawBrightnessBar(hue, saturation);
            drawResultBar(hue, saturation, brightness);

            // Update arrow positions
            document.getElementById('arrow-hue').style.left = (hue * canvasWidth) + 'px';
            document.getElementById('arrow-saturation').style.left = (saturation * canvasWidth) + 'px';
            document.getElementById('arrow-brightness').style.left = (brightness * canvasWidth) + 'px';
        }
    </script>
</body>
