    </style>
</head>
<body>
    <div class="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label>α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span class="value-display" id="value-alpha">0.0500</span>
            </div>
            <div class="control-row">
                <label>β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span class="value-display" id="value-beta">5.00</span>
            </div>
            <div class="control-row">
                <label>γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span class="value-display" id="value-gamma">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Get slider elements
        const sliderAlpha = document.getElementById('slider-alpha');
        const sliderBeta = document.getElementById('slider-beta');
        const sliderGamma = document.getElementById('slider-gamma');

        // Get value display elements
        const valueAlpha = document.getElementById('value-alpha');
        const valueBeta = document.getElementById('value-beta');
        const valueGamma = document.getElementById('value-gamma');

        // RK4 ODE solver
        function rk4Step(f, y, t, h, params) {
            const k1 = f(t, y, params);
            const y1 = y.map((yi, i) => yi + 0.5 * h * k1[i]);

            const k2 = f(t + 0.5 * h, y1, params);
            const y2 = y.map((yi, i) => yi + 0.5 * h * k2[i]);

            const k3 = f(t + 0.5 * h, y2, params);
            const y3 = y.map((yi, i) => yi + h * k3[i]);

            const k4 = f(t + h, y3, params);

            return y.map((yi, i) => yi + (h / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
        }

        // ODE system
        function odeSystem(t, y, params) {
            const deltaT = y[0];
            const Cx = y[1];
            const { alpha, beta, gamma } = params;

            const expTerm = Math.exp(deltaT / (1 + deltaT / alpha));

            const dDeltaT_dt = gamma * Cx * expTerm - beta * deltaT;
            const dCx_dt = 1 - Cx * expTerm;

            return [dDeltaT_dt, dCx_dt];
        }

        // Solve ODE system
        function solveODE(alpha, beta, gamma) {
            const params = { alpha, beta, gamma };
            const h = 0.05;
            const tMax = 20;
            const steps = Math.floor(tMax / h);

            const t = [];
            const deltaT = [];
            const Cx = [];

            let y = [0, 0]; // Initial conditions: ΔT(0) = 0, C_X(0) = 0

            for (let i = 0; i <= steps; i++) {
                const currentT = i * h;
                t.push(currentT);
                deltaT.push(y[0]);
                Cx.push(y[1] * 1000); // Scale C_X by 1000

                if (i < steps) {
                    y = rk4Step(odeSystem, y, currentT, h, params);
                }
            }

            return { t, deltaT, Cx };
        }

        // Update plot
        function updatePlot() {
            const alpha = parseFloat(sliderAlpha.value);
            const beta = parseFloat(sliderBeta.value);
            const gamma = parseFloat(sliderGamma.value);

            const solution = solveODE(alpha, beta, gamma);

            const trace1 = {
                x: solution.t,
                y: solution.deltaT,
                mode: 'lines',
                line: { color: 'orange', width: 2 },
                showlegend: false
            };

            const trace2 = {
                x: solution.t,
                y: solution.Cx,
                mode: 'lines',
                line: { color: 'steelblue', width: 2 },
                showlegend: false
            };

            const layout = {
                xaxis: { 
                    title: '',
                    range: [0, 20],
                    zeroline: true,
                    gridcolor: '#e0e0e0'
                },
                yaxis: { 
                    title: '',
                    range: [0, 100],
                    zeroline: true,
                    gridcolor: '#e0e0e0'
                },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                annotations: [
                    {
                        text: 'ΔT',
                        x: -0.08,
                        y: 100,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple', size: 14 }
                    },
                    {
                        text: '1000 C<sub>X</sub>',
                        x: -0.08,
                        y: 90,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple', size: 14 }
                    }
                ]
            };

            Plotly.newPlot('plot-container', [trace1, trace2], layout, {responsive: true});
        }

        // Update value displays
        function updateValueDisplays() {
            valueAlpha.textContent = parseFloat(sliderAlpha.value).toFixed(4);
            valueBeta.textContent = parseFloat(sliderBeta.value).toFixed(2);
            valueGamma.textContent = parseInt(sliderGamma.value);
        }

        // Event listeners
        sliderAlpha.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot();
        });

        sliderBeta.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot();
        });

        sliderGamma.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot();
        });

        // Initial plot
        updateValueDisplays();
        updatePlot();
    </script>
</body>
</html>
