    <script>
        // Linear regression function
        function linearRegression(points) {
            const n = points.length;
            let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0, sumY2 = 0;

            for (let i = 0; i < n; i++) {
                const [x, y] = points[i];
                sumX += x;
                sumY += y;
                sumXY += x * y;
                sumX2 += x * x;
                sumY2 += y * y;
            }

            const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
            const intercept = (sumY - slope * sumX) / n;

            // Calculate R-squared
            const meanY = sumY / n;
            let ssTotal = 0, ssResidual = 0;

            for (let i = 0; i < n; i++) {
                const [x, y] = points[i];
                const yPred = slope * x + intercept;
                ssTotal += (y - meanY) * (y - meanY);
                ssResidual += (y - yPred) * (y - yPred);
            }

            const rSquared = 1 - (ssResidual / ssTotal);

            return { slope, intercept, rSquared };
        }

        // State management
        let activeModel = 'arrhenius';

        // Get slider values
        function getSliderValues() {
            return {
                n: parseInt(document.getElementById('slider-n').value),
                tMin: parseFloat(document.getElementById('slider-tmin').value),
                tMax: parseFloat(document.getElementById('slider-tmax').value),
                tRef: parseFloat(document.getElementById('slider-tref').value),
                c: parseFloat(document.getElementById('slider-c').value),
                b: parseFloat(document.getElementById('slider-b').value),
                a: parseFloat(document.getElementById('slider-a').value)
            };
        }

        // Update plots
        function updatePlots() {
            const params = getSliderValues();

            // Generate temperature array
            const temperatures = [];
            for (let i = 0; i < params.n; i++) {
                const t = params.tMin + (params.tMax - params.tMin) * i / (params.n - 1);
                temperatures.push(t);
            }

            // Generate data points
            const dataPoints = [];
            const kRef = activeModel === 'arrhenius' 
                ? Math.exp(-params.a / (params.tRef + params.b))
                : 1;

            for (const T of temperatures) {
                let y;
                if (activeModel === 'arrhenius') {
                    y = Math.exp(-params.a / (T + params.b)) / kRef;
                } else {
                    y = Math.exp(params.c * (T - params.tRef));
                }
                dataPoints.push([T, y]);
            }

            // Perform fitting
            let fitParams, transformedPoints, fitX, fitY;

            if (activeModel === 'arrhenius') {
                // Fit with exponential model
                transformedPoints = dataPoints.map(([T, y]) => [T - params.tRef, Math.log(y)]);
                fitParams = linearRegression(transformedPoints);

                // Generate fit curve
                fitX = [];
                fitY = [];
                for (let T = params.tMin; T <= params.tMax; T += 0.5) {
                    fitX.push(T);
                    fitY.push(Math.exp(fitParams.slope * (T - params.tRef)));
                }
            } else {
                // Fit with Arrhenius model
                transformedPoints = dataPoints.map(([T, y]) => [1 / (T + params.b), Math.log(y)]);
                fitParams = linearRegression(transformedPoints);
                const fittedA = -fitParams.slope;

                // Generate fit curve
                fitX = [];
                fitY = [];
                const kRefFit = Math.exp(-fittedA / (params.tRef + params.b));
                for (let T = params.tMin; T <= params.tMax; T += 0.5) {
                    fitX.push(T);
                    fitY.push(Math.exp(-fittedA / (T + params.b)) / kRefFit);
                }
            }

            // Plot top figure
            const topData = [
                {
                    x: dataPoints.map(p => p[0]),
                    y: dataPoints.map(p => p[1]),
                    mode: 'markers',
                    type: 'scatter',
                    name: 'Data',
                    marker: { color: '#d62728', size: 8 }
                },
                {
                    x: fitX,
                    y: fitY,
                    mode: 'lines',
                    type: 'scatter',
                    name: 'Fit',
                    line: { color: '#d62728', width: 2 }
                }
            ];

            const topLayout = {
                title: activeModel === 'arrhenius' 
                    ? `exponential model fit to Arrhenius data<br>c = ${fitParams.slope.toFixed(4)} R² = ${fitParams.rSquared.toFixed(4)}`
                    : `Arrhenius model fit to exponential data<br>a = ${(-fitParams.slope).toFixed(1)}, R² = ${fitParams.rSquared.toFixed(4)}`,
                xaxis: { title: 'T (°C)' },
                yaxis: { title: 'k(T)/k(T<sub>ref</sub>)' },
                showlegend: false,
                margin: { t: 80, b: 60, l: 60, r: 40 }
            };

            Plotly.newPlot('plot-top', topData, topLayout);

            // Plot bottom figure
            let bottomData, bottomLayout;

            if (activeModel === 'arrhenius') {
                const linearFitX = [transformedPoints[0][0], transformedPoints[transformedPoints.length - 1][0]];
                const linearFitY = linearFitX.map(x => fitParams.slope * x + fitParams.intercept);

                bottomData = [
                    {
                        x: transformedPoints.map(p => p[0]),
                        y: transformedPoints.map(p => p[1]),
                        mode: 'markers',
                        type: 'scatter',
                        name: 'Data',
                        marker: { color: '#2ca02c', size: 8 }
                    },
                    {
                        x: linearFitX,
                        y: linearFitY,
                        mode: 'lines',
                        type: 'scatter',
                        name: 'Linear Fit',
                        line: { color: '#2ca02c', width: 2 }
                    }
                ];

                bottomLayout = {
                    title: `linear fit of the exponential model<br>R² = ${fitParams.rSquared.toFixed(4)}`,
                    xaxis: { title: 'T - T<sub>ref</sub>' },
                    yaxis: { title: 'log(k(T)/k(T<sub>ref</sub>))' },
                    showlegend: false,
                    margin: { t: 80, b: 60, l: 60, r: 40 },
                    shapes: [
                        { type: 'line', x0: 0, x1: 0, y0: 0, y1: 1, yref: 'paper', line: { color: 'gray', dash: 'dash' } },
                        { type: 'line', x0: 0, x1: 1, xref: 'paper', y0: 0, y1: 0, line: { color: 'gray', dash: 'dash' } }
                    ]
                };
            } else {
                const linearFitX = [transformedPoints[0][0], transformedPoints[transformedPoints.length - 1][0]];
                const linearFitY = linearFitX.map(x => fitParams.slope * x + fitParams.intercept);

                bottomData = [
                    {
                        x: transformedPoints.map(p => p[0]),
                        y: transformedPoints.map(p => p[1]),
                        mode: 'markers',
                        type: 'scatter',
                        name: 'Data',
                        marker: { color: '#1f77b4', size: 8 }
                    },
                    {
                        x: linearFitX,
                        y: linearFitY,
                        mode: 'lines',
                        type: 'scatter',
                        name: 'Linear Fit',
                        line: { color: '#1f77b4', width: 2 }
                    }
                ];

                bottomLayout = {
                    title: `linear fit of the Arrhenius model<br>R² = ${fitParams.rSquared.toFixed(4)}`,
                    xaxis: { title: '1/(T + b)' },
                    yaxis: { title: 'log(k(T)/k(T<sub>ref</sub>))' },
                    showlegend: false,
                    margin: { t: 80, b: 60, l: 60, r: 40 },
                    shapes: [
                        { type: 'line', x0: 0, x1: 0, y0: 0, y1: 1, yref: 'paper', line: { color: 'gray', dash: 'dash' } },
                        { type: 'line', x0: 0, x1: 1, xref: 'paper', y0: 0, y1: 0, line: { color: 'gray', dash: 'dash' } }
                    ]
                };
            }

            Plotly.newPlot('plot-bottom', bottomData, bottomLayout);
        }

        // Event listeners
        document.getElementById('btn-arrhenius').addEventListener('click', function() {
            activeModel = 'arrhenius';
            document.getElementById('btn-arrhenius').classList.add('active-button');
            document.getElementById('btn-exponential').classList.remove('active-button');
            updatePlots();
        });

        document.getElementById('btn-exponential').addEventListener('click', function() {
            activeModel = 'exponential';
            document.getElementById('btn-exponential').classList.add('active-button');
            document.getElementById('btn-arrhenius').classList.remove('active-button');
            updatePlots();
        });

        // Slider event listeners
        const sliders = ['n', 'tmin', 'tmax', 'tref', 'c', 'b', 'a'];
        sliders.forEach(id => {
            const slider = document.getElementById(`slider-${id}`);
            const display = document.getElementById(`value-${id}`);

            slider.addEventListener('input', function() {
                display.textContent = this.value;

                // Enforce T_max > T_min constraint
                if (id === 'tmin') {
                    const tmaxSlider = document.getElementById('slider-tmax');
                    const currentTmax = parseFloat(tmaxSlider.value);
                    const newTmin = parseFloat(this.value);
                    tmaxSlider.min = newTmin + 1;
                    if (currentTmax <= newTmin) {
                        tmaxSlider.value = newTmin + 1;
                        document.getElementById('value-tmax').textContent = tmaxSlider.value;
                    }
                }

                updatePlots();
            });
        });

        // Initial plot
        updatePlots();
    </script>
</body>
</html>
