                    x0: -0.5,
                    x1: 25.5,
                    y0: 3.846,
                    y1: 3.846,
                    line: { color: 'red', width: 2, dash: 'dash' }
                }]
            };

            Plotly.newPlot(elementId, data, layout, {displayModeBar: false});
        }

        // Main update function
        function updateVisualization() {
            // Read current values
            const messageType = document.getElementById('select-message').value;
            const cipherType = document.getElementById('select-cipher').value;
            const keyword = document.getElementById('input-keyword').value;
            const offset = parseInt(document.getElementById('slider-offset').value);

            // Update UI state based on cipher type
            const keywordInput = document.getElementById('input-keyword');
            const offsetSlider = document.getElementById('slider-offset');
            const offsetValue = document.getElementById('offset-value');

            if (cipherType === 'Caesar') {
                keywordInput.disabled = true;
                offsetSlider.disabled = false;
                offsetValue.style.opacity = '1';
            } else {
                keywordInput.disabled = false;
                offsetSlider.disabled = true;
                offsetValue.style.opacity = '0.5';
            }

            // Get and process message
            const originalMessage = messages[messageType];
            const processedMessage = processText(originalMessage);

            // Display original message (first 80 chars)
            document.getElementById('original-text').textContent = originalMessage.substring(0, 80) + '...';

            // Calculate and display original frequency
            const originalFreq = calculateFrequency(processedMessage);
            createFrequencyChart('original-chart', originalFreq);

            // Encrypt message
            let encryptedMessage;
            switch (cipherType) {
                case 'Caesar':
                    encryptedMessage = caesarEncrypt(processedMessage, offset);
                    break;
                case 'De Vigenere':
                    encryptedMessage = vigenereEncrypt(processedMessage, keyword);
                    break;
                case 'Autokey':
                    encryptedMessage = autokeyEncrypt(processedMessage, keyword);
                    break;
            }

            // Display encrypted message
            document.getElementById('encrypted-text').textContent = encryptedMessage;

            // Calculate and display encrypted frequency
            const encryptedFreq = calculateFrequency(encryptedMessage);
            createFrequencyChart('encrypted-chart', encryptedFreq);
        }

        // Event listeners
        document.getElementById('select-message').addEventListener('change', updateVisualization);
        document.getElementById('select-cipher').addEventListener('change', updateVisualization);
        document.getElementById('input-keyword').addEventListener('input', updateVisualization);
        document.getElementById('slider-offset').addEventListener('input', function() {
            document.getElementById('offset-value').textContent = this.value;
            updateVisualization();
        });

        // Initial render
        updateVisualization();
    </script>
</body>
</html>
