<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Forest Growth Curve</title>
    <style>
        body {
            display: flex;
            font-family: sans-serif;
            margin: 0;
            padding: 0;
        }

        #left-column {
            flex: 2;
            padding: 20px;
        }

        #right-column {
            flex: 1;
            padding: 20px;
        }

        #canvas-container {
            margin-bottom: 20px;
        }

        #results-container {
            font-size: 16px;
        }

        #results-container p {
            margin: 10px 0;
        }

        #data-table {
            border-collapse: collapse;
            width: 100%;
        }

        #data-table th, #data-table td {
            border: 1px solid black;
            padding: 8px;
            text-align: center;
        }

        #data-table th {
            background-color: #f2f2f2;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="left-column">
        <div id="canvas-container"></div>
        <div id="results-container">
            <p>rejuvenation bias <i>u</i> = <span id="results-u"></span></p>
            <p>site productivity <i>p</i> = <span id="results-p"></span></p>
        </div>
    </div>

    <div id="right-column">
        <table id="data-table">
            <thead>
                <tr>
                    <th>age</th>
                    <th>volume</th>
                </tr>
            </thead>
            <tbody id="data-table-body"></tbody>
        </table>
    </div>

    <script>
        // Global variables
        let dataPoints = [
            { age: 30, volume: 289 },
            { age: 35, volume: 445 },
            { age: 40, volume: 565 },
            { age: 45, volume: 704 },
            { age: 50, volume: 833 },
            { age: 55, volume: 924 }
        ];

        let selectedPoint = null;
        let isDragging = false;
        let p = 58.2729;
        let u = 22.7319;

        // Canvas dimensions
        const canvasWidth = 500;
        const canvasHeight = 500;
        const margin = 50;

        // Data ranges
        const xMin = 20, xMax = 80;
        const yMin = 0, yMax = 1500;

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            recalculate();
        }

        function draw() {
            background('#FFFFE0');

            // Draw grid
            strokeWeight(0.5);
            stroke(200);
            drawDashedLine();

            // Draw axes
            strokeWeight(1);
            stroke(0);
            // X-axis
            line(margin, height - margin, width - margin, height - margin);
            // Y-axis
            line(margin, margin, margin, height - margin);

            // Draw tick marks and labels
            fill(0);
            textAlign(CENTER, TOP);
            textSize(12);
            // X-axis ticks
            for (let x = xMin; x <= xMax; x += 10) {
                let px = map(x, xMin, xMax, margin, width - margin);
                line(px, height - margin, px, height - margin + 5);
                text(x, px, height - margin + 10);
            }

            // Y-axis ticks
            textAlign(RIGHT, CENTER);
            for (let y = 200; y <= yMax; y += 200) {
                let py = map(y, yMin, yMax, height - margin, margin);
                line(margin - 5, py, margin, py);
                text(y, margin - 10, py);
            }

            // Draw labels
            textAlign(CENTER, CENTER);
            textSize(16);
            // Title
            text("Forest Growth Curve", width / 2, 20);

            // X-axis label
            textSize(14);
            text("Stand age, yr", width / 2, height - 10);

            // Y-axis label
            push();
            translate(15, height / 2);
            rotate(-PI / 2);
            text("Wood volume, m³ ha⁻¹", 0, 0);
            pop();

            // 4/5 law label
            push();
            translate(width - 15, height / 2);
            rotate(PI / 2);
            text("4/5 law", 0, 0);
            pop();

            // Draw fitted curve
            stroke('#0000CD');
            strokeWeight(2);
            noFill();
            beginShape();
            for (let x = u + 0.1; x <= xMax; x += 0.5) {
                let y = p * Math.pow(x - u, 4/5);
                let px = map(x, xMin, xMax, margin, width - margin);
                let py = map(y, yMin, yMax, height - margin, margin);
                vertex(px, py);
            }
            endShape();

            // Draw data points
            fill('#0000CD');
            stroke(0);
            strokeWeight(1);
            for (let point of dataPoints) {
                let px = map(point.age, xMin, xMax, margin, width - margin);
                let py = map(point.volume, yMin, yMax, height - margin, margin);
                ellipse(px, py, 8, 8);
            }
        }

        function drawDashedLine() {
            // Draw vertical grid lines
            for (let x = xMin; x <= xMax; x += 10) {
                let px = map(x, xMin, xMax, margin, width - margin);
                drawDash(px, margin, px, height - margin, 5, 5);
            }

            // Draw horizontal grid lines
            for (let y = 200; y <= yMax; y += 200) {
                let py = map(y, yMin, yMax, height - margin, margin);
                drawDash(margin, py, width - margin, py, 5, 5);
            }
        }

        function drawDash(x1, y1, x2, y2, dashLength, gapLength) {
            let distance = dist(x1, y1, x2, y2);
            let dashCount = distance / (dashLength + gapLength);
            let dx = (x2 - x1) / distance;
            let dy = (y2 - y1) / distance;

            for (let i = 0; i < dashCount; i++) {
                let startX = x1 + i * (dashLength + gapLength) * dx;
                let startY = y1 + i * (dashLength + gapLength) * dy;
                let endX = startX + dashLength * dx;
                let endY = startY + dashLength * dy;
                line(startX, startY, endX, endY);
            }
        }

        function mousePressed() {
            let mx = mouseX;
            let my = mouseY;

            // Check if click is within plot area
            if (mx < margin || mx > width - margin || my < margin || my > height - margin) {
                return;
            }

            // Check if clicking on existing point
            for (let point of dataPoints) {
                let px = map(point.age, xMin, xMax, margin, width - margin);
                let py = map(point.volume, yMin, yMax, height - margin, margin);
                if (dist(mx, my, px, py) < 10) {
                    selectedPoint = point;
                    isDragging = true;
                    return;
                }
            }

            // Add new point
            let age = map(mx, margin, width - margin, xMin, xMax);
            let volume = map(my, height - margin, margin, yMin, yMax);
            dataPoints.push({ age: age, volume: volume });
            recalculate();
        }

        function mouseDragged() {
            if (isDragging && selectedPoint) {
                let mx = constrain(mouseX, margin, width - margin);
                let my = constrain(mouseY, margin, height - margin);

                selectedPoint.age = map(mx, margin, width - margin, xMin, xMax);
                selectedPoint.volume = map(my, height - margin, margin, yMin, yMax);
                recalculate();
            }
        }

        function mouseReleased() {
            isDragging = false;
            selectedPoint = null;
        }

        function doubleClicked() {
            let mx = mouseX;
            let my = mouseY;

            // Check if double-clicking on existing point
            for (let i = 0; i < dataPoints.length; i++) {
                let point = dataPoints[i];
                let px = map(point.age, xMin, xMax, margin, width - margin);
                let py = map(point.volume, yMin, yMax, height - margin, margin);
                if (dist(mx, my, px, py) < 10) {
                    dataPoints.splice(i, 1);
                    recalculate();
                    return;
                }
            }
        }

        function recalculate() {
            if (dataPoints.length < 2) return;

            // Fit the model using gradient descent
            let learningRate = 0.001;
            let iterations = 1000;

            // Initialize parameters
            p = 50;
            u = 1;

            for (let iter = 0; iter < iterations; iter++) {
                let gradP = 0;
                let gradU = 0;

                for (let point of dataPoints) {
                    if (point.age - u <= 0) continue;

                    let predicted = p * Math.pow(point.age - u, 4/5);
                    let error = predicted - point.volume;

                    gradP += 2 * error * Math.pow(point.age - u, 4/5);
                    gradU += 2 * error * p * (4/5) * Math.pow(point.age - u, -1/5) * (-1);
                }

                // Update parameters
                p -= learningRate * gradP;
                u -= learningRate * gradU;

                // Ensure u is less than minimum age
                let minAge = Math.min(...dataPoints.map(pt => pt.age));
                u = Math.min(u, minAge - 0.1);
            }

            // Update UI
            document.getElementById('results-u').textContent = u.toFixed(4);
            document.getElementById('results-p').textContent = p.toFixed(4);

            // Update table
            let tbody = document.getElementById('data-table-body');
            tbody.innerHTML = '';
            for (let point of dataPoints) {
                let row = tbody.insertRow();
                row.insertCell(0).textContent = point.age.toFixed(0);
                row.insertCell(1).textContent = point.volume.toFixed(0);
            }
        }
    </script>
</body>
</html>
