<body>
    <div id="main-container">
        <div id="controls">
            <div id="text-control">
                <label for="select-text">text</label>
                <select id="select-text">
                    <option>To Be Or Not To Be</option>
                    <option>The quick brown fox jumps over the lazy dog</option>
                    <option>It was the best of times, it was the worst of times</option>
                </select>
            </div>
            <div id="view-controls">
                <label>plot</label>
                <button id="btn-plot-2d" class="active">distribution 2D</button>
                <button id="btn-plot-3d">keyboard 3D</button>
                <label>keyboard</label>
                <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
                <button id="btn-keyboard-dvorak">Dvorak</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="visualization-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Global variables
        let scene, renderer, camera, orthoCam, perspCam;
        let currentView = '2d';
        let currentKeyboard = 'qwerty';
        let letterFrequencies = {};

        // Keyboard layouts
        const keyboards = {
            qwerty: {
                rows: [
                    ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
                    ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
                    ['Z', 'X', 'C', 'V', 'B', 'N', 'M']
                ]
            },
            dvorak: {
                rows: [
                    ['P', 'Y', 'F', 'G', 'C', 'R', 'L'],
                    ['A', 'O', 'E', 'U', 'I', 'D', 'H', 'T', 'N', 'S'],
                    ['Q', 'J', 'K', 'X', 'B', 'M', 'W', 'V', 'Z']
                ]
            }
        };

        // Initialize Three.js
        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Renderer
            const canvas = document.getElementById('visualization-canvas');
            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.shadowMap.enabled = true;

            // Cameras
            const aspect = canvas.clientWidth / canvas.clientHeight;
            orthoCam = new THREE.OrthographicCamera(-15, 15, 10, -10, 0.1, 1000);
            orthoCam.position.set(0, 10, 20);
            orthoCam.lookAt(0, 0, 0);

            perspCam = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
            perspCam.position.set(0, 20, 25);
            perspCam.lookAt(0, 0, 0);

            camera = orthoCam;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 10, 5);
            directionalLight.castShadow = true;
            scene.add(directionalLight);

            resize();
            window.addEventListener('resize', resize);
        }

        function resize() {
            const canvas = renderer.domElement;
            const width = canvas.clientWidth;
            const height = canvas.clientHeight;

            if (canvas.width !== width || canvas.height !== height) {
                renderer.setSize(width, height, false);
                const aspect = width / height;

                orthoCam.left = -15 * aspect;
                orthoCam.right = 15 * aspect;
                orthoCam.updateProjectionMatrix();

                perspCam.aspect = aspect;
                perspCam.updateProjectionMatrix();
            }
        }

        // Calculate letter frequencies
        function calculateFrequencies(text) {
            const freq = {};
            for (let i = 65; i <= 90; i++) {
                freq[String.fromCharCode(i)] = 0;
            }

            text = text.toUpperCase();
            for (let char of text) {
                if (char >= 'A' && char <= 'Z') {
                    freq[char]++;
                }
            }

            return freq;
        }

        // Color mapping function
        function getColorForFrequency(value, maxValue) {
            const normalized = value / maxValue;
            let r, g, b;

            if (normalized < 0.25) {
                // Blue to purple
                r = normalized * 4 * 128;
                g = 0;
                b = 255;
            } else if (normalized < 0.5) {
                // Purple to orange
                const t = (normalized - 0.25) * 4;
                r = 128 + t * 127;
                g = t * 165;
                b = 255 - t * 255;
            } else {
                // Orange to red
                const t = (normalized - 0.5) * 2;
                r = 255;
                g = 165 - t * 165;
                b = 0;
            }

            return new THREE.Color(r/255, g/255, b/255);
        }

        // Create text sprite
        function createTextSprite(text, size = 64) {
            const canvas = document.createElement('canvas');
            canvas.width = size * 2;
            canvas.height = size * 2;
            const context = canvas.getContext('2d');

            context.font = `bold ${size}px Arial`;
            context.fillStyle = 'black';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillText(text, size, size);

            const texture = new THREE.CanvasTexture(canvas);
            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.scale.set(1, 1, 1);

            return sprite;
        }

        // Clear scene
        function clearScene() {
            while(scene.children.length > 2) { // Keep lights
                scene.remove(scene.children[2]);
            }
        }

        // Draw axes and grid
        function drawAxes(maxY) {
            const material = new THREE.LineBasicMaterial({ color: 0x888888 });

            // Y-axis
            const yAxis = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(0, maxY, 0)
            ]);
            scene.add(new THREE.Line(yAxis, material));

            // Grid lines and labels
            for (let i = 0; i <= maxY; i += 50) {
                const gridLine = new THREE.BufferGeometry().setFromPoints([
                    new THREE.Vector3(-15, i, 0),
                    new THREE.Vector3(15, i, 0)
                ]);
                scene.add(new THREE.Line(gridLine, material));

                const label = createTextSprite(i.toString(), 32);
                label.position.set(-1.5, i, 0);
                scene.add(label);
            }
        }

        // Draw 2D distribution
        function draw2DDistribution() {
            clearScene();
            camera = orthoCam;

            const maxFreq = Math.max(...Object.values(letterFrequencies));
            const maxY = Math.ceil(maxFreq / 50) * 50 || 50;

            drawAxes(maxY);

            const letters = Object.keys(letterFrequencies).sort();
            const barWidth = 0.8;
            const spacing = 1;
            const startX = -13;

            letters.forEach((letter, index) => {
                const freq = letterFrequencies[letter];
                const height = (freq / maxY) * 10 || 0.1;

                // Bar
                const geometry = new THREE.BoxGeometry(barWidth, height, barWidth);
                const material = new THREE.MeshStandardMaterial({
                    color: getColorForFrequency(freq, maxFreq)
                });
                const bar = new THREE.Mesh(geometry, material);
                bar.position.set(startX + index * spacing, height / 2, 0);
                bar.castShadow = true;
                bar.receiveShadow = true;
                scene.add(bar);

                // Letter label
                const label = createTextSprite(letter, 24);
                label.position.set(startX + index * spacing, height + 0.5, 0);
                scene.add(label);
            });
        }

        // Get keyboard row color
        function getRowColor(row) {
            const colors = [
                new THREE.Color(0x9ACD32), // Yellow-green
                new THREE.Color(0x00CED1), // Cyan
                new THREE.Color(0x9370DB)  // Purple
            ];
            return colors[row];
        }

        // Draw 3D keyboard
        function draw3DKeyboard() {
            clearScene();
            camera = perspCam;

            const maxFreq = Math.max(...Object.values(letterFrequencies));
            const maxY = Math.ceil(maxFreq / 50) * 50 || 50;

            drawAxes(maxY);

            const layout = keyboards[currentKeyboard];
            const keyRadius = 0.4;
            const keySpacing = 1.2;
            const rowSpacing = 1.5;

            layout.rows.forEach((row, rowIndex) => {
                const rowOffset = (row.length - 1) * keySpacing / 2;

                row.forEach((letter, colIndex) => {
                    const freq = letterFrequencies[letter];
                    const height = (freq / maxY) * 10 || 0.2;

                    // Key cylinder
                    const geometry = new THREE.CylinderGeometry(keyRadius, keyRadius, height, 32);
                    const material = new THREE.MeshStandardMaterial({
                        color: getRowColor(rowIndex)
                    });
                    const key = new THREE.Mesh(geometry, material);

                    const x = colIndex * keySpacing - rowOffset;
                    const z = rowIndex * rowSpacing - 1.5;

                    key.position.set(x, height / 2, z);
                    key.castShadow = true;
                    key.receiveShadow = true;
                    scene.add(key);

                    // Letter label on top
                    const labelGeometry = new THREE.CircleGeometry(keyRadius, 32);
                    const canvas = document.createElement('canvas');
                    canvas.width = 128;
                    canvas.height = 128;
                    const context = canvas.getContext('2d');
                    context.fillStyle = 'white';
                    context.fillRect(0, 0, 128, 128);
                    context.font = 'bold 64px Arial';
                    context.fillStyle = 'black';
                    context.textAlign = 'center';
                    context.textBaseline = 'middle';
                    context.fillText(letter, 64, 64);

                    const texture = new THREE.CanvasTexture(canvas);
                    const labelMaterial = new THREE.MeshBasicMaterial({ map: texture });
                    const label = new THREE.Mesh(labelGeometry, labelMaterial);
                    label.rotation.x = -Math.PI / 2;
                    label.position.set(x, height + 0.01, z);
                    scene.add(label);
                });
            });
        }

        // Update visualization
        function updateVisualization() {
            const text = document.getElementById('select-text').value;
            letterFrequencies = calculateFrequencies(text);

            if (currentView === '2d') {
                draw2DDistribution();
            } else {
                draw3DKeyboard();
            }

            renderer.render(scene, camera);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('select-text').addEventListener('change', updateVisualization);

        document.getElementById('btn-plot-2d').addEventListener('click', function() {
            document.getElementById('btn-plot-2d').classList.add('active');
            document.getElementById('btn-plot-3d').classList.remove('active');
            currentView = '2d';
            updateVisualization();
        });

        document.getElementById('btn-plot-3d').addEventListener('click', function() {
            document.getElementById('btn-plot-2d').classList.remove('active');
            document.getElementById('btn-plot-3d').classList.add('active');
            currentView = '3d';
            updateVisualization();
        });

        document.getElementById('btn-keyboard-qwerty').addEventListener('click', function() {
            document.getElementById('btn-keyboard-qwerty').classList.add('active');
            document.getElementById('btn-keyboard-dvorak').classList.remove('active');
            currentKeyboard = 'qwerty';
            if (currentView === '3d') {
                updateVisualization();
            }
        });

        document.getElementById('btn-keyboard-dvorak').addEventListener('click', function() {
            document.getElementById('btn-keyboard-qwerty').classList.remove('active');
            document.getElementById('btn-keyboard-dvorak').classList.add('active');
            currentKeyboard = 'dvorak';
            if (currentView === '3d') {
                updateVisualization();
            }
        });

        // Initialize
        init();
        updateVisualization();
        animate();
    </script>
</body>
</html>
