    <script>
        let particles = [];
        let isPlaying = false;
        let animationTime = 0;

        // Default values
        const defaults = {
            time: 0,
            trail: 5,
            points: 50,
            pAmp: 0.2,
            pWl: 3,
            sAmp: 0.1,
            sWl: 1
        };

        // Get elements
        const sliderTime = document.getElementById('slider-time');
        const sliderTrail = document.getElementById('slider-trail');
        const sliderPoints = document.getElementById('slider-points');
        const sliderPAmp = document.getElementById('slider-p-amp');
        const sliderPWl = document.getElementById('slider-p-wl');
        const sliderSAmp = document.getElementById('slider-s-amp');
        const sliderSWl = document.getElementById('slider-s-wl');

        const labelTime = document.getElementById('label-time');
        const labelTrail = document.getElementById('label-trail');
        const labelPoints = document.getElementById('label-points');
        const labelPAmp = document.getElementById('label-p-amp');
        const labelPWl = document.getElementById('label-p-wl');
        const labelSAmp = document.getElementById('label-s-amp');
        const labelSWl = document.getElementById('label-s-wl');

        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnReset = document.getElementById('btn-reset');

        // Update label functions
        function updateLabels() {
            labelTime.textContent = parseFloat(sliderTime.value).toFixed(2);
            labelTrail.textContent = sliderTrail.value;
            labelPoints.textContent = sliderPoints.value;
            labelPAmp.textContent = parseFloat(sliderPAmp.value).toFixed(3);
            labelPWl.textContent = parseFloat(sliderPWl.value).toFixed(2);
            labelSAmp.textContent = parseFloat(sliderSAmp.value).toFixed(3);
            labelSWl.textContent = parseFloat(sliderSWl.value).toFixed(2);
        }

        // Initialize particles
        function initializeParticles() {
            particles = [];
            const numPoints = parseInt(sliderPoints.value);
            for (let i = 0; i < numPoints; i++) {
                particles.push({
                    x0: (i / (numPoints - 1)) * 5,
                    history: []
                });
            }
        }

        // Event listeners
        sliderTime.addEventListener('input', updateLabels);
        sliderTrail.addEventListener('input', updateLabels);
        sliderPoints.addEventListener('input', () => {
            updateLabels();
            initializeParticles();
        });
        sliderPAmp.addEventListener('input', updateLabels);
        sliderPWl.addEventListener('input', updateLabels);
        sliderSAmp.addEventListener('input', updateLabels);
        sliderSWl.addEventListener('input', updateLabels);

        btnPlayPause.addEventListener('click', () => {
            isPlaying = !isPlaying;
            btnPlayPause.textContent = isPlaying ? 'Pause' : 'Play';
        });

        btnReset.addEventListener('click', () => {
            isPlaying = false;
            btnPlayPause.textContent = 'Play';

            sliderTime.value = defaults.time;
            sliderTrail.value = defaults.trail;
            sliderPoints.value = defaults.points;
            sliderPAmp.value = defaults.pAmp;
            sliderPWl.value = defaults.pWl;
            sliderSAmp.value = defaults.sAmp;
            sliderSWl.value = defaults.sWl;

            updateLabels();
            initializeParticles();
        });

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(700, 400);
            canvas.parent('canvas-container');
            initializeParticles();
            updateLabels();
        }

        function draw() {
            background(255);

            // Update time if playing
            if (isPlaying) {
                animationTime = parseFloat(sliderTime.value);
                animationTime += 0.01;
                if (animationTime > 5) {
                    animationTime = 0;
                }
                sliderTime.value = animationTime;
                updateLabels();
            }

            // Get current values
            const currentTime = parseFloat(sliderTime.value);
            const trailLength = parseInt(sliderTrail.value);
            const pAmp = parseFloat(sliderPAmp.value);
            const pWl = parseFloat(sliderPWl.value);
            const sAmp = parseFloat(sliderSAmp.value);
            const sWl = parseFloat(sliderSWl.value);

            // Set up coordinate transformation
            push();
            translate(50, height/2);
            scale(120, -height/2.5);

            // Draw axes
            stroke(0);
            strokeWeight(0.01);
            line(0, -0.5, 5, -0.5);
            line(0, -0.5, 0, 0.5);

            // Draw ticks
            for (let x = 0; x <= 5; x++) {
                line(x, -0.51, x, -0.49);
            }
            for (let y = -0.4; y <= 0.4; y += 0.2) {
                line(-0.02, y, 0.02, y);
            }

            // Update and draw particles
            particles.forEach(particle => {
                // Calculate displacements
                const dx = pAmp * sin(TWO_PI * (particle.x0 / pWl - currentTime));
                const dy = sAmp * sin(TWO_PI * (particle.x0 / sWl - currentTime));

                const xFinal = particle.x0 + dx;
                const yFinal = dy;

                // Add to history
                particle.history.push({x: xFinal, y: yFinal});
                if (particle.history.length > trailLength) {
                    particle.history.shift();
                }

                // Draw vertical line
                stroke(211, 211, 211);
                strokeWeight(0.005);
                line(xFinal, -0.5, xFinal, 0.5);

                // Draw trail
                if (trailLength > 0) {
                    noStroke();
                    particle.history.forEach((pos, i) => {
                        fill(106, 90, 205, 100 * (i / particle.history.length));
                        circle(pos.x, pos.y, 0.02);
                    });
                }

                // Draw particle
                fill(106, 90, 205);
                noStroke();
                circle(xFinal, yFinal, 0.04);
            });

            pop();

            // Draw axis labels
            fill(0);
            noStroke();
            textAlign(CENTER);
            text('0', 50, height/2 + 20);
            text('1', 170, height/2 + 20);
            text('2', 290, height/2 + 20);
            text('3', 410, height/2 + 20);
            text('4', 530, height/2 + 20);
            text('5', 650, height/2 + 20);

            textAlign(RIGHT);
            text('0.4', 40, height/2 - 160);
            text('0.2', 40, height/2 - 80);
            text('0', 40, height/2);
            text('-0.2', 40, height/2 + 80);
            text('-0.4', 40, height/2 + 160);
        }
    </script>
</body>
</html>
