                </div>
            </div>
        </div>
        <div id="plot-container">
            <div id="plot"></div>
        </div>
    </div>

    <script>
        // Constants
        const nu = 0.25; // Poisson's ratio
        const b = 1.0; // Dislocation magnitude in meters
        const gridResolution = 50;

        // State
        let currentComponent = 'Z';
        let currentFaultType = 'tensile';

        // Calculate displacements using point source dislocation theory
        function calculateDisplacements(x, y, dip, depth, faultType, nu) {
            const dipRad = dip;
            const cosDip = Math.cos(dipRad);
            const sinDip = Math.sin(dipRad);

            // Distance from source
            const r = Math.sqrt(x*x + y*y + depth*depth);
            if (r === 0) return { ux: 0, uy: 0, uz: 0 };

            // Direction cosines
            const l = x / r;
            const m = y / r;
            const n = -depth / r;

            // Common factors
            const factor = b / (4 * Math.PI * r);
            const c1 = 1 - 2 * nu;

            let ux = 0, uy = 0, uz = 0;

            if (faultType === 'tensile') {
                // Tensile fault (opening mode)
                ux = factor * (3 * l * n * cosDip + c1 * l * sinDip);
                uy = factor * (3 * m * n * cosDip + c1 * m * sinDip);
                uz = factor * (3 * n * n * cosDip - c1 * cosDip);
            } else if (faultType === 'strike-slip') {
                // Strike-slip fault (mode II)
                ux = factor * (3 * l * l - 1) * cosDip;
                uy = factor * (3 * l * m) * cosDip;
                uz = factor * (3 * l * n) * sinDip;
            } else if (faultType === 'normal') {
                // Normal fault (mode III)
                ux = factor * (3 * l * m * cosDip + c1 * m * sinDip);
                uy = factor * (3 * m * m * cosDip - c1 * cosDip);
                uz = factor * (3 * m * n * sinDip);
            }

            return { ux, uy, uz };
        }

        // Generate grid and calculate displacements
        function generatePlotData() {
            const dip = parseFloat(document.getElementById('slider-dip').value) * Math.PI / 180;
            const depth = parseFloat(document.getElementById('slider-depth').value);
            const limit = parseFloat(document.getElementById('slider-limit').value);

            // Create grid
            const x_coords = [];
            const y_coords = [];
            for (let i = 0; i < gridResolution; i++) {
                const val = -limit + (2 * limit * i) / (gridResolution - 1);
                x_coords.push(val);
                y_coords.push(val);
            }

            // Calculate displacements
            const z_values = [];
            for (let j = 0; j < gridResolution; j++) {
                const row = [];
                for (let i = 0; i < gridResolution; i++) {
                    const disp = calculateDisplacements(x_coords[i], y_coords[j], dip, depth, currentFaultType, nu);
                    let value;
                    if (currentComponent === 'X') {
                        value = disp.ux * 1000; // Convert to mm
                    } else if (currentComponent === 'Y') {
                        value = disp.uy * 1000;
                    } else {
                        value = disp.uz * 1000;
                    }
                    row.push(value);
                }
                z_values.push(row);
            }

            return { x_coords, y_coords, z_values, limit };
        }

        // Update plot
        function updatePlot() {
            const { x_coords, y_coords, z_values, limit } = generatePlotData();

            const data = [{
                type: 'surface',
                x: x_coords,
                y: y_coords,
                z: z_values,
                colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
                showscale: false,
                contours: {
                    x: { show: true, color: 'black', width: 0.5 },
                    y: { show: true, color: 'black', width: 0.5 },
                    z: { show: false }
                },
                lighting: {
                    ambient: 0.8,
                    diffuse: 0.8,
                    specular: 0.2
                }
            }];

            const layout = {
                title: '',
                scene: {
                    xaxis: { title: 'x (m)', range: [-limit, limit] },
                    yaxis: { title: 'y (m)', range: [-limit, limit] },
                    zaxis: { title: 'z (mm)', autorange: true },
                    camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
                },
                margin: { l: 20, r: 20, b: 20, t: 20 }
            };

            Plotly.react('plot', data, layout);
        }

        // Initialize event listeners
        function initializeControls() {
            // Slider event listeners
            document.getElementById('slider-dip').addEventListener('input', function(e) {
                document.getElementById('value-dip').textContent = e.target.value;
                updatePlot();
            });

            document.getElementById('slider-depth').addEventListener('input', function(e) {
                document.getElementById('value-depth').textContent = e.target.value;
                updatePlot();
            });

            document.getElementById('slider-limit').addEventListener('input', function(e) {
                document.getElementById('value-limit').textContent = e.target.value;
                updatePlot();
            });

            // Component button event listeners
            document.querySelectorAll('#btn-group-component button').forEach(button => {
                button.addEventListener('click', function() {
                    document.querySelectorAll('#btn-group-component button').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    currentComponent = this.textContent;
                    updatePlot();
                });
            });

            // Fault type button event listeners
            document.querySelectorAll('#btn-group-fault-type button').forEach(button => {
                button.addEventListener('click', function() {
                    document.querySelectorAll('#btn-group-fault-type button').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    if (this.id === 'btn-fault-tensile') currentFaultType = 'tensile';
                    else if (this.id === 'btn-fault-strike-slip') currentFaultType = 'strike-slip';
                    else if (this.id === 'btn-fault-normal') currentFaultType = 'normal';
                    updatePlot();
                });
            });
        }

        // Initialize on page load
        window.addEventListener('DOMContentLoaded', function() {
            initializeControls();
            updatePlot();
        });
    </script>
</body>
</html>
