        }

        #formula-display {
            height: 80px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 20px;
            border-bottom: 1px solid #ddd;
        }

        #canvas-container {
            flex: 1;
            display: flex;
            align-items: center;
            justify-content: center;
