    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <label for="slider-t">t</label>
                <input type="range" id="slider-t" min="0" max="4" step="0.01" value="0.1">
                <span id="t-value-display">0.10</span>
            </div>
            <div>
                <label for="slider-k">k</label>
                <input type="range" id="slider-k" min="1" max="10" step="1" value="2">
                <span id="k-value-display">2</span>
            </div>
            <div>
                <label for="checkbox-axes">axes</label>
                <input type="checkbox" id="checkbox-axes" checked>
            </div>
        </div>
        <div id="visualization-area">
            <div id="formula-display">\(\omega_2 = 2.76 \sqrt{g/l}\)</div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // Bessel function zeros
        const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

        // Bessel function J0 approximation
        function besselJ0(x) {
            const ax = Math.abs(x);
            if (ax < 8.0) {
                const y = x * x;
                const ans1 = 57568490574.0 + y * (-13362590354.0 + y * (651619640.7
                    + y * (-11214424.18 + y * (77392.33017 + y * (-184.9052456)))));
                const ans2 = 57568490411.0 + y * (1029532985.0 + y * (9494680.718
                    + y * (59272.64853 + y * (267.8532712 + y * 1.0))));
                return ans1 / ans2;
            } else {
                const z = 8.0 / ax;
                const y = z * z;
                const xx = ax - 0.785398164;
                const ans1 = 1.0 + y * (-0.1098628627e-2 + y * (0.2734510407e-4
                    + y * (-0.2073370639e-5 + y * 0.2093887211e-6)));
                const ans2 = -0.1562499995e-1 + y * (0.1430488765e-3
                    + y * (-0.6911147651e-5 + y * (0.7621095161e-6
                    - y * 0.934945152e-7)));
                const ans = Math.sqrt(0.636619772 / ax) * 
                    (Math.cos(xx) * ans1 - z * Math.sin(xx) * ans2);
                return x < 0.0 ? -ans : ans;
            }
        }

        let sliderT, sliderK, checkboxAxes;
        let tValue, kValue;

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');

            // Get DOM elements
            sliderT = document.getElementById('slider-t');
            sliderK = document.getElementById('slider-k');
            checkboxAxes = document.getElementById('checkbox-axes');

            // Add event listeners
            sliderT.addEventListener('input', updateTValue);
            sliderK.addEventListener('input', updateKValue);

            // Initial values
            tValue = parseFloat(sliderT.value);
            kValue = parseInt(sliderK.value);

            // Initial formula update
            updateFormula();
        }

        function updateTValue() {
            tValue = parseFloat(sliderT.value);
            document.getElementById('t-value-display').textContent = tValue.toFixed(2);
        }

        function updateKValue() {
            kValue = parseInt(sliderK.value);
            document.getElementById('k-value-display').textContent = kValue;
            updateFormula();
        }

        function updateFormula() {
            const zk = zeros[kValue - 1];
            const omegaK = zk / 2;
            const formulaText = `\\(\\omega_{${kValue}} = ${omegaK.toFixed(2)} \\sqrt{g/l}\\)`;
            document.getElementById('formula-display').innerHTML = formulaText;
            MathJax.typesetPromise([document.getElementById('formula-display')]);
        }

        function draw() {
            background(240);

            // Coordinate mapping
            const xMin = -2, xMax = 2;
            const yMin = -1.2, yMax = 0;

            function mapX(x) {
                return map(x, xMin, xMax, 100, width - 100);
            }

            function mapY(y) {
                return map(y, yMax, yMin, 50, height - 50);
            }

            // Draw axes if checkbox is checked
            if (checkboxAxes.checked) {
                stroke(100);
                strokeWeight(1);
                drawingContext.setLineDash([5, 5]);

                // X-axis
                line(mapX(xMin), mapY(0), mapX(xMax), mapY(0));

                // Y-axis
                line(mapX(0), mapY(yMax), mapX(0), mapY(yMin));

                drawingContext.setLineDash([]);

                // X-axis labels
                textAlign(CENTER, TOP);
                fill(50);
                noStroke();
                for (let x = -2; x <= 2; x++) {
                    if (x !== 0) {
                        text(x, mapX(x), mapY(0) + 5);
                        stroke(100);
                        line(mapX(x), mapY(0) - 3, mapX(x), mapY(0) + 3);
                        noStroke();
                    }
                }

                // Y-axis labels
                textAlign(RIGHT, CENTER);
                for (let y = -0.2; y >= -1.0; y -= 0.2) {
                    text(y.toFixed(1), mapX(0) - 8, mapY(y));
                    stroke(100);
                    line(mapX(0) - 3, mapY(y), mapX(0) + 3, mapY(y));
                    noStroke();
                }
            }

            // Calculate string parameters
            const zk = zeros[kValue - 1];
            const omegaK = zk / 2;
            const timeFactor = Math.cos(omegaK * tValue);
            const amplitude = 0.8;

            // Draw the vibrating string
            stroke(70, 130, 180);
            strokeWeight(3);
            noFill();

            beginShape();
            for (let yNorm = 0; yNorm <= 1; yNorm += 0.01) {
                const besselArg = zk * Math.sqrt(yNorm);
                const xPos = amplitude * timeFactor * besselJ0(besselArg);
                const yPos = -yNorm;

                vertex(mapX(xPos), mapY(yPos));
            }
            endShape();
        }
    </script>
</body>
</html>
