                <label>layering (bottom to top)</label>
                <span id="layering-display">1, 2, 3, 4</span>
                <div class="layer-buttons">
                    <button id="btn-layer-next">▴</button>
                    <button id="btn-layer-prev">▾</button>
                </div>
            </div>
        </div>

        <div id="main-content">
            <div id="color-controls">
                <div class="color-control">
                    <label for="color-picker1">color 1</label>
                    <input type="color" id="color-picker1" value="#00ff00">
                </div>

                <div class="color-control">
                    <label for="color-picker2">color 2</label>
                    <input type="color" id="color-picker2" value="#0000ff">
                </div>

                <div class="color-control">
                    <label for="color-picker3">color 3</label>
                    <input type="color" id="color-picker3" value="#ff0000">
                </div>

                <div class="color-control">
                    <label for="color-picker4">color 4</label>
                    <input type="color" id="color-picker4" value="#000000">
                </div>
            </div>

            <div id="canvas-container"></div>
        </div>
