    <script>
        // State variables
        let layeringOrderIndex = 0;
        const layeringPermutations = [
            [1, 2, 3, 4],
            [4, 3, 2, 1],
            [1, 3, 2, 4],
            [2, 4, 1, 3],
            [3, 1, 4, 2]
        ];

        // p5.js sketch
        const sketch = (p) => {
            p.setup = () => {
                const canvas = p.createCanvas(600, 600);
                canvas.parent('canvas-container');
                p.noLoop();
            };

            p.draw = () => {
                p.background(240);

                // Get current layering order
                const currentOrder = layeringPermutations[layeringOrderIndex];

                // Circle data
                const circles = {
                    1: { x: p.width * 0.35, y: p.height * 0.4, d: p.width * 0.4 },
                    2: { x: p.width * 0.65, y: p.height * 0.4, d: p.width * 0.4 },
                    3: { x: p.width * 0.5, y: p.height * 0.65, d: p.width * 0.4 },
                    4: { x: p.width * 0.5, y: p.height * 0.45, d: p.width * 0.3 }
                };

                // Draw circles in the current layer order
                currentOrder.forEach(circleNum => {
                    const circle = circles[circleNum];
                    const color = document.getElementById(`color-picker${circleNum}`).value;
                    const opacity = parseFloat(document.getElementById(`slider-opacity${circleNum}`).value);

                    // Convert hex to RGB
                    const r = parseInt(color.substr(1, 2), 16);
                    const g = parseInt(color.substr(3, 2), 16);
                    const b = parseInt(color.substr(5, 2), 16);

                    p.noStroke();
                    p.fill(r, g, b, opacity * 255);
                    p.ellipse(circle.x, circle.y, circle.d);
                });

                // Draw labels
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(32);
                p.fill(0);

                for (let i = 1; i <= 4; i++) {
                    const circle = circles[i];
                    p.text(i.toString(), circle.x, circle.y);
                }
            };
        };

        // Initialize p5.js
        let myp5 = new p5(sketch);

        // Event listeners for opacity sliders
        for (let i = 1; i <= 4; i++) {
            document.getElementById(`slider-opacity${i}`).addEventListener('input', () => {
                myp5.redraw();
            });
        }

        // Event listeners for color pickers
        for (let i = 1; i <= 4; i++) {
            document.getElementById(`color-picker${i}`).addEventListener('input', () => {
                myp5.redraw();
            });
        }

        // Layering control event listeners
        document.getElementById('btn-layer-next').addEventListener('click', () => {
            layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
            updateLayeringDisplay();
            myp5.redraw();
        });

        document.getElementById('btn-layer-prev').addEventListener('click', () => {
            layeringOrderIndex = layeringOrderIndex - 1;
            if (layeringOrderIndex < 0) {
                layeringOrderIndex = layeringPermutations.length - 1;
            }
            updateLayeringDisplay();
            myp5.redraw();
        });

        // Update layering display
        function updateLayeringDisplay() {
            const currentOrder = layeringPermutations[layeringOrderIndex];
            document.getElementById('layering-display').textContent = currentOrder.join(', ');
        }

        // Reset button event listener
        document.getElementById('btn-reset').addEventListener('click', () => {
            // Reset opacity sliders
            for (let i = 1; i <= 4; i++) {
                document.getElementById(`slider-opacity${i}`).value = 0.7;
