<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <button id="btn-srk" class="selected">SRK</button>
                <button id="btn-pr">PR</button>
            </div>
            <div>
                <input type="checkbox" id="checkbox-aspen" checked>
                <label for="checkbox-aspen">comparison with Aspen Plus</label>
            </div>
            <div>
                <label for="slider-pressure">pressure P</label>
                <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
                <span id="display-pressure">10</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Constants for Propylene
        const Tc = 365.57; // K
        const Pc = 46.65; // bar
        const omega = 0.144;
        const R = 0.0831446; // L·bar/(mol·K)

        // Aspen Plus data
        const aspenData = {
            x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
            y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946]
        };

        // Current state
        let currentEoS = 'SRK';
        let currentPressure = 10;
        let showAspen = true;

        // DOM elements
        const btnSRK = document.getElementById('btn-srk');
        const btnPR = document.getElementById('btn-pr');
        const sliderPressure = document.getElementById('slider-pressure');
        const displayPressure = document.getElementById('display-pressure');
        const checkboxAspen = document.getElementById('checkbox-aspen');
        const plotContainer = document.getElementById('plot-container');

        // Cubic equation solver
        function solveCubic(a, b, c, d) {
            // Solve ax³ + bx² + cx + d = 0
            // Convert to x³ + px² + qx + r = 0
            const p = b / a;
            const q = c / a;
            const r = d / a;

            // Calculate discriminant
            const a0 = q - p * p / 3;
            const b0 = r - p * q / 3 + 2 * p * p * p / 27;
            const disc = b0 * b0 / 4 + a0 * a0 * a0 / 27;

            const roots = [];

            if (disc > 0) {
                // One real root
                const sqrtDisc = Math.sqrt(disc);
                const u = Math.cbrt(-b0 / 2 + sqrtDisc);
                const v = Math.cbrt(-b0 / 2 - sqrtDisc);
                roots.push(u + v - p / 3);
            } else {
                // Three real roots
                const theta = Math.acos(-b0 / 2 / Math.sqrt(-a0 * a0 * a0 / 27));
                const sqrt_a0 = 2 * Math.sqrt(-a0 / 3);
                roots.push(sqrt_a0 * Math.cos(theta / 3) - p / 3);
                roots.push(sqrt_a0 * Math.cos((theta + 2 * Math.PI) / 3) - p / 3);
                roots.push(sqrt_a0 * Math.cos((theta + 4 * Math.PI) / 3) - p / 3);
            }

            return roots;
        }

        // Calculate fugacity coefficient
        function calculateFugacity(T, P, eos) {
            const Tr = T / Tc;

            let a, b, m, alpha, A, B, Z, lnPhi;

            if (eos === 'SRK') {
                a = 0.42748 * R * R * Tc * Tc / Pc;
                b = 0.08664 * R * Tc / Pc;
                m = 0.480 + 1.574 * omega - 0.176 * omega * omega;
                alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
                const a_eff = a * alpha;
                A = a_eff * P / (R * R * T * T);
                B = b * P / (R * T);

                // Solve Z³ - Z² + (A - B - B²)Z - AB = 0
                const roots = solveCubic(1, -1, A - B - B * B, -A * B);
                Z = Math.max(...roots.filter(r => r > 0));

                lnPhi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);
            } else { // PR
                a = 0.45724 * R * R * Tc * Tc / Pc;
                b = 0.07780 * R * Tc / Pc;
                m = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
                alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
                const a_eff = a * alpha;
                A = a_eff * P / (R * R * T * T);
                B = b * P / (R * T);

                // Solve Z³ - (1-B)Z² + (A - 3B² - 2B)Z - (AB - B² - B³) = 0
                const roots = solveCubic(1, -(1 - B), A - 3 * B * B - 2 * B, -(A * B - B * B - B * B * B));
                Z = Math.max(...roots.filter(r => r > 0));

                const sqrt2 = Math.sqrt(2);
                lnPhi = Z - 1 - Math.log(Z - B) - (A / (2 * sqrt2 * B)) * 
                        Math.log((Z + (1 + sqrt2) * B) / (Z + (1 - sqrt2) * B));
            }

            return Math.exp(lnPhi);
        }

        // Generate fugacity data
        function generateFugacityData(P, eos) {
            const temps = [];
            const fugacities = [];

            for (let T = 300; T <= 500; T += 2) {
                temps.push(T);
                fugacities.push(calculateFugacity(T, P, eos));
            }

            return { x: temps, y: fugacities };
        }

        // Update plot
        function updatePlot() {
            const fugacityData = generateFugacityData(currentPressure, currentEoS);

            const traces = [{
                x: fugacityData.x,
                y: fugacityData.y,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: currentEoS === 'SRK' ? 'red' : 'blue',
                    width: 2
                },
                name: currentEoS
            }];

            // Add Aspen data if conditions are met
            if (showAspen && currentPressure === 10) {
                traces.push({
                    x: aspenData.x,
                    y: aspenData.y,
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        symbol: 'square',
                        size: 8
                    },
                    name: 'Aspen Plus'
                });
            }

            const layout = {
                xaxis: {
                    title: 'temperature (K)',
                    range: [295, 505],
                    showgrid: true,
                    gridcolor: 'white',
                    showline: true,
                    linecolor: 'black'
                },
                yaxis: {
                    title: 'gas-phase fugacity coefficient',
                    range: [0.68, 1.02],
                    showgrid: true,
                    gridcolor: 'white',
                    showline: true,
                    linecolor: 'black'
                },
                plot_bgcolor: '#e0e0e0',
                paper_bgcolor: 'white',
                showlegend: true,
                legend: {
                    x: 0.7,
                    y: 0.95
                }
            };

            Plotly.newPlot(plotContainer, traces, layout, {responsive: true});
        }

        // Event handlers
        btnSRK.addEventListener('click', () => {
            currentEoS = 'SRK';
            btnSRK.classList.add('selected');
            btnPR.classList.remove('selected');
            updatePlot();
        });

        btnPR.addEventListener('click', () => {
            currentEoS = 'PR';
            btnPR.classList.add('selected');
            btnSRK.classList.remove('selected');
            updatePlot();
        });

        sliderPressure.addEventListener('input', () => {
            currentPressure = parseInt(sliderPressure.value);
            displayPressure.textContent = currentPressure;
            updatePlot();
        });

        checkboxAspen.addEventListener('change', () => {
            showAspen = checkboxAspen.checked;
            updatePlot();
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
