    <script>
        // Global state
        let state = {
            mode: 'da',
            inverse: false,
            focus: false,
            reactionProgress: 0,
            ergEwgIntensity: 0
        };

        // Mode button handlers
        const modeButtons = {
            'btn-mode-da': 'da',
            'btn-mode-regio': 'regio',
            'btn-mode-stereo-phile': 'stereo-phile',
            'btn-mode-stereo-diene': 'stereo-diene',
            'btn-mode-homo': 'homo'
        };

        Object.entries(modeButtons).forEach(([btnId, mode]) => {
            document.getElementById(btnId).addEventListener('click', () => {
                state.mode = mode;
                updateModeUI();
                updateControlVisibility();
            });
        });

        function updateModeUI() {
            document.querySelectorAll('.mode-button').forEach(btn => {
                btn.classList.remove('active');
            });
            Object.entries(modeButtons).forEach(([btnId, mode]) => {
                if (state.mode === mode) {
                    document.getElementById(btnId).classList.add('active');
                }
            });
        }

        function updateControlVisibility() {
            const controls = {
                'inverse-container': ['regio', 'homo'].includes(state.mode),
                'focus-container': ['da', 'regio', 'stereo-phile', 'stereo-diene'].includes(state.mode),
                'reaction-slider-container': ['da', 'regio'].includes(state.mode),
                'erg-ewg-slider-container': ['regio', 'homo'].includes(state.mode)
            };

            Object.entries(controls).forEach(([id, visible]) => {
                const element = document.getElementById(id);
                if (visible) {
                    element.classList.remove('hidden');
                } else {
                    element.classList.add('hidden');
                }
            });
        }

        // Control handlers
        document.getElementById('check-inverse').addEventListener('change', (e) => {
            state.inverse = e.target.checked;
        });

        document.getElementById('check-focus').addEventListener('change', (e) => {
            state.focus = e.target.checked;
        });

        document.getElementById('slider-reaction').addEventListener('input', (e) => {
            state.reactionProgress = parseInt(e.target.value);
        });

        document.getElementById('btn-reaction-minus').addEventListener('click', () => {
            const slider = document.getElementById('slider-reaction');
            slider.value = Math.max(0, parseInt(slider.value) - 1);
            state.reactionProgress = parseInt(slider.value);
        });

        document.getElementById('btn-reaction-plus').addEventListener('click', () => {
            const slider = document.getElementById('slider-reaction');
            slider.value = Math.min(100, parseInt(slider.value) + 1);
            state.reactionProgress = parseInt(slider.value);
        });

        document.getElementById('slider-erg-ewg').addEventListener('input', (e) => {
            state.ergEwgIntensity = parseInt(e.target.value);
        });

        document.getElementById('btn-erg-ewg-minus').addEventListener('click', () => {
            const slider = document.getElementById('slider-erg-ewg');
            slider.value = Math.max(0, parseInt(slider.value) - 1);
            state.ergEwgIntensity = parseInt(slider.value);
        });

        document.getElementById('btn-erg-ewg-plus').addEventListener('click', () => {
            const slider = document.getElementById('slider-erg-ewg');
            slider.value = Math.min(100, parseInt(slider.value) + 1);
            state.ergEwgIntensity = parseInt(slider.value);
        });

        // Initialize visibility
        updateControlVisibility();

        // p5.js sketch
        function sketch(p) {
            let canvas;

            p.setup = function() {
                const container = document.getElementById('canvas-container');
                canvas = p.createCanvas(container.offsetWidth - 40, container.offsetHeight - 40);
                canvas.parent('canvas-container');
            };

            p.draw = function() {
                p.background(255);

                switch(state.mode) {
                    case 'da':
                        drawDielsAlderReaction(p);
                        break;
                    case 'regio':
                        drawRegioselectivity(p);
                        break;
                    case 'stereo-phile':
                        drawStereoDienophile(p);
                        break;
                    case 'stereo-diene':
                        drawStereoDiene(p);
                        break;
                    case 'homo':
                        drawHOMOLUMO(p);
                        break;
                }
            };

            function drawDielsAlderReaction(p) {
                const progress = state.reactionProgress / 100;
                const centerX = p.width / 2;
                const centerY = p.height / 2;

                // Calculate positions
                const dieneStartX = centerX - 200;
                const dienophileStartX = centerX + 100;
                const dieneX = p.lerp(dieneStartX, centerX - 50, Math.min(progress * 2, 1));
                const dienophileX = p.lerp(dienophileStartX, centerX + 50, Math.min(progress * 2, 1));

                if (progress < 0.5) {
                    // Draw reactants approaching
                    drawButadiene(p, dieneX, centerY, 1, state.focus);
                    drawEthylene(p, dienophileX, centerY, 1, state.focus);
                } else {
                    // Draw reaction and product formation
                    const reactionProgress = (progress - 0.5) * 2;
                    drawReactionTransition(p, centerX, centerY, reactionProgress, state.focus);
                }
            }

            function drawButadiene(p, x, y, opacity, highlight) {
                p.push();
                p.translate(x, y);
                p.strokeWeight(2);
                p.stroke(0, 0, 0, opacity * 255);

                // Draw C=C-C=C structure
                p.line(-60, -10, -20, -10);
                p.line(-60, -6, -20, -6);
                p.line(-20, -10, 20, 10);
                p.line(20, 10, 60, 10);
                p.line(20, 14, 60, 14);

                if (highlight) {
                    p.strokeWeight(4);
                    p.stroke(255, 0, 0, opacity * 128);
                    p.line(-60, -8, -20, -8);
                    p.line(20, 12, 60, 12);
                }
                p.pop();
            }

            function drawEthylene(p, x, y, opacity, highlight) {
                p.push();
                p.translate(x, y);
                p.strokeWeight(2);
                p.stroke(0, 0, 0, opacity * 255);

                // Draw C=C structure
                p.line(-30, 0, 30, 0);
                p.line(-30, 4, 30, 4);

                if (highlight) {
                    p.strokeWeight(4);
                    p.stroke(255, 0, 0, opacity * 128);
                    p.line(-30, 2, 30, 2);
                }
                p.pop();
            }

            function drawReactionTransition(p, x, y, progress, highlight) {
                p.push();
                p.translate(x, y);

                // Interpolate between reactants and cyclohexene
                const angle = progress * p.TWO_PI / 6;
                const radius = p.lerp(40, 60, progress);

                p.strokeWeight(2);

                // Draw forming ring
                for (let i = 0; i < 6; i++) {
                    const x1 = radius * p.cos(i * p.TWO_PI / 6);
                    const y1 = radius * p.sin(i * p.TWO_PI / 6);
                    const x2 = radius * p.cos((i + 1) * p.TWO_PI / 6);
                    const y2 = radius * p.sin((i + 1) * p.TWO_PI / 6);

                    if (i === 0) {
                        // Double bond in final product
                        p.stroke(0, 0, 0, p.lerp(128, 255, progress));
                        p.line(x1, y1, x2, y2);
                        const offset = 4;
                        const dx = (y2 - y1) / p.dist(x1, y1, x2, y2) * offset;
                        const dy = -(x2 - x1) / p.dist(x1, y1, x2, y2) * offset;
                        p.line(x1 + dx, y1 + dy, x2 + dx, y2 + dy);

                        if (highlight) {
                            p.strokeWeight(4);
                            p.stroke(255, 0, 0, 128);
                            p.line(x1, y1, x2, y2);
                        }
                    } else {
                        p.stroke(0, 0, 0, 255);
                        p.line(x1, y1, x2, y2);

                        if (highlight && (i === 2 || i === 5)) {
                            p.strokeWeight(4);
                            p.stroke(255, 0, 0, p.lerp(0, 128, progress));
                            p.line(x1, y1, x2, y2);
                        }
                    }
                    p.strokeWeight(2);
                }

                p.pop();
            }

            function drawRegioselectivity(p) {
                const progress = state.reactionProgress / 100;
                const intensity = state.ergEwgIntensity / 100;

                // Draw two parallel reaction pathways
                const topY = p.height * 0.3;
                const bottomY = p.height * 0.7;

                // Top pathway (ortho product)
                const orthoOpacity = state.inverse ? 1 - intensity * 0.5 : 1;
                drawRegioPathway(p, p.width / 2, topY, progress, orthoOpacity, 'ortho', state.focus);

                // Bottom pathway (meta product)
                const metaOpacity = state.inverse ? 1 : 1 - intensity * 0.5;
                drawRegioPathway(p, p.width / 2, bottomY, progress, metaOpacity, 'meta', state.focus);
            }

            function drawRegioPathway(p, x, y, progress, opacity, type, highlight) {
                p.push();
                p.translate(x, y);

                const startX = -200;
                const endX = 100;
                const currentX = p.lerp(startX, endX, progress);

                p.stroke(0, 0, 0, opacity * 255);
                p.strokeWeight(2);

                // Draw reactants or products based on progress
                if (progress < 0.5) {
                    // Draw approaching reactants
                    p.text("Substituted diene", currentX - 50, -20);
                    p.text("Substituted dienophile", currentX + 50, -20);
                } else {
                    // Draw product
                    p.text(type + " product", 0, -20);
                    drawCyclohexeneProduct(p, 0, 0, opacity, highlight, type);
                }

                p.pop();
            }

            function drawCyclohexeneProduct(p, x, y, opacity, highlight, type) {
                const radius = 50;
                p.stroke(0, 0, 0, opacity * 255);

                for (let i = 0; i < 6; i++) {
                    const x1 = x + radius * p.cos(i * p.TWO_PI / 6);
                    const y1 = y + radius * p.sin(i * p.TWO_PI / 6);
                    const x2 = x + radius * p.cos((i + 1) * p.TWO_PI / 6);
                    const y2 = y + radius * p.sin((i + 1) * p.TWO_PI / 6);
                    p.line(x1, y1, x2, y2);
                }

                // Add substituent markers
                if (type === 'ortho') {
                    p.text("R1", x + radius + 10, y - radius/2);
                    p.text("R2", x + radius + 10, y + radius/2);
                } else {
                    p.text("R1", x + radius + 10, y - radius/2);
                    p.text("R2", x - radius - 20, y);
                }
            }

            function drawStereoDienophile(p) {
                const centerX = p.width / 2;

                // Top half - cis dienophile
                p.push();
                p.translate(centerX, p.height * 0.25);
                p.textAlign(p.CENTER);
                p.text("cis-dienophile → meso product", 0, -80);
                drawStereoProduct(p, 0, 0, 'meso', state.focus);

                // Draw symmetry plane
                p.stroke(255, 0, 0, 128);
                p.strokeWeight(2);
                p.drawingContext.setLineDash([5, 5]);
                p.line(0, -60, 0, 60);
                p.drawingContext.setLineDash([]);
                p.pop();

                // Bottom half - trans dienophile
                p.push();
                p.translate(centerX, p.height * 0.75);
                p.textAlign(p.CENTER);
                p.text("trans-dienophile → racemic mixture", 0, -80);
                drawStereoProduct(p, -80, 0, 'R', state.focus);
                drawStereoProduct(p, 80, 0, 'S', state.focus);
                p.pop();
            }

            function drawStereoDiene(p) {
                const centerX = p.width / 2;

                // Top half - trans,trans-diene
                p.push();
                p.translate(centerX, p.height * 0.25);
                p.textAlign(p.CENTER);
                p.text("trans,trans-diene → meso product", 0, -80);
                drawStereoProduct(p, 0, 0, 'meso', state.focus);

                // Draw symmetry plane
                p.stroke(255, 0, 0, 128);
                p.strokeWeight(2);
                p.drawingContext.setLineDash([5, 5]);
                p.line(0, -60, 0, 60);
                p.drawingContext.setLineDash([]);
                p.pop();

                // Bottom half - trans,cis-diene
                p.push();
                p.translate(centerX, p.height * 0.75);
                p.textAlign(p.CENTER);
                p.text("trans,cis-diene → racemic mixture", 0, -80);
                drawStereoProduct(p, -80, 0, 'R', state.focus);
                drawStereoProduct(p, 80, 0, 'S', state.focus);
                p.pop();
            }

            function drawStereoProduct(p, x, y, type, highlight) {
                const radius = 40;
                p.strokeWeight(2);
                p.stroke(0);

                // Draw cyclohexene ring
                for (let i = 0; i < 6; i++) {
                    const x1 = x + radius * p.cos(i * p.TWO_PI / 6);
                    const y1 = y + radius * p.sin(i * p.TWO_PI / 6);
                    const x2 = x + radius * p.cos((i + 1) * p.TWO_PI / 6);
                    const y2 = y + radius * p.sin((i + 1) * p.TWO_PI / 6);
                    p.line(x1, y1, x2, y2);

                    if (highlight && (i === 1 || i === 4)) {
                        p.push();
                        p.strokeWeight(4);
                        p.stroke(255, 0, 0, 128);
                        p.line(x1, y1, x2, y2);
                        p.pop();
                    }
                }

                // Add stereochemistry indicators
                p.textAlign(p.CENTER);
                p.text(type, x, y + radius + 20);
            }

            function drawHOMOLUMO(p) {
                const centerX = p.width / 2;
                const centerY = p.height / 2;
                const intensity = state.ergEwgIntensity / 100;

                // Base energy levels
                let dieneHOMO = centerY + 50;
                let dieneLUMO = centerY - 100;
                let dienophileHOMO = centerY + 100;
                let dienophileLUMO = centerY - 50;

                // Adjust levels based on ERG/EWG effects
                if (!state.inverse) {
                    dieneHOMO -= intensity * 30;
                    dieneLUMO -= intensity * 30;
                    dienophileHOMO += intensity * 30;
                    dienophileLUMO += intensity * 30;
                } else {
                    dieneHOMO += intensity * 30;
                    dieneLUMO += intensity * 30;
                    dienophileHOMO -= intensity * 30;
                    dienophileLUMO -= intensity * 30;
                }

                // Draw energy levels
                p.strokeWeight(2);
                p.stroke(0);

                // Diene levels
                p.line(centerX - 200, dieneHOMO, centerX - 100, dieneHOMO);
                p.line(centerX - 200, dieneLUMO, centerX - 100, dieneLUMO);

                // Dienophile levels
                p.line(centerX + 100, dienophileHOMO, centerX + 200, dienophileHOMO);
                p.line(centerX + 100, dienophileLUMO, centerX + 200, dienophileLUMO);

                // Draw orbital representations
                drawOrbital(p, centerX - 150, dieneHOMO, true);
                drawOrbital(p, centerX - 150, dieneLUMO, false);
                drawOrbital(p, centerX + 150, dienophileHOMO, true);
                drawOrbital(p, centerX + 150, dienophileLUMO, false);

                // Draw interaction arrows
                const gap1 = Math.abs(dieneHOMO - dienophileLUMO);
                const gap2 = Math.abs(dienophileHOMO - dieneLUMO);

                p.drawingContext.setLineDash([5, 5]);

                // Diene HOMO -> Dienophile LUMO
                if (gap1 < gap2 || (!state.inverse && intensity > 0)) {
                    p.strokeWeight(3);
                    p.stroke(255, 0, 0);
                } else {
                    p.strokeWeight(1);
                    p.stroke(0);
                }
                drawArrow(p, centerX - 100, dieneHOMO, centerX + 100, dienophileLUMO);

                // Dienophile HOMO -> Diene LUMO
                if (gap2 < gap1 || (state.inverse && intensity > 0)) {
                    p.strokeWeight(3);
                    p.stroke(255, 0, 0);
                } else {
                    p.strokeWeight(1);
                    p.stroke(0);
                }
                drawArrow(p, centerX + 100, dienophileHOMO, centerX - 100, dieneLUMO);

                p.drawingContext.setLineDash([]);

                // Labels
                p.textAlign(p.CENTER);
                p.text("Diene", centerX - 150, centerY - 150);
                p.text("Dienophile", centerX + 150, centerY - 150);

                if (state.inverse) {
                    p.text("(EWG)", centerX - 150, centerY - 130);
                    p.text("(ERG)", centerX + 150, centerY - 130);
                } else {
                    p.text("(ERG)", centerX - 150, centerY - 130);
                    p.text("(EWG)", centerX + 150, centerY - 130);
                }
            }

            function drawOrbital(p, x, y, filled) {
                p.strokeWeight(1);
                p.stroke(0);
                p.line(x - 10, y - 10, x - 10, y - 5);
                p.line(x - 5, y - 10, x - 5, y - 5);

                if (filled) {
                    // Draw electron arrows
                    p.line(x - 10, y - 7, x - 10, y - 5);
                    p.line(x - 10, y - 7, x - 12, y - 6);
                    p.line(x - 5, y - 5, x - 5, y - 7);
                    p.line(x - 5, y - 5, x - 3, y - 6);
                }
            }

            function drawArrow(p, x1, y1, x2, y2) {
                p.line(x1, y1, x2, y2);

                // Arrowhead
                const angle = p.atan2(y2 - y1, x2 - x1);
                const arrowSize = 10;
                p.line(x2, y2, x2 - arrowSize * p.cos(angle - p.PI / 6), y2 - arrowSize * p.sin(angle - p.PI / 6));
                p.line(x2, y2, x2 - arrowSize * p.cos(angle + p.PI / 6), y2 - arrowSize * p.sin(angle + p.PI / 6));
            }
        }

        // Create p5 instance
        new p5(sketch);
    </script>
</body>
</html>
