</head>
<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        let currentStep = "1";
        let pointP = {x: 0, y: 0};
        let isDragging = false;
        let triangleVertices = {};
        let canvasWidth = 600;
        let canvasHeight = 520;

        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            // Initialize triangle vertices
            let centerX = width / 2;
            let centerY = height / 2;
            let radius = 200;

            triangleVertices.A = {
                x: centerX - radius * cos(PI/6),
                y: centerY + radius * sin(PI/6)
            };
            triangleVertices.B = {
                x: centerX + radius * cos(PI/6),
                y: centerY + radius * sin(PI/6)
            };
            triangleVertices.C = {
                x: centerX,
                y: centerY - radius
            };

            // Initialize point P position
            pointP.x = width * 0.4;
            pointP.y = height * 0.6;

            // Add event listeners for radio buttons
            document.querySelectorAll('input[name="step-selector"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    currentStep = this.value;
                    redraw();
                });
            });
        }

        function draw() {
            background(255);

            // Draw main equilateral triangle
            stroke(0);
            strokeWeight(2);
            noFill();
            triangle(triangleVertices.A.x, triangleVertices.A.y,
                    triangleVertices.B.x, triangleVertices.B.y,
                    triangleVertices.C.x, triangleVertices.C.y);

            // Label vertices
            fill(0);
            noStroke();
            textSize(16);
            textAlign(CENTER, TOP);
            text('A', triangleVertices.A.x, triangleVertices.A.y + 5);
            text('B', triangleVertices.B.x, triangleVertices.B.y + 5);
            textAlign(CENTER, BOTTOM);
            text('C', triangleVertices.C.x, triangleVertices.C.y - 5);

            // Draw main altitude (dashed line)
            stroke(135, 206, 235);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            let midAB = {
                x: (triangleVertices.A.x + triangleVertices.B.x) / 2,
                y: (triangleVertices.A.y + triangleVertices.B.y) / 2
            };
            line(triangleVertices.C.x, triangleVertices.C.y, midAB.x, midAB.y);
            drawingContext.setLineDash([]);

            // Constrain P to triangle
            pointP = constrainPointToTriangle(pointP);

            // Draw based on current step
            if (currentStep === "1") {
                drawStep1();
            } else if (currentStep === "2") {
                drawStep2();
            } else if (currentStep === "3") {
                drawStep3();
            }

            // Draw point P (except in step 3)
            if (currentStep !== "3") {
                drawPointP(pointP.x, pointP.y, false);
            }
        }

        function drawStep1() {
            // Calculate perpendicular distances and feet
            let perpAB = getPerpendicularToLine(pointP, triangleVertices.A, triangleVertices.B);
            let perpBC = getPerpendicularToLine(pointP, triangleVertices.B, triangleVertices.C);
            let perpAC = getPerpendicularToLine(pointP, triangleVertices.A, triangleVertices.C);

            // Draw perpendicular lines
            stroke(0);
            strokeWeight(1);
            line(pointP.x, pointP.y, perpAB.foot.x, perpAB.foot.y);
            line(pointP.x, pointP.y, perpBC.foot.x, perpBC.foot.y);
            line(pointP.x, pointP.y, perpAC.foot.x, perpAC.foot.y);

            // Draw feet as blue circles
            fill(0, 0, 255);
            noStroke();
            circle(perpAB.foot.x, perpAB.foot.y, 8);
            circle(perpBC.foot.x, perpBC.foot.y, 8);
            circle(perpAC.foot.x, perpAC.foot.y, 8);
        }

        function drawStep2() {
            let perpAB = getPerpendicularToLine(pointP, triangleVertices.A, triangleVertices.B);
            let perpBC = getPerpendicularToLine(pointP, triangleVertices.B, triangleVertices.C);
            let perpAC = getPerpendicularToLine(pointP, triangleVertices.A, triangleVertices.C);

            // Draw red triangle (base parallel to AB)
            let redSide = perpAB.distance * 2 / sqrt(3);
            drawEquilateralTriangle(pointP, redSide, 0, 'rgba(255, 0, 0, 0.5)', false);

            // Draw green triangle (base parallel to AC)
            let greenSide = perpAC.distance * 2 / sqrt(3);
            let angleAC = atan2(triangleVertices.C.y - triangleVertices.A.y, 
                               triangleVertices.C.x - triangleVertices.A.x) + PI/2;
            drawEquilateralTriangle(pointP, greenSide, angleAC, 'rgba(0, 255, 0, 0.5)', true, 'x');

            // Draw blue triangle (base parallel to BC)
            let blueSide = perpBC.distance * 2 / sqrt(3);
            let angleBC = atan2(triangleVertices.C.y - triangleVertices.B.y,
                               triangleVertices.C.x - triangleVertices.B.x) + PI/2;
            drawEquilateralTriangle(pointP, blueSide, angleBC, 'rgba(0, 0, 255, 0.5)', true, 'y');

            // Draw perpendiculars
            stroke(0);
            strokeWeight(1);
            line(pointP.x, pointP.y, perpAB.foot.x, perpAB.foot.y);
            line(pointP.x, pointP.y, perpBC.foot.x, perpBC.foot.y);
            line(pointP.x, pointP.y, perpAC.foot.x, perpAC.foot.y);
        }

        function drawStep3() {
            let perpAB = getPerpendicularToLine(pointP, triangleVertices.A, triangleVertices.B);
            let perpBC = getPerpendicularToLine(pointP, triangleVertices.B, triangleVertices.C);
            let perpAC = getPerpendicularToLine(pointP, triangleVertices.A, triangleVertices.C);

            let altitudes = [perpAB.distance, perpBC.distance, perpAC.distance];
            let colors = ['rgba(255, 0, 0, 0.5)', 'rgba(0, 0, 255, 0.5)', 'rgba(0, 255, 0, 0.5)'];

            // Draw stacked triangles
            let baseY = triangleVertices.A.y;
            let centerX = (triangleVertices.A.x + triangleVertices.B.x) / 2;

            for (let i = 0; i < 3; i++) {
                let side = altitudes[i] * 2 / sqrt(3);
                let v1 = {x: centerX - side/2, y: baseY};
                let v2 = {x: centerX + side/2, y: baseY};
                let v3 = {x: centerX, y: baseY - altitudes[i]};

                // Draw triangle
                drawingContext.fillStyle = colors[i];
                drawingContext.beginPath();
                drawingContext.moveTo(v1.x, v1.y);
                drawingContext.lineTo(v2.x, v2.y);
                drawingContext.lineTo(v3.x, v3.y);
                drawingContext.closePath();
                drawingContext.fill();

                // Label blue triangle's top vertex as 'y'
                if (i === 1) {
                    fill(0);
                    noStroke();
                    textSize(14);
                    textAlign(LEFT, BOTTOM);
                    text('y', v3.x + 5, v3.y);
                }

                baseY = v3.y;
            }

            // Draw grayed-out point P marker
            drawPointP(pointP.x, pointP.y, true);
        }

        function drawEquilateralTriangle(center, side, angle, fillColor, drawFromVertex, label) {
            push();
            translate(center.x, center.y);
            rotate(angle);

            let h = side * sqrt(3) / 2;
            let v1, v2, v3;

            if (drawFromVertex) {
                v1 = {x: 0, y: 0};
                v2 = {x: -side/2, y: h};
                v3 = {x: side/2, y: h};
            } else {
                v1 = {x: -side/2, y: h/3};
                v2 = {x: side/2, y: h/3};
                v3 = {x: 0, y: -2*h/3};
            }

            drawingContext.fillStyle = fillColor;
            drawingContext.beginPath();
            drawingContext.moveTo(v1.x, v1.y);
            drawingContext.lineTo(v2.x, v2.y);
            drawingContext.lineTo(v3.x, v3.y);
            drawingContext.closePath();
            drawingContext.fill();

            if (label) {
                fill(0);
                noStroke();
                textSize(14);
                textAlign(LEFT, BOTTOM);
                text(label, v1.x + 5, v1.y);
            }

            pop();
        }

        function drawPointP(x, y, grayed) {
            push();
            if (grayed) {
                fill(200, 200, 200, 128);
                stroke(100, 100, 100, 128);
            } else {
                fill(200);
                stroke(0);
            }
            strokeWeight(1);
            circle(x, y, 20);

            // Draw cross
            strokeWeight(2);
            line(x - 5, y, x + 5, y);
            line(x, y - 5, x, y + 5);

            // Label
            if (!grayed) {
                fill(255, 0, 0);
                noStroke();
                textSize(14);
                textAlign(LEFT, CENTER);
                text('P', x + 12, y);
            }
            pop();
        }

        function getPerpendicularToLine(point, lineStart, lineEnd) {
            let dx = lineEnd.x - lineStart.x;
            let dy = lineEnd.y - lineStart.y;
            let t = ((point.x - lineStart.x) * dx + (point.y - lineStart.y) * dy) / (dx * dx + dy * dy);
            t = constrain(t, 0, 1);

            let foot = {
                x: lineStart.x + t * dx,
                y: lineStart.y + t * dy
            };

            let distance = dist(point.x, point.y, foot.x, foot.y);

            return {foot: foot, distance: distance};
        }

        function constrainPointToTriangle(p) {
            let A = triangleVertices.A;
            let B = triangleVertices.B;
            let C = triangleVertices.C;

            // Barycentric coordinates
            let v0 = {x: C.x - A.x, y: C.y - A.y};
            let v1 = {x: B.x - A.x, y: B.y - A.y};
            let v2 = {x: p.x - A.x, y: p.y - A.y};

            let dot00 = v0.x * v0.x + v0.y * v0.y;
            let dot01 = v0.x * v1.x + v0.y * v1.y;
            let dot02 = v0.x * v2.x + v0.y * v2.y;
            let dot11 = v1.x * v1.x + v1.y * v1.y;
            let dot12 = v1.x * v2.x + v1.y * v2.y;

            let invDenom = 1 / (dot00 * dot11 - dot01 * dot01);
            let u = (dot11 * dot02 - dot01 * dot12) * invDenom;
            let v = (dot00 * dot12 - dot01 * dot02) * invDenom;

            if (u >= 0 && v >= 0 && u + v <= 1) {
                return p;
            }

            // Find closest point on triangle edges
            let closest = p;
            let minDist = Infinity;

            let edges = [
                [A, B],
                [B, C],
                [C, A]
            ];

            for (let edge of edges) {
                let proj = getPerpendicularToLine(p, edge[0], edge[1]);
                let d = dist(p.x, p.y, proj.foot.x, proj.foot.y);
                if (d < minDist) {
                    minDist = d;
                    closest = proj.foot;
                }
            }

            return closest;
        }

        function mousePressed() {
            if (currentStep !== "3") {
                let d = dist(mouseX, mouseY, pointP.x, pointP.y);
                if (d < 15) {
                    isDragging = true;
                }
            }
        }

        function mouseDragged() {
            if (isDragging && currentStep !== "3") {
                pointP.x = mouseX;
                pointP.y = mouseY;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }
    </script>
</body>
