            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const pointLight = new THREE.PointLight(0xffffff, 1, 100);
            pointLight.position.set(0, 0, 0);
            scene.add(pointLight);

            // Create Sun
            const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            scene.add(sun);

            // Create inner planets
            createPlanet('Mercury', 0x999999, 0.1);
            createPlanet('Venus', 0xffd4a3, 0.15);
            createPlanet('Earth', 0x4169e1, 0.15);
            createPlanet('Mars', 0xff4500, 0.12);

            // Create initial dwarf planet
            updateDwarfPlanet();

            // Initial position update
            updatePositions();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function createPlanet(name, color, radius) {
            const geometry = new THREE.SphereGeometry(radius, 32, 32);
            const material = new THREE.MeshLambertMaterial({ color });
            const planet = new THREE.Mesh(geometry, material);
            scene.add(planet);
            planetObjects[name] = planet;

            // Create orbit
            const orbit = createOrbit(orbitalElements[name], 0x0066cc);
            scene.add(orbit);
            orbitLines[name] = orbit;
        }

        function createOrbit(elements, color) {
            const points = calculateOrbitPoints(elements);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color });
            return new THREE.Line(geometry, material);
        }

        function calculateOrbitPoints(elements) {
            const points = [];
            const { a, e, i, omega_cap, omega } = elements;

            for (let angle = 0; angle <= 360; angle += 360/200) {
                const E = angle * Math.PI / 180;
                const r = a * (1 - e * Math.cos(E));
                const nu = 2 * Math.atan2(Math.sqrt(1 + e) * Math.sin(E/2), Math.sqrt(1 - e) * Math.cos(E/2));

                const x_orb = r * Math.cos(nu);
                const y_orb = r * Math.sin(nu);

                const pos = transformToEcliptic(x_orb, y_orb, 0, i, omega_cap, omega);
                points.push(pos);
            }

            return points;
        }

        function transformToEcliptic(x_orb, y_orb, z_orb, i, omega_cap, omega) {
            const i_rad = i * Math.PI / 180;
            const omega_cap_rad = omega_cap * Math.PI / 180;
            const omega_rad = omega * Math.PI / 180;

            const cos_i = Math.cos(i_rad);
            const sin_i = Math.sin(i_rad);
            const cos_omega_cap = Math.cos(omega_cap_rad);
            const sin_omega_cap = Math.sin(omega_cap_rad);
            const cos_omega = Math.cos(omega_rad);
            const sin_omega = Math.sin(omega_rad);

            const x = (cos_omega_cap * cos_omega - sin_omega_cap * sin_omega * cos_i) * x_orb +
                     (-cos_omega_cap * sin_omega - sin_omega_cap * cos_omega * cos_i) * y_orb;

            const y = (sin_omega_cap * cos_omega + cos_omega_cap * sin_omega * cos_i) * x_orb +
                     (-sin_omega_cap * sin_omega + cos_omega_cap * cos_omega * cos_i) * y_orb;

            const z = (sin_omega * sin_i) * x_orb + (cos_omega * sin_i) * y_orb;

            return new THREE.Vector3(x, z, -y);
        }

        function calculatePosition(date, elements) {
            const j2000 = new Date('2000-01-01T12:00:00Z');
            const d = (date - j2000) / (1000 * 60 * 60 * 24);

            const { a, e, i, omega_cap, omega, M0 } = elements;
            const n = 360 / (365.25 * Math.pow(a, 1.5));
            let M = (M0 + n * d) % 360;
            if (M < 0) M += 360;
            M = M * Math.PI / 180;

            // Solve Kepler's equation
            let E = M;
            for (let j = 0; j < 10; j++) {
                E = E - (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
            }

            const nu = 2 * Math.atan2(Math.sqrt(1 + e) * Math.sin(E/2), Math.sqrt(1 - e) * Math.cos(E/2));
            const r = a * (1 - e * Math.cos(E));

            const x_orb = r * Math.cos(nu);
            const y_orb = r * Math.sin(nu);

            return transformToEcliptic(x_orb, y_orb, 0, i, omega_cap, omega);
        }

        function updateDwarfPlanet() {
            // Remove existing dwarf planet and orbit
            if (dwarfPlanetObject) {
                scene.remove(dwarfPlanetObject);
                scene.remove(dwarfPlanetOrbit);
            }

            // Create new dwarf planet
            const geometry = new THREE.SphereGeometry(0.2, 32, 32);
            const material = new THREE.MeshLambertMaterial({ color: 0x9932cc });
            dwarfPlanetObject = new THREE.Mesh(geometry, material);
            scene.add(dwarfPlanetObject);

            // Create orbit
            dwarfPlanetOrbit = createOrbit(orbitalElements[selectedDwarfPlanet], 0xff0000);
            scene.add(dwarfPlanetOrbit);
        }

        function updatePositions() {
            const slider = document.getElementById('time-slider');
            const days = parseInt(slider.value);
            const date = new Date('2000-01-01T00:00:00Z');
            date.setDate(date.getDate() + days);

            // Update date display
            const dateDisplay = document.getElementById('date-display');
            const options = { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric' };
            const dateStr = date.toLocaleDateString('en-US', options);
            const timeStr = date.toTimeString().split(' ')[0];
            dateDisplay.textContent = `${dateStr} ${timeStr}`;

            // Update planet positions
            ['Mercury', 'Venus', 'Earth', 'Mars'].forEach(planet => {
                const pos = calculatePosition(date, orbitalElements[planet]);
                planetObjects[planet].position.copy(pos);
            });

            // Update dwarf planet position
            if (dwarfPlanetObject) {
                const pos = calculatePosition(date, orbitalElements[selectedDwarfPlanet]);
                dwarfPlanetObject.position.copy(pos);
            }
        }

        function animate() {
            if (isAnimating) {
                const slider = document.getElementById('time-slider');
                let value = parseInt(slider.value) + 2;
                if (value > parseInt(slider.max)) {
                    value = parseInt(slider.min);
                }
                slider.value = value;
                updatePositions();
                animationId = requestAnimationFrame(animate);
            }

            controls.update();
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const canvas = document.getElementById('3d-canvas');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        // Event listeners
        document.getElementById('dwarf-planet-selector').addEventListener('click', function(e) {
            if (e.target.tagName === 'BUTTON') {
                // Update active state
                document.querySelectorAll('#dwarf-planet-selector button').forEach(btn => {
                    btn.classList.remove('active');
                });
                e.target.classList.add('active');

                // Update selected dwarf planet
                selectedDwarfPlanet = e.target.getAttribute('data-planet');
                updateDwarfPlanet();
                updatePositions();
            }
        });

        document.getElementById('time-slider').addEventListener('input', function() {
            updatePositions();
        });

        document.getElementById('play-pause-button').addEventListener('click', function() {
            isAnimating = !isAnimating;
            this.textContent = isAnimating ? '⏸' : '▶';

            if (isAnimating) {
                animate();
            } else if (animationId) {
                cancelAnimationFrame(animationId);
            }
        });

        // Initialize everything
        initScene();

        // Start render loop
        function render() {
            if (!isAnimating) {
                controls.update();
                renderer.render(scene, camera);
            }
            requestAnimationFrame(render);
        }
        render();
    </script>
</body>
</html>
