    </style>
</head>
<body>

    <div id="main-container">
        <!-- Left Panel: Controls -->
        <div id="control-panel">
            <label for="select-cfc">cfc plot</label>
            <select id="select-cfc">
                <option value="CFC-12">CFC-12</option>
                <option value="CFC-11">CFC-11</option>
                <option value="CFC-113">CFC-113</option>
                <option value="CCl4">CCl4</option>
            </select>

            <label for="checkbox-show-all">show all</label>
            <input type="checkbox" id="checkbox-show-all">

            <label for="slider-year">year</label>
            <input type="range" id="slider-year" min="1940" max="2020" step="1" value="1950">
            <span id="label-year">1950</span>

            <label for="slider-time">time (s)</label>
            <input type="range" id="slider-time" min="0" max="30" step="1" value="1">
            <span id="label-time">1</span>
        </div>

        <!-- Right Panel: Visualizations -->
        <div id="visualization-panel">
            <div id="plots-container">
                <div id="plot-ozone" class="plot"></div>
                <div id="plot-cfc" class="plot"></div>
            </div>
            <div id="scene-container"></div>
        </div>
    </div>

    <script>
        // CFC Data
        const cfcData = {
            'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
            'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
            'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
            'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
        };

        // Get controls
        const selectCFC = document.getElementById('select-cfc');
        const checkboxShowAll = document.getElementById('checkbox-show-all');
        const sliderYear = document.getElementById('slider-year');
        const labelYear = document.getElementById('label-year');
        const sliderTime = document.getElementById('slider-time');
        const labelTime = document.getElementById('label-time');

        // Three.js setup
        let scene, camera, renderer;
        let earth, ozoneLayer, atmosphere, sun;

        function initThreeJS() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(75, document.getElementById('scene-container').clientWidth / document.getElementById('scene-container').clientHeight, 0.1, 1000);
            camera.position.set(5, 2, 5);
            camera.lookAt(0, 0, 0);

            renderer = new THREE.WebGLRenderer();
            renderer.setSize(document.getElementById('scene-container').clientWidth, document.getElementById('scene-container').clientHeight);
            document.getElementById('scene-container').appendChild(renderer.domElement);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(1, 32, 32);
            const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
            earth = new THREE.Mesh(earthGeometry, earthMaterial);
            scene.add(earth);

            // Ozone Layer
            const ozoneGeometry = new THREE.SphereGeometry(1.3, 32, 32);
            const ozoneMaterial = new THREE.MeshBasicMaterial({ color: 0x00ff00, transparent: true, opacity: 0.5 });
            ozoneLayer = new THREE.Mesh(ozoneGeometry, ozoneMaterial);
            scene.add(ozoneLayer);

            // Atmosphere
            const atmosphereGeometry = new THREE.SphereGeometry(1.8, 32, 32);
            const atmosphereMaterial = new THREE.MeshBasicMaterial({ color: 0xffaa88, transparent: true, opacity: 0.1 });
            atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
            scene.add(atmosphere);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.3, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            sun = new THREE.Mesh(sunGeometry, sunMaterial);
            sun.position.set(-4, 1, 0);
            scene.add(sun);

            // Radiation arrows
            const arrowOrigin = new THREE.Vector3(-3.5, 1, 0);
            const arrowColors = 0xffa500;

            for (let i = 0; i < 5; i++) {
                const angle = (i - 2) * 0.3;
                const direction = new THREE.Vector3(1, -0.3 + angle * 0.2, angle * 0.5).normalize();
                const arrow = new THREE.ArrowHelper(direction, arrowOrigin, 3, arrowColors, 0.5, 0.3);
                scene.add(arrow);
            }

            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        // Interpolate CFC concentration for a given year
        function interpolateCFC(cfcType, year) {
            const data = cfcData[cfcType].data;
            for (let i = 0; i < data.length - 1; i++) {
                if (year >= data[i][0] && year <= data[i + 1][0]) {
                    const x1 = data[i][0], y1 = data[i][1];
                    const x2 = data[i + 1][0], y2 = data[i + 1][1];
                    return y1 + (y2 - y1) * (year - x1) / (x2 - x1);
                }
            }
            return data[data.length - 1][1];
        }

        // Update function
        function update() {
            const selectedCFC = selectCFC.value;
            const showAll = checkboxShowAll.checked;
            const year = parseInt(sliderYear.value);
            const time = parseInt(sliderTime.value);

            labelYear.textContent = year;
            labelTime.textContent = time;

            // Update CFC plot
            const traces = [];

            if (showAll) {
                for (const [cfcType, cfcInfo] of Object.entries(cfcData)) {
                    const trace = {
                        x: cfcInfo.data.map(d => d[0]),
                        y: cfcInfo.data.map(d => d[1]),
                        type: 'scatter',
                        mode: 'lines',
                        name: cfcType,
                        line: { color: cfcInfo.color, width: cfcType === selectedCFC ? 3 : 1 }
                    };
                    traces.push(trace);
                }
            } else {
                const cfcInfo = cfcData[selectedCFC];
                const trace = {
                    x: cfcInfo.data.map(d => d[0]),
                    y: cfcInfo.data.map(d => d[1]),
                    type: 'scatter',
                    mode: 'lines',
                    name: selectedCFC,
                    line: { color: cfcInfo.color }
                };
                traces.push(trace);
            }

            // Add marker for current year
            const currentConc = interpolateCFC(selectedCFC, year);
            traces.push({
                x: [year],
                y: [currentConc],
                type: 'scatter',
                mode: 'markers',
                marker: { color: 'red', size: 10 },
                showlegend: false
            });

            const cfcLayout = {
                title: 'CFC concentration',
                xaxis: { title: 'year', range: [1920, 2020] },
                yaxis: { range: [0, 600] },
                margin: { t: 40, b: 40, l: 40, r: 40 }
            };

            Plotly.newPlot('plot-cfc', traces, cfcLayout);

            // Update ozone plot
            const k = currentConc * 0.001; // Pseudo rate constant
            const timePoints = [];
            const ozoneInverse = [];

            for (let t = 0; t <= time; t += 0.1) {
                timePoints.push(t);
                ozoneInverse.push(10 + k * t); // 1/[O3](t) = 1/[O3](0) + k*[CFC]*t
            }

            const ozoneTrace = {
                x: timePoints,
                y: ozoneInverse,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue' }
            };

            const ozoneLayout = {
                title: 'concentration of ozone',
                xaxis: { title: 'time (s)', range: [0, 30] },
                yaxis: { title: '1/[O<sub>3</sub>]', range: [0, 110] },
                margin: { t: 40, b: 40, l: 60, r: 40 }
            };

            Plotly.newPlot('plot-ozone', [ozoneTrace], ozoneLayout);

            // Update 3D scene ozone layer opacity
            if (ozoneLayer) {
                const maxConc = 600;
                const opacity = Math.max(0.1, 0.8 - (currentConc / maxConc) * 0.7);
                ozoneLayer.material.opacity = opacity;
            }
        }

        // Event listeners
        selectCFC.addEventListener('change', update);
        checkboxShowAll.addEventListener('change', update);
        sliderYear.addEventListener('input', update);
        sliderTime.addEventListener('input', update);

        // Initialize
        window.addEventListener('load', () => {
            initThreeJS();
            update();
        });

        // Handle resize
        window.addEventListener('resize', () => {
            if (camera && renderer) {
                camera.aspect = document.getElementById('scene-container').clientWidth / document.getElementById('scene-container').clientHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(document.getElementById('scene-container').clientWidth, document.getElementById('scene-container').clientHeight);
            }
            update();
        });
    </script>

</body>
</html>
