    <div id="app-container">
        <div id="control-panel">
            <button id="btn-reset">+</button>
            <div>
                <label for="slider-moon-pos">position of moon</label>
                <input type="range" id="slider-moon-pos" min="0" max="360" value="144" step="1">
            </div>
            <div>
                <label for="slider-moon-pull">pull from moon</label>
                <input type="range" id="slider-moon-pull" min="0" max="100" value="40" step="1">
            </div>
            <div>
                <label for="slider-sun-pos">position of sun</label>
                <input type="range" id="slider-sun-pos" min="0" max="360" value="144" step="1">
            </div>
            <div>
                <label for="slider-sun-pull">pull from sun</label>
                <input type="range" id="slider-sun-pull" min="0" max="100" value="20" step="1">
            </div>
        </div>
        <div id="viz-container"></div>
    </div>

    <script>
        // Get references to UI elements
        const moonPosSlider = document.getElementById('slider-moon-pos');
        const moonPullSlider = document.getElementById('slider-moon-pull');
        const sunPosSlider = document.getElementById('slider-sun-pos');
        const sunPullSlider = document.getElementById('slider-sun-pull');
        const resetButton = document.getElementById('btn-reset');

        // Constants for visualization
        const canvasSize = 500;
        const earthRadius = 180;
        const baseWaterRadius = 200;
        const scalingFactor = 0.5;

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('viz-container');
            noLoop();
            redraw();
        }

        function draw() {
            // Clear the canvas
            background(255);

            // Get slider values
            const moonAngle = parseFloat(moonPosSlider.value);
            const moonPull = parseFloat(moonPullSlider.value);
            const sunAngle = parseFloat(sunPosSlider.value);
            const sunPull = parseFloat(sunPullSlider.value);

            // Convert angles to radians
            const moonAngleRad = moonAngle * Math.PI / 180;
            const sunAngleRad = sunAngle * Math.PI / 180;

            // Center of the canvas
            const centerX = canvasSize / 2;
            const centerY = canvasSize / 2;

            // Draw water layer
            fill('#aed6f1');
            stroke('#000000');
            strokeWeight(1);

            beginShape();
            for (let theta = 0; theta <= 360; theta += 1) {
                const thetaRad = theta * Math.PI / 180;

                // Calculate tidal effects
                const moonEffect = moonPull * Math.pow(Math.cos(thetaRad - moonAngleRad), 2);
                const sunEffect = sunPull * Math.pow(Math.cos(thetaRad - sunAngleRad), 2);
                const totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

                // Convert polar to Cartesian coordinates
                const x = centerX + totalRadius * Math.cos(thetaRad);
                const y = centerY + totalRadius * Math.sin(thetaRad);

                vertex(x, y);
            }
            endShape(CLOSE);

            // Draw Earth on top
            noStroke();
            fill('#966F33');
            ellipse(centerX, centerY, earthRadius * 2, earthRadius * 2);
        }

        // Event listeners for sliders
        moonPosSlider.addEventListener('input', () => redraw());
        moonPullSlider.addEventListener('input', () => redraw());
        sunPosSlider.addEventListener('input', () => redraw());
        sunPullSlider.addEventListener('input', () => redraw());

        // Reset button functionality
        resetButton.addEventListener('click', () => {
            moonPosSlider.value = 144;
            moonPullSlider.value = 40;
            sunPosSlider.value = 144;
            sunPullSlider.value = 20;
            redraw();
        });
    </script>
</body>
</html>
