        #orbit-view-wrapper p {
            height: 30px;
            line-height: 30px;
            text-align: center;
            font-size: 14px;
            color: #666;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <div class="control-group">
                <label>Earth position</label>
                <input type="range" id="slider-earth-position" min="0" max="360" value="180" step="1">
            </div>
            <div class="control-group">
                <label>comet position</label>
                <input type="range" id="slider-comet-position" min="0" max="360" value="180" step="1">
            </div>
            <div class="control-group">
                <label>coma</label>
                <input type="range" id="slider-coma" min="0" max="100" value="20" step="1">
            </div>
            <div class="control-group">
                <label>tail</label>
                <input type="range" id="slider-tail" min="0" max="100" value="50" step="1">
            </div>
            <div class="control-group">
                <label>tail spread</label>
                <input type="range" id="slider-tail-spread" min="0" max="100" value="20" step="1">
            </div>
        </div>
        <div id="visualization-panel">
            <div id="telescopic-view-wrapper">
                <canvas id="telescopic-canvas"></canvas>
                <p>telescopic view</p>
            </div>
            <div id="orbit-view-wrapper">
                <canvas id="orbit-canvas"></canvas>
                <p>orbit diagram</p>
            </div>
        </div>
