    <script>
        // Initialize Three.js scenes
        let orbitScene, orbitCamera, orbitRenderer;
        let telescopeScene, telescopeCamera, telescopeRenderer;
        let earthMesh, cometGroup, nucleus, coma, tail;

        // Orbital parameters
        const orbitalParams = {
            earth: { a: 4, e: 0.0167, color: 0x0066ff },
            mars: { a: 6.1, e: 0.0934, color: 0xff0000 },
            jupiter: { a: 20.8, e: 0.0489, color: 0xffa500 },
            comet: { a: 14.4, e: 0.432, color: 0xffff00 }
        };

        function init() {
            // Setup orbit diagram
            orbitScene = new THREE.Scene();
            orbitScene.background = new THREE.Color(0xffffff);

            const orbitCanvas = document.getElementById('orbit-canvas');
            orbitRenderer = new THREE.WebGLRenderer({ canvas: orbitCanvas, antialias: true });

            orbitCamera = new THREE.PerspectiveCamera(50, 1, 0.1, 1000);
            orbitCamera.position.set(0, 20, 20);
            orbitCamera.lookAt(0, 0, 0);

            // Lighting for orbit view
            const orbitAmbient = new THREE.AmbientLight(0xffffff, 0.3);
            orbitScene.add(orbitAmbient);

            const sunLight = new THREE.PointLight(0xffffff, 1);
            sunLight.position.set(0, 0, 0);
            orbitScene.add(sunLight);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.3, 16, 16);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00, emissive: 0xffff00 });
            const sunMesh = new THREE.Mesh(sunGeometry, sunMaterial);
            orbitScene.add(sunMesh);

            // Create orbits
            createOrbit(orbitalParams.earth, orbitScene);
            createOrbit(orbitalParams.mars, orbitScene);
            createOrbit(orbitalParams.jupiter, orbitScene);
            createOrbit(orbitalParams.comet, orbitScene);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(0.15, 16, 16);
            const earthMaterial = new THREE.MeshPhongMaterial({ color: orbitalParams.earth.color });
            earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
            orbitScene.add(earthMesh);

            // Comet group
            cometGroup = new THREE.Group();

            // Nucleus
            const nucleusGeometry = new THREE.SphereGeometry(0.05, 16, 16);
            const nucleusMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
            nucleus = new THREE.Mesh(nucleusGeometry, nucleusMaterial);
            cometGroup.add(nucleus);

            // Coma
            const comaGeometry = new THREE.SphereGeometry(0.5, 16, 16);
            const comaMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x99ff99, 
                transparent: true, 
                opacity: 0.5,
                blending: THREE.AdditiveBlending
            });
            coma = new THREE.Mesh(comaGeometry, comaMaterial);
            cometGroup.add(coma);

            // Tail
            const tailGeometry = new THREE.ConeGeometry(0.3, 2, 8);
            const tailMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x99ff99, 
                transparent: true, 
                opacity: 0.4,
                side: THREE.DoubleSide
            });
            tail = new THREE.Mesh(tailGeometry, tailMaterial);
            tail.position.y = -1; // Position at the base of the cone
            cometGroup.add(tail);

            orbitScene.add(cometGroup);

            // Setup telescopic view
            telescopeScene = new THREE.Scene();
            telescopeScene.background = new THREE.Color(0x000000);

            const telescopeCanvas = document.getElementById('telescopic-canvas');
            telescopeRenderer = new THREE.WebGLRenderer({ canvas: telescopeCanvas, antialias: true });

            telescopeCamera = new THREE.PerspectiveCamera(15, 1, 0.1, 1000);

            const telescopeAmbient = new THREE.AmbientLight(0xffffff, 0.5);
            telescopeScene.add(telescopeAmbient);

            // Add the same comet group to telescope scene
            telescopeScene.add(cometGroup.clone());

            // Add event listeners
            document.getElementById('slider-earth-position').addEventListener('input', update);
            document.getElementById('slider-comet-position').addEventListener('input', update);
            document.getElementById('slider-coma').addEventListener('input', update);
            document.getElementById('slider-tail').addEventListener('input', update);
            document.getElementById('slider-tail-spread').addEventListener('input', update);

            window.addEventListener('resize', onWindowResize);
            onWindowResize();
            update();
            animate();
        }

        function createOrbit(params, scene) {
            const curve = new THREE.EllipseCurve(
                -params.a * params.e, 0,  // Center offset
                params.a * (1 - params.e), params.a * Math.sqrt(1 - params.e * params.e),  // radii
                0, 2 * Math.PI,  // angles
                false,
                0
            );

            const points = curve.getPoints(100);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: params.color });
            const ellipse = new THREE.Line(geometry, material);
            ellipse.rotation.x = -Math.PI / 2;
            scene.add(ellipse);
        }

        function getEllipticalPosition(angle, params) {
            const rad = angle * Math.PI / 180;
            const r = params.a * (1 - params.e * params.e) / (1 + params.e * Math.cos(rad));
            const x = r * Math.cos(rad) - params.a * params.e;
            const z = r * Math.sin(rad);
            return { x, y: 0, z };
        }

        function update() {
            const earthPos = parseFloat(document.getElementById('slider-earth-position').value);
            const cometPos = parseFloat(document.getElementById('slider-comet-position').value);
            const comaSize = parseFloat(document.getElementById('slider-coma').value);
            const tailSize = parseFloat(document.getElementById('slider-tail').value);
            const tailSpread = parseFloat(document.getElementById('slider-tail-spread').value);

            // Update Earth position
            const earthPosition = getEllipticalPosition(earthPos, orbitalParams.earth);
            earthMesh.position.set(earthPosition.x, earthPosition.y, earthPosition.z);

            // Update comet position
            const cometPosition = getEllipticalPosition(cometPos, orbitalParams.comet);
            cometGroup.position.set(cometPosition.x, cometPosition.y, cometPosition.z);

            // Update coma size
            const comaScale = 0.1 + (comaSize / 100) * 2;
            coma.scale.set(comaScale, comaScale, comaScale);

            // Update tail size and spread
            const tailLength = 0.1 + (tailSize / 100) * 4;
            const tailWidth = 0.1 + (tailSpread / 100) * 1;
            tail.scale.set(tailWidth, tailLength, tailWidth);

            // Orient tail away from sun
            const dirToSun = new THREE.Vector3(-cometPosition.x, 0, -cometPosition.z).normalize();
            const tailDirection = dirToSun.clone().multiplyScalar(-1);
            tail.lookAt(tailDirection);
            tail.rotateX(Math.PI / 2);
            tail.position.copy(tailDirection.multiplyScalar(tailLength / 2));

            // Update telescope camera position and target
            telescopeCamera.position.set(earthPosition.x, earthPosition.y, earthPosition.z);
            telescopeCamera.lookAt(cometPosition.x, cometPosition.y, cometPosition.z);

            // Update telescope scene comet
            const telescopeComet = telescopeScene.children.find(child => child.type === 'Group');
            if (telescopeComet) {
                telescopeComet.position.set(cometPosition.x, cometPosition.y, cometPosition.z);
                telescopeComet.children[1].scale.set(comaScale, comaScale, comaScale);
                telescopeComet.children[2].scale.set(tailWidth, tailLength, tailWidth);
                telescopeComet.children[2].lookAt(tailDirection);
                telescopeComet.children[2].rotateX(Math.PI / 2);
                telescopeComet.children[2].position.copy(tailDirection.multiplyScalar(tailLength / 2));
            }
        }

        function onWindowResize() {
            const orbitCanvas = document.getElementById('orbit-canvas');
            const telescopeCanvas = document.getElementById('telescopic-canvas');

            const width = orbitCanvas.clientWidth;
            const height = orbitCanvas.clientHeight;

            orbitCamera.aspect = width / height;
            orbitCamera.updateProjectionMatrix();
            orbitRenderer.setSize(width, height);

            telescopeCamera.aspect = width / height;
            telescopeCamera.updateProjectionMatrix();
            telescopeRenderer.setSize(width, height);
        }

        function animate() {
            requestAnimationFrame(animate);
            orbitRenderer.render(orbitScene, orbitCamera);
            telescopeRenderer.render(telescopeScene, telescopeCamera);
        }

        init();
    </script>
</body>
</html>
