<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-m">m</label>
                <input type="range" id="slider-m" min="0" max="10" step="0.001" value="2.845">
                <span id="label-m">2.845</span>
            </div>
            <div class="slider-group">
                <label for="slider-x">x</label>
                <input type="range" id="slider-x" min="0" max="10" step="0.1" value="4.1">
                <span id="label-x">4.1</span>
            </div>
            <div class="slider-group">
                <label for="slider-y">y</label>
                <input type="range" id="slider-y" min="0.1" max="5" step="0.001" value="1.498">
                <span id="label-y">1.498</span>
            </div>
        </div>
        <div id="visualization"></div>
    </div>

    <script>
        // Slider event listeners
        document.getElementById('slider-m').addEventListener('input', function(e) {
            document.getElementById('label-m').textContent = e.target.value;
        });

        document.getElementById('slider-x').addEventListener('input', function(e) {
            document.getElementById('label-x').textContent = e.target.value;
        });

        document.getElementById('slider-y').addEventListener('input', function(e) {
            document.getElementById('label-y').textContent = e.target.value;
        });

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(800, 500);
            canvas.parent('visualization');
            textAlign(CENTER, CENTER);
        }

        function draw() {
            background(255);

            // Get slider values
            let m = parseFloat(document.getElementById('slider-m').value);
            let x = parseFloat(document.getElementById('slider-x').value);
            let y = parseFloat(document.getElementById('slider-y').value);

            // Constants
            const L = 10;
            const g = 9.81;

            // Calculate angles
            let theta1 = Math.atan(y / x);
            let theta2 = Math.atan(y / (L - x));

            // Calculate forces
            let Fg = m * g;
            let T1 = (Fg * Math.cos(theta2)) / Math.sin(theta1 + theta2);
            let T2 = (Fg * Math.cos(theta1)) / Math.sin(theta1 + theta2);

            // Canvas coordinates
            const leftAnchorX = 100;
            const rightAnchorX = 700;
            const anchorY = 150;
            const pixelsPerUnit = (rightAnchorX - leftAnchorX) / L;
            const yScale = 50;

            // Mass position in pixels
            let massX = leftAnchorX + x * pixelsPerUnit;
            let massY = anchorY + y * yScale;

            // Draw posts
            strokeWeight(8);
            stroke(0);
            line(leftAnchorX, anchorY - 30, leftAnchorX, anchorY + 100);
            line(rightAnchorX, anchorY - 30, rightAnchorX, anchorY + 100);

            // Draw horizontal reference line (dashed)
            strokeWeight(1);
            stroke(150);
            drawDashedLine(leftAnchorX, anchorY, rightAnchorX, anchorY);

            // Draw ropes
            strokeWeight(3);
            stroke(0);
            line(leftAnchorX, anchorY, massX, massY);
            line(rightAnchorX, anchorY, massX, massY);

            // Draw mass
            fill(0);
            noStroke();
            ellipse(massX, massY, 40, 40);

            // Draw angles
            noFill();
            stroke(100);
            strokeWeight(2);
            arc(leftAnchorX, anchorY, 80, 80, 0, theta1);
            arc(rightAnchorX, anchorY, Math.PI - theta2, Math.PI);

            // Draw labels
            fill(0);
            noStroke();
            textSize(16);

            // Angle labels
            text('θ₁', leftAnchorX + 50, anchorY + 20);
            text('θ₂', rightAnchorX - 50, anchorY + 20);

            // Tension labels
            text('T₁', (leftAnchorX + massX) / 2 - 20, (anchorY + massY) / 2 - 20);
            text('T₂', (rightAnchorX + massX) / 2 + 20, (anchorY + massY) / 2 - 20);

            // Display tension values at top
            textSize(20);
            text('T₁ = ' + T1.toFixed(4) + ' N | T₂ = ' + T2.toFixed(4) + ' N', width / 2, 50);
        }

        function drawDashedLine(x1, y1, x2, y2) {
            let dashLength = 10;
            let gapLength = 5;
            let distance = dist(x1, y1, x2, y2);
            let steps = distance / (dashLength + gapLength);

            for (let i = 0; i < steps; i++) {
                let startRatio = i * (dashLength + gapLength) / distance;
                let endRatio = (i * (dashLength + gapLength) + dashLength) / distance;

                if (endRatio > 1) endRatio = 1;

                let x1Dash = lerp(x1, x2, startRatio);
                let y1Dash = lerp(y1, y2, startRatio);
                let x2Dash = lerp(x1, x2, endRatio);
                let y2Dash = lerp(y1, y2, endRatio);

                line(x1Dash, y1Dash, x2Dash, y2Dash);
            }
        }
    </script>
</body>
</html>
