            <div class="control-row">
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|◀</button>
            </div>
        </div>

        <div id="visuals-container">
            <div id="scene-container"></div>
            <div id="plot-displacement"></div>
            <div id="plot-velocity"></div>
        </div>
    </div>

    <script>
        // State variables
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;
        let lastTimestamp = 0;

        // System parameters
        let x0 = 0.1;
        let v0 = 1.0;
        let k = 1.0;
        let m = 1.0;
        let c = 0.1;
        let Kp = 0.1;
        let Kd = 0.1;

        // Solution arrays
        let timeArray = [];
        let displacementArray = [];
        let velocityArray = [];

        // Three.js setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(45, 1, 0.1, 1000);
        camera.position.set(10, 10, 10);
        camera.lookAt(0, 0, 0);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        const sceneContainer = document.getElementById('scene-container');
        renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);
        sceneContainer.appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight.position.set(5, 10, 5);
        scene.add(directionalLight);

        // Base
        const baseGeometry = new THREE.BoxGeometry(15, 0.5, 5);
        const baseMaterial = new THREE.MeshStandardMaterial({ color: 0x4CAF50 });
        const base = new THREE.Mesh(baseGeometry, baseMaterial);
        base.position.y = -0.25;
        scene.add(base);

        // Mass
        const massGeometry = new THREE.BoxGeometry(1, 1, 1);
        const massMaterial = new THREE.MeshStandardMaterial({ color: 0xFFD700 });
        const mass = new THREE.Mesh(massGeometry, massMaterial);
        mass.position.y = 0.5;
        scene.add(mass);

        // Spring
        const springCurve = new THREE.CatmullRomCurve3([]);
        const numCoils = 10;
        const coilRadius = 0.3;
        for (let i = 0; i <= numCoils * 10; i++) {
            const t = i / (numCoils * 10);
            const angle = t * numCoils * Math.PI * 2;
            springCurve.points.push(new THREE.Vector3(
                -3 + t * 3,
                0.5 + Math.sin(angle) * coilRadius,
                Math.cos(angle) * coilRadius
            ));
        }
        const springGeometry = new THREE.TubeGeometry(springCurve, 100, 0.05, 8, false);
        const springMaterial = new THREE.MeshStandardMaterial({ color: 0xFF0000 });
        const spring = new THREE.Mesh(springGeometry, springMaterial);
        scene.add(spring);

        // Damper
        const damperHousingGeometry = new THREE.CylinderGeometry(0.3, 0.3, 2, 32);
        const damperPistonGeometry = new THREE.CylinderGeometry(0.15, 0.15, 2.5, 32);
        const damperMaterial = new THREE.MeshStandardMaterial({ color: 0x0000FF });
        const damperHousing = new THREE.Mesh(damperHousingGeometry, damperMaterial);
        const damperPiston = new THREE.Mesh(damperPistonGeometry, damperMaterial);
        damperHousing.rotation.z = Math.PI / 2;
        damperPiston.rotation.z = Math.PI / 2;
        damperHousing.position.set(-2, 0.5, 1.5);
        damperPiston.position.set(-1, 0.5, 1.5);
        scene.add(damperHousing);
        scene.add(damperPiston);

        // Wireframe enclosure
        const enclosureGeometry = new THREE.BoxGeometry(12, 5, 6);
        const enclosureMaterial = new THREE.MeshBasicMaterial({ 
            color: 0x333333, 
            wireframe: true,
            transparent: true,
            opacity: 0.3
        });
        const enclosure = new THREE.Mesh(enclosureGeometry, enclosureMaterial);
        enclosure.position.y = 2.5;
        scene.add(enclosure);

        // Initialize plots
        const plotLayout = {
            margin: { t: 30, r: 30, b: 50, l: 50 },
            xaxis: { title: 'Time (s)', range: [0, 10] },
            yaxis: { range: [-3, 3] },
            showlegend: false
        };

        Plotly.newPlot('plot-displacement', [], {
            ...plotLayout,
            yaxis: { ...plotLayout.yaxis, title: 'Displacement (m)' }
        });

        Plotly.newPlot('plot-velocity', [], {
            ...plotLayout,
            yaxis: { ...plotLayout.yaxis, title: 'Velocity (m/s)' }
        });

        // Solve differential equation
        function solveDifferentialEquation() {
            const k_eff = k + Kp;
            const c_eff = c + Kd;
            const omega_n = Math.sqrt(k_eff / m);
            const zeta = c_eff / (2 * Math.sqrt(m * k_eff));

            timeArray = [];
            displacementArray = [];
            velocityArray = [];

            for (let t = 0; t <= 10; t += 0.05) {
                timeArray.push(t);

                let x, v;
                if (zeta < 1) { // Underdamped
                    const omega_d = omega_n * Math.sqrt(1 - zeta * zeta);
                    const A = x0;
                    const B = (v0 + zeta * omega_n * x0) / omega_d;
                    x = Math.exp(-zeta * omega_n * t) * (A * Math.cos(omega_d * t) + B * Math.sin(omega_d * t));
                    v = Math.exp(-zeta * omega_n * t) * (
                        -zeta * omega_n * (A * Math.cos(omega_d * t) + B * Math.sin(omega_d * t)) +
                        omega_d * (-A * Math.sin(omega_d * t) + B * Math.cos(omega_d * t))
                    );
                } else if (zeta === 1) { // Critically damped
                    const A = x0;
                    const B = v0 + omega_n * x0;
                    x = Math.exp(-omega_n * t) * (A + B * t);
                    v = Math.exp(-omega_n * t) * (B - omega_n * (A + B * t));
                } else { // Overdamped
                    const r1 = -omega_n * (zeta - Math.sqrt(zeta * zeta - 1));
                    const r2 = -omega_n * (zeta + Math.sqrt(zeta * zeta - 1));
                    const A = (v0 - r2 * x0) / (r1 - r2);
                    const B = (v0 - r1 * x0) / (r2 - r1);
                    x = A * Math.exp(r1 * t) + B * Math.exp(r2 * t);
                    v = A * r1 * Math.exp(r1 * t) + B * r2 * Math.exp(r2 * t);
                }

                displacementArray.push(x);
                velocityArray.push(v);
            }
        }

        // Update plots
        function updatePlots() {
            const displacementData = [
                {
                    x: timeArray,
                    y: displacementArray,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue' }
                },
                {
                    x: [0],
                    y: [x0],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: 'blue', size: 10 }
                }
            ];

            const velocityData = [
                {
                    x: timeArray,
                    y: velocityArray,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green' }
                },
                {
                    x: [0],
                    y: [v0],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: 'green', size: 10 }
                }
            ];

            Plotly.react('plot-displacement', displacementData);
            Plotly.react('plot-velocity', velocityData);
        }

        // Update 3D scene
        function update3DScene(displacement) {
            mass.position.x = displacement;
            damperPiston.position.x = -1 + displacement;

            // Update spring
            springCurve.points = [];
            for (let i = 0; i <= numCoils * 10; i++) {
                const t = i / (numCoils * 10);
                const angle = t * numCoils * Math.PI * 2;
                springCurve.points.push(new THREE.Vector3(
                    -3 + t * (3 + displacement),
                    0.5 + Math.sin(angle) * coilRadius,
                    Math.cos(angle) * coilRadius
                ));
            }
            spring.geometry.dispose();
            spring.geometry = new THREE.TubeGeometry(springCurve, 100, 0.05, 8, false);
        }

        // Get current values at time t
        function getValuesAtTime(t) {
            const index = Math.min(Math.floor(t / 0.05), displacementArray.length - 1);
            return {
                displacement: displacementArray[index],
                velocity: velocityArray[index]
            };
        }

        // Animation loop
        function animate(timestamp) {
            if (!isRunning) return;

            if (lastTimestamp === 0) lastTimestamp = timestamp;
            const deltaTime = (timestamp - lastTimestamp) / 1000;
            lastTimestamp = timestamp;

            time += deltaTime;

            if (time > 10) {
                time = 10;
                isRunning = false;
                lastTimestamp = 0;
            }

            const values = getValuesAtTime(time);
            update3DScene(values.displacement);

            // Update plot markers
            Plotly.restyle('plot-displacement', {
                x: [[time]],
                y: [[values.displacement]]
            }, [1]);

            Plotly.restyle('plot-velocity', {
                x: [[time]],
                y: [[values.velocity]]
            }, [1]);

            renderer.render(scene, camera);

            if (isRunning) {
                animationFrameId = requestAnimationFrame(animate);
            }
        }

        // Slider event handlers
        function handleSliderChange() {
            // Pause and reset
            isRunning = false;
            time = 0;
            lastTimestamp = 0;
            if (animationFrameId) {
                cancelAnimationFrame(animationFrameId);
            }

            // Update parameters
            x0 = parseFloat(document.getElementById('slider-displacement').value);
            v0 = parseFloat(document.getElementById('slider-velocity').value);
            k = parseFloat(document.getElementById('slider-stiffness').value);
            m = parseFloat(document.getElementById('slider-mass').value);
            c = parseFloat(document.getElementById('slider-damping').value);
            Kp = parseFloat(document.getElementById('slider-proportional-gain').value);
            Kd = parseFloat(document.getElementById('slider-derivative-gain').value);

            // Update display values
            document.getElementById('value-displacement').textContent = x0.toFixed(1);
            document.getElementById('value-velocity').textContent = v0.toFixed(1);
            document.getElementById('value-stiffness').textContent = k.toFixed(2);
            document.getElementById('value-mass').textContent = m.toFixed(2);
            document.getElementById('value-damping').textContent = c.toFixed(1);
            document.getElementById('value-proportional-gain').textContent = Kp.toFixed(1);
            document.getElementById('value-derivative-gain').textContent = Kd.toFixed(1);

            // Recalculate and update
            solveDifferentialEquation();
            updatePlots();
            update3DScene(x0);
            renderer.render(scene, camera);
        }

        // Add event listeners to sliders
        document.getElementById('slider-displacement').addEventListener('input', handleSliderChange);
        document.getElementById('slider-velocity').addEventListener('input', handleSliderChange);
        document.getElementById('slider-stiffness').addEventListener('input', handleSliderChange);
        document.getElementById('slider-mass').addEventListener('input', handleSliderChange);
        document.getElementById('slider-damping').addEventListener('input', handleSliderChange);
        document.getElementById('slider-proportional-gain').addEventListener('input', handleSliderChange);
        document.getElementById('slider-derivative-gain').addEventListener('input', handleSliderChange);

        // Button event handlers
        document.getElementById('btn-play').addEventListener('click', () => {
            if (!isRunning && time < 10) {
                isRunning = true;
                animate(performance.now());
            }
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            isRunning = false;
            lastTimestamp = 0;
            if (animationFrameId) {
                cancelAnimationFrame(animationFrameId);
            }
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            isRunning = false;
            time = 0;
            lastTimestamp = 0;
            if (animationFrameId) {
                cancelAnimationFrame(animationFrameId);
            }

            const values = getValuesAtTime(0);
            update3DScene(values.displacement);

            Plotly 
('plot-displacement', {
                x: [[0]],
                y: [[values.displacement]]
            }, [1]);

            Plotly.restyle('plot-velocity', {
                x: [[0]],
                y: [[values.velocity]]
            }, [1]);

            renderer.render(scene, camera);
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            const width = sceneContainer.clientWidth;
            const height = sceneContainer.clientHeight;
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
            renderer.render(scene, camera);

            Plotly.Plots.resize('plot-displacement');
            Plotly.Plots.resize('plot-velocity');
        });

        // Initial setup
        solveDifferentialEquation();
        updatePlots();
        update3DScene(x0);
        renderer.render(scene, camera);
    </script>
</body>
</html>
