<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>tension T</h4>
            <input type="range" id="slider-tension" min="1" max="100" value="50" step="1">

            <h4>displacement</h4>
            <label for="slider-x1">x₁</label>
            <input type="range" id="slider-x1" min="-0.5" max="0.5" value="0.1" step="0.01">
            <label for="slider-x2">x₂</label>
            <input type="range" id="slider-x2" min="-0.5" max="0.5" value="0.1" step="0.01">
            <label for="slider-x3">x₃</label>
            <input type="range" id="slider-x3" min="-0.5" max="0.5" value="0.0" step="0.01">

            <h4>mass</h4>
            <label for="slider-m1">m₁</label>
            <input type="range" id="slider-m1" min="0.1" max="2.0" value="1.0" step="0.05">
            <label for="slider-m2">m₂</label>
            <input type="range" id="slider-m2" min="0.1" max="2.0" value="1.0" step="0.05">
            <label for="slider-m3">m₃</label>
            <input type="range" id="slider-m3" min="0.1" max="2.0" value="1.0" step="0.05">

            <h4>release system</h4>
            <div id="animation-controls">
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">⏮</button>
            </div>
        </div>

        <div id="visualization-area">
            <div id="main-animation-container"></div>
            <div id="displacement-plot-container"></div>
            <div id="velocity-plot-container"></div>
        </div>
    </div>

    <script>
        // Global variables
        let isAnimating = false;
        let t = 0;
        let dt = 0.02;
        let systemParams = {};
        let displacementData = [];
        let velocityData = [];
        let p5Instance;

        // Physics calculation functions
        function calculateSystem() {
            const T = parseFloat(document.getElementById('slider-tension').value);
            const x1 = parseFloat(document.getElementById('slider-x1').value);
            const x2 = parseFloat(document.getElementById('slider-x2').value);
            const x3 = parseFloat(document.getElementById('slider-x3').value);
            const m1 = parseFloat(document.getElementById('slider-m1').value);
            const m2 = parseFloat(document.getElementById('slider-m2').value);
            const m3 = parseFloat(document.getElementById('slider-m3').value);

            // Mass matrix
            const M = [[m1, 0, 0], [0, m2, 0], [0, 0, m3]];

            // Stiffness matrix
            const K = numeric.mul(T, [[2, -1, 0], [-1, 2, -1], [0, -1, 2]]);

            // Calculate M^(-1/2)
            const M_sqrt_inv = [[1/Math.sqrt(m1), 0, 0], [0, 1/Math.sqrt(m2), 0], [0, 0, 1/Math.sqrt(m3)]];

            // C = M^(-1/2) * K * M^(-1/2)
            const C = numeric.dot(numeric.dot(M_sqrt_inv, K), M_sqrt_inv);

            // Solve eigenvalue problem
            const eig = numeric.eig(C);
            const eigenvalues = eig.lambda.x;
            const eigenvectors = eig.E.x;

            // Transform eigenvectors back to original coordinates
            const modes = [];
            for (let i = 0; i < 3; i++) {
                const v = [eigenvectors[0][i], eigenvectors[1][i], eigenvectors[2][i]];
                const mode = numeric.dot(M_sqrt_inv, v);
                modes.push(mode);
            }

            // Angular frequencies
            const omega = eigenvalues.map(lambda => Math.sqrt(lambda));

            // Initial conditions
            const y0 = [x1, x2, x3];

            // Calculate modal participation factors
            const c = [];
            for (let i = 0; i < 3; i++) {
                const numerator = numeric.dot(y0, modes[i]);
                const denominator = numeric.dot(modes[i], modes[i]);
                c.push(numerator / denominator);
            }

            systemParams = {
                T: T,
                masses: [m1, m2, m3],
                initialDisp: [x1, x2, x3],
                omega: omega,
                modes: modes,
                c: c
            };

            // Generate time series data
            generateTimeSeriesData();
        }

        function generateTimeSeriesData() {
            const tMax = 5;
            const tSteps = 200;
            displacementData = [[], [], []];
            velocityData = [[], [], []];

            for (let i = 0; i <= tSteps; i++) {
                const time = (i / tSteps) * tMax;
                const disp = calculateDisplacement(time);
                const vel = calculateVelocity(time);

                for (let j = 0; j < 3; j++) {
                    displacementData[j].push({x: time, y: disp[j]});
                    velocityData[j].push({x: time, y: vel[j]});
                }
            }
        }

        function calculateDisplacement(time) {
            const disp = [0, 0, 0];
            for (let i = 0; i < 3; i++) {
                for (let j = 0; j < 3; j++) {
                    disp[j] += systemParams.c[i] * systemParams.modes[i][j] * Math.cos(systemParams.omega[i] * time);
                }
            }
            return disp;
        }

        function calculateVelocity(time) {
            const vel = [0, 0, 0];
            for (let i = 0; i < 3; i++) {
                for (let j = 0; j < 3; j++) {
                    vel[j] += -systemParams.c[i] * systemParams.modes[i][j] * systemParams.omega[i] * Math.sin(systemParams.omega[i] * time);
                }
            }
            return vel;
        }

        // p5.js sketch
        const sketch = (p) => {
            let canvasWidth, canvasHeight;

            p.setup = () => {
                canvasWidth = document.getElementById('main-animation-container').offsetWidth - 20;
                canvasHeight = 400;
                p.createCanvas(canvasWidth, canvasHeight);
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                // Set up coordinate transformation
                const margin = 50;
                const plotWidth = canvasWidth - 2 * margin;
                const plotHeight = canvasHeight - 2 * margin;

                p.push();
                p.translate(margin, canvasHeight/2);
                p.scale(plotWidth/4, -plotHeight/2);

                // Draw grid
                p.strokeWeight(0.5/plotWidth);
                p.stroke(200);
                for (let x = 0; x <= 4; x++) {
                    p.line(x, -1, x, 1);
                }
                for (let y = -1; y <= 1; y += 0.5) {
                    p.line(0, y, 4, y);
                }

                // Draw axes
                p.strokeWeight(2/plotWidth);
                p.stroke(0);
                p.line(0, 0, 4, 0);
                p.line(0, -1, 0, 1);

                if (systemParams.masses) {
                    // Calculate current positions
                    const disp = calculateDisplacement(t);

                    // Draw string
                    p.stroke(100);
                    p.strokeWeight(3/plotWidth);
                    p.line(0, 0, 1, disp[0]);
                    p.line(1, disp[0], 2, disp[1]);
                    p.line(2, disp[1], 3, disp[2]);
                    p.line(3, disp[2], 4, 0);

                    // Draw masses
                    p.fill(255, 0, 0);
                    p.noStroke();
                    const massScale = 0.1;
                    for (let i = 0; i < 3; i++) {
                        const radius = massScale * Math.sqrt(systemParams.masses[i]);
                        p.circle(i + 1, disp[i], radius);
                    }
                }

                p.pop();

                // Draw labels
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER);
                for (let x = 0; x <= 4; x++) {
                    p.text(x, margin + x * plotWidth/4, canvasHeight/2 + 20);
                }
                p.textAlign(p.RIGHT);
                for (let y = -1; y <= 1; y += 0.5) {
                    if (y !== 0) {
                        p.text(y.toFixed(1), margin - 5, canvasHeight/2 - y * plotHeight/2);
                    }
                }
            };
        };

        // Initialize plots
        function initializePlots() {
            const layout = {
                xaxis: { title: 'time t', range: [0, 5] },
                yaxis: { title: 'displacement' },
                showlegend: true,
                shapes: [{
                    type: 'line',
                    x0: 0, x1: 0,
                    y0: 0, y1: 1,
                    yref: 'paper',
                    line: { color: 'red', width: 2 }
                }]
            };

            const traces = [
                { x: [], y: [], mode: 'lines', name: 'x₁', line: { color: 'blue' } },
                { x: [], y: [], mode: 'lines', name: 'x₂', line: { color: 'orange' } },
                { x: [], y: [], mode: 'lines', name: 'x₃', line: { color: 'green' } }
            ];

            Plotly.newPlot('displacement-plot-container', traces, {...layout, title: 'displacement'});
            Plotly.newPlot('velocity-plot-container', traces.map(t => ({...t})), {...layout, title: 'velocity', yaxis: { title: 'velocity' }});
        }

        function updatePlots() {
            // Update displacement plot
            for (let i = 0; i < 3; i++) {
                Plotly.restyle('displacement-plot-container', {
                    x: [displacementData[i].map(d => d.x)],
                    y: [displacementData[i].map(d => d.y)]
                }, [i]);
            }

            // Update velocity plot
            for (let i = 0; i < 3; i++) {
                Plotly.restyle('velocity-plot-container', {
                    x: [velocityData[i].map(d => d.x)],
                    y: [velocityData[i].map(d => d.y)]
                }, [i]);
            }

            updateTimeLine();
        }

        function updateTimeLine() {
            const update = {
                'shapes[0].x0': t,
                'shapes[0].x1': t
            };
            Plotly.relayout('displacement-plot-container', update);
            Plotly.relayout('velocity-plot-container', update);
        }

        function reset() {
            isAnimating = false;
            t = 0;
            document.getElementById('btn-play-pause').textContent = '▶';
            if (p5Instance) {
                p5Instance.noLoop();
            }
            calculateSystem();
            updatePlots();
            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        function animate() {
            if (isAnimating) {
                t += dt;
                if (t > 5) t = 0;
                updateTimeLine();
                if (p5Instance) {
                    p5Instance.redraw();
                }
                requestAnimationFrame(animate);
            }
        }

        // Event listeners
        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isAnimating = !isAnimating;
            document.getElementById('btn-play-pause').textContent = isAnimating ? '❚❚' : '▶';
            if (isAnimating) {
                if (p5Instance) {
                    p5Instance.loop();
                }
                animate();
            } else {
                if (p5Instance) {
                    p5Instance.noLoop();
                }
            }
        });

        document.getElementById('btn-reset').addEventListener('click', reset);

        // Add event listeners to all sliders
        const sliders = document.querySelectorAll('input[type="range"]');
        sliders.forEach(slider => {
            slider.addEventListener('input', reset);
        });

        // Initialize on load
        window.addEventListener('load', () => {
            p5Instance = new p5(sketch, 'main-animation-container');
            initializePlots();
            reset();
        });
    </script>
</body>
</html>
