            <div class="control-group">
                <label for="slider-enzyme-control">intermediate enzyme control [mol/m³]</label>
                <input type="range" id="slider-enzyme-control" min="0.1" max="5.0" step="0.01" value="1">
                <span class="value-display" id="value-enzyme-control">1</span>
            </div>

            <div class="control-group">
                <label for="slider-feed-rate">feed flow rate (m³/h)</label>
                <input type="range" id="slider-feed-rate" min="0.0" max="2.0" step="0.01" value="0.2">
                <span class="value-display" id="value-feed-rate">0.2</span>
            </div>

            <div class="control-group">
                <label for="slider-substrate-feed-conc">substrate feed concentration [mol/m³]</label>
                <input type="range" id="slider-substrate-feed-conc" min="10" max="200" step="1" value="100">
                <span class="value-display" id="value-substrate-feed-conc">100</span>
            </div>

            <div class="control-group">
                <label for="slider-feed-time">feed time (h)</label>
                <input type="range" id="slider-feed-time" min="0" max="60" step="1" value="35">
                <span class="value-display" id="value-feed-time">35</span>
            </div>

            <div class="control-group">
                <label for="select-plot">plot</label>
                <select id="select-plot">
                    <option value="fed batch">fed batch</option>
                    <option value="α">α</option>
                </select>
            </div>
        </div>

        <div id="visualization">
            <div id="plot-top"></div>
            <div id="plot-bottom"></div>
        </div>
    </div>

    <script>
        // Global variables to store simulation results
        let simulationData = {};

        // Initial conditions
        const initialConditions = {
            X: 0.1,
            Glu: 60,
            Eta: 0,
            E0: 1.0,
            E1: 0.0,
            E2: 0.0,
            V: 1.0
        };

        // Model parameters
        const params = {
            mu_max_glu: 0.5,
            mu_max_eta: 0.3,
            K_glu: 0.1,
            K_eta: 0.1,
            Y_x_glu: 0.5,
            Y_x_eta: 0.3,
            Y_eta_glu: 2.0,
            k_e: 0.1,
            n: 4
        };

        // RK4 ODE solver
        function rk4(f, y0, t0, tf, dt) {
            const n = Math.floor((tf - t0) / dt);
            const t = [];
            const y = [];

            let yi = [...y0];
            let ti = t0;

            for (let i = 0; i <= n; i++) {
                t.push(ti);
                y.push([...yi]);

                if (i < n) {
                    const k1 = f(ti, yi).map(v => v * dt);
                    const k2 = f(ti + dt/2, yi.map((v, j) => v + k1[j]/2)).map(v => v * dt);
                    const k3 = f(ti + dt/2, yi.map((v, j) => v + k2[j]/2)).map(v => v * dt);
                    const k4 = f(ti + dt, yi.map((v, j) => v + k3[j])).map(v => v * dt);

                    yi = yi.map((v, j) => v + (k1[j] + 2*k2[j] + 2*k3[j] + k4[j]) / 6);
                    ti += dt;
                }
            }

            return { t, y };
        }

        // ODE system for diauxic growth
        function odeSystem(t, y) {
            const [X, Glu, Eta, E0, E1, E2, V] = y;

            // Get current parameter values
            const alpha_max = parseFloat(document.getElementById('slider-alpha-max').value);
            const K_I = parseFloat(document.getElementById('slider-enzyme-control').value);
            const F = parseFloat(document.getElementById('slider-feed-rate').value);
            const G_in = parseFloat(document.getElementById('slider-substrate-feed-conc').value);
            const t_feed = parseFloat(document.getElementById('slider-feed-time').value);

            // Feed is active only until t_feed
            const F_active = t <= t_feed ? F : 0;

            // Calculate alpha (fraction of respiring biomass)
            const alpha = alpha_max * Math.pow(K_I, params.n) / (Math.pow(K_I, params.n) + Math.pow(Math.max(Glu, 0), params.n));

            // Growth rates
            const mu_glu = params.mu_max_glu * Glu / (params.K_glu + Glu);
            const mu_eta = params.mu_max_eta * Eta / (params.K_eta + Eta);

            // Total growth rate
            const mu = (1 - alpha) * mu_glu + alpha * mu_eta;

            // Dilution rate
            const D = F_active / V;

            // ODEs
            const dXdt = mu * X - D * X;
            const dGludt = F_active * G_in / V - mu_glu * X / params.Y_x_glu - D * Glu;
            const dEtadt = params.Y_eta_glu * mu_glu * X * (1 - alpha) / params.Y_x_glu - mu_eta * X / params.Y_x_eta - D * Eta;
            const dE0dt = params.k_e * (1 - E0 - E1 - E2);
            const dE1dt = params.k_e * E0 * Glu / (1 + Glu) - params.k_e * E1;
            const dE2dt = params.k_e * E0 / (1 + Glu) - params.k_e * E2;
            const dVdt = F_active;

            return [dXdt, dGludt, dEtadt, dE0dt, dE1dt, dE2dt, dVdt];
        }

        // Run simulation
        function runSimulation() {
            const t_max = parseFloat(document.getElementById('slider-t-max').value);
            const dt = 0.1;

            const y0 = [
                initialConditions.X,
                initialConditions.Glu,
                initialConditions.Eta,
                initialConditions.E0,
                initialConditions.E1,
                initialConditions.E2,
                initialConditions.V
            ];

            const result = rk4(odeSystem, y0, 0, t_max, dt);

            // Extract results
            simulationData.t = result.t;
            simulationData.X = result.y.map(v => v[0]);
            simulationData.Glu = result.y.map(v => v[1]);
            simulationData.Eta = result.y.map(v => v[2]);
            simulationData.E0 = result.y.map(v => v[3]);
            simulationData.E1 = result.y.map(v => v[4]);
            simulationData.E2 = result.y.map(v => v[5]);
            simulationData.V = result.y.map(v => v[6]);

            // Calculate alpha for each time point
            const alpha_max = parseFloat(document.getElementById('slider-alpha-max').value);
            const K_I = parseFloat(document.getElementById('slider-enzyme-control').value);
            simulationData.alpha = simulationData.Glu.map(Glu => 
                alpha_max * Math.pow(K_I, params.n) / (Math.pow(K_I, params.n) + Math.pow(Math.max(Glu, 0), params.n))
            );
        }

        // Draw plots
        function drawPlots() {
            const plotType = document.getElementById('select-plot').value;

            if (plotType === 'fed batch') {
                // Top plot: diauxic behavior
                const trace1 = {
                    x: simulationData.t,
                    y: simulationData.X,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'X(t)',
                    line: { color: 'blue' }
                };

                const trace2 = {
                    x: simulationData.t,
                    y: simulationData.Glu,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Glu(t)',
                    line: { color: 'red' }
                };

                const trace3 = {
                    x: simulationData.t,
                    y: simulationData.Eta,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Eta(t)',
                    line: { color: 'green' }
                };

                const layout1 = {
                    title: 'diauxic behavior',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'concentration [mol/m³]' },
                    showlegend: true
                };

                Plotly.react('plot-top', [trace1, trace2, trace3], layout1);

                // Bottom plot: enzyme concentration
                const trace4 = {
                    x: simulationData.t,
                    y: simulationData.E2,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'E₂(t)',
                    line: { color: 'black' }
                };

                const trace5 = {
                    x: simulationData.t,
                    y: simulationData.E1,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'E₁(t)',
                    line: { color: 'magenta' }
                };

                const trace6 = {
                    x: simulationData.t,
                    y: simulationData.E0,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'E₀(t)',
                    line: { color: 'orange' }
                };

                const layout2 = {
                    title: 'enzyme concentration',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'enzyme concentration [mol/m³]' },
                    showlegend: true
                };

                Plotly.react('plot-bottom', [trace4, trace5, trace6], layout2);

            } else if (plotType === 'α') {
                // Top plot: glucose concentration
                const trace1 = {
                    x: simulationData.t,
                    y: simulationData.Glu,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Glu(t)',
                    line: { color: 'red' }
                };

                const layout1 = {
                    title: 'glucose concentration',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'glucose concentration [mol/m³]' },
                    showlegend: true
                };

                Plotly.react('plot-top', [trace1], layout1);

                // Bottom plot: fraction of respiring biomass
                const trace2 = {
                    x: simulationData.t,
                    y: simulationData.alpha,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'α',
                    line: { color: 'blue' }
                };

                const layout2 = {
                    title: 'fraction of respiring biomass',
                    xaxis: { title: 'time (h)' },
                    yaxis: { title: 'α' },
                    showlegend: true
                };

                Plotly.react('plot-bottom', [trace2], layout2);
            }
        }

        // Update value displays
        function updateValueDisplay(sliderId, valueId) {
            const slider = document.getElementById(sliderId);
            const valueDisplay = document.getElementById(valueId);
            valueDisplay.textContent = slider.value;
        }

        // Initialize event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Set up slider event listeners
            const sliders = [
                { slider: 'slider-alpha-max', value: 'value-alpha-max' },
                { slider: 'slider-t-min', value: 'value-t-min' },
                { slider: 'slider-t-max', value: 'value-t-max' },
                { slider: 'slider-enzyme-control', value: 'value-enzyme-control' },
                { slider: 'slider-feed-rate', value: 'value-feed-rate' },
                { slider: 'slider-substrate-feed-conc', value: 'value-substrate-feed-conc' },
                { slider: 'slider-feed-time', value: 'value-feed-time' }
            ];

            sliders.forEach(({ slider, value }) => {
                const sliderElem = document.getElementById(slider);
                sliderElem.addEventListener('input', function() {
                    updateValueDisplay(slider, value);

                    // Special handling for t-max slider
                    if (slider === 'slider-t-max') {
                        const feedTimeSlider = document.getElementById('slider-feed-time');
                        feedTimeSlider.max = sliderElem.value;
                        if (parseFloat(feedTimeSlider.value) > parseFloat(sliderElem.value)) {
                            feedTimeSlider.value = sliderElem.value;
                            updateValueDisplay('slider-feed-time', 'value-feed-time');
                        }
                    }

                    // Run simulation and update plots
                    runSimulation();
                    drawPlots();
                });
            });

            // Plot selector event listener
            document.getElementById('select-plot').addEventListener('change', function() {
                drawPlots();
            });

            // Initial simulation and plot
            runSimulation();
            drawPlots();
        });
    </script>
</body>
</html>
