    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Scene setup
        let scene, camera, renderer, controls;
        let sun, earth, sunPath, constellations, terminator, latitudeParallel, dayNightParallel;
        let groundView = true;
        let dirty = true;

        // Initialize Three.js
        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000020);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // Camera
            camera = new THREE.PerspectiveCamera(75, (window.innerWidth - 300) / window.innerHeight, 0.1, 1000);
            camera.position.set(0, 1, 5);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lights
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(10, 10, 5);
            scene.add(directionalLight);

            // Create objects
            createSun();
            createEarth();
            createGround();
            createConstellations();
            createSunPath();
            createTerminator();
            createLatitudeParallel();

            // Initial update
            updateScene();
            animate();
        }

        function createSun() {
            const geometry = new THREE.SphereGeometry(0.3, 32, 32);
            const material = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            sun = new THREE.Mesh(geometry, material);
            scene.add(sun);
        }

        function createEarth() {
            const geometry = new THREE.SphereGeometry(2, 32, 32);
            const material = new THREE.MeshPhongMaterial({ color: 0x2233ff });
            earth = new THREE.Mesh(geometry, material);
            earth.rotation.z = 23.45 * Math.PI / 180;
            scene.add(earth);

            // Add axis
            const axisGeometry = new THREE.CylinderGeometry(0.05, 0.05, 5);
            const axisMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
            const axis = new THREE.Mesh(axisGeometry, axisMaterial);
            earth.add(axis);
        }

        function createGround() {
            const geometry = new THREE.CylinderGeometry(10, 10, 0.1, 32);
            const material = new THREE.MeshPhongMaterial({ color: 0x00aa00 });
            const ground = new THREE.Mesh(geometry, material);
            ground.position.y = -0.05;
            scene.add(ground);
        }

        function createConstellations() {
            constellations = new THREE.Group();

            // Simple constellation patterns (example)
            const points = [
                new THREE.Vector3(20, 10, 0),
                new THREE.Vector3(22, 12, 0),
                new THREE.Vector3(24, 10, 0),
                new THREE.Vector3(22, 8, 0),
                new THREE.Vector3(20, 10, 0)
            ];

            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0xaaaaaa });
            const line = new THREE.Line(geometry, material);
            constellations.add(line);

            scene.add(constellations);
        }

        function createSunPath() {
            const curve = new THREE.EllipseCurve(0, 0, 8, 8, 0, Math.PI, false, 0);
            const points = curve.getPoints(50);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0xffaa00 });
            sunPath = new THREE.Line(geometry, material);
            sunPath.rotation.x = -Math.PI / 2;
            scene.add(sunPath);
        }

        function createTerminator() {
            const geometry = new THREE.PlaneGeometry(10, 10);
            const material = new THREE.MeshBasicMaterial({ 
                color: 0x000000, 
                transparent: true, 
                opacity: 0.5,
                side: THREE.DoubleSide
            });
            terminator = new THREE.Mesh(geometry, material);
            scene.add(terminator);
        }

        function createLatitudeParallel() {
            const geometry = new THREE.TorusGeometry(1.5, 0.02, 8, 50);
            const material = new THREE.MeshBasicMaterial({ color: 0xff0000 });
            latitudeParallel = new THREE.Mesh(geometry, material);
            scene.add(latitudeParallel);

            // Day/night portion
            const points = [];
            for (let i = 0; i <= 50; i++) {
                const angle = (i / 50) * Math.PI;
                points.push(new THREE.Vector3(Math.cos(angle) * 1.5, 0, Math.sin(angle) * 1.5));
            }
            const lineGeometry = new THREE.BufferGeometry().setFromPoints(points);
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0xffff00, linewidth: 3 });
            dayNightParallel = new THREE.Line(lineGeometry, lineMaterial);
            scene.add(dayNightParallel);
        }

        function getDayOfYear(day) {
            const date = new Date(2024, 0, 1);
            date.setDate(day);
            const months = ['January', 'February', 'March', 'April', 'May', 'June', 
                          'July', 'August', 'September', 'October', 'November', 'December'];
            return `${months[date.getMonth()]} ${date.getDate()}`;
        }

        function calculateSolarDeclination(dayOfYear) {
            const angle = (dayOfYear - 80) * 2 * Math.PI / 365;
            return 23.45 * Math.sin(angle) * Math.PI / 180;
        }

        function calculateSunTimes(latitude, declination) {
            const lat = latitude * Math.PI / 180;
            const cosH = -Math.tan(lat) * Math.tan(declination);

            if (cosH > 1) {
                return { dayLength: 0, sunrise: null, sunset: null };
            } else if (cosH < -1) {
                return { dayLength: 24, sunrise: null, sunset: null };
            }

            const H = Math.acos(cosH);
            const dayLength = 2 * H * 12 / Math.PI;
            const sunrise = 12 - dayLength / 2;
            const sunset = 12 + dayLength / 2;

            return { dayLength, sunrise, sunset };
        }

        function formatTime(hours) {
            if (hours === null) return 'N/A';
            const h = Math.floor(hours);
            const m = Math.round((hours - h) * 60);
            return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}`;
        }

        function updateReadouts() {
            const day = parseInt(document.getElementById('slider-date').value);
            const latitude = parseInt(document.getElementById('slider-latitude').value);

            const declination = calculateSolarDeclination(day);
            const { dayLength, sunrise, sunset } = calculateSunTimes(latitude, declination);

            document.getElementById('readout-day-length').textContent = 
                dayLength === 24 ? '24:00' : formatTime(dayLength);
            document.getElementById('readout-night-length').textContent = 
                dayLength === 24 ? '0:00' : formatTime(24 - dayLength);
            document.getElementById('readout-sunrise-time').textContent = formatTime(sunrise);
            document.getElementById('readout-sunset-time').textContent = formatTime(sunset);

            if (sunrise !== null) {
                const sunriseAzimuth = Math.acos(Math.sin(declination) / Math.cos(latitude * Math.PI / 180));
                const sunsetAzimuth = 2 * Math.PI - sunriseAzimuth;
                document.getElementById('readout-sunrise-direction').textContent = 
                    `${Math.round(sunriseAzimuth * 180 / Math.PI)}°`;
                document.getElementById('readout-sunset-direction').textContent = 
                    `${Math.round(sunsetAzimuth * 180 / Math.PI)}°`;
            } else {
                document.getElementById('readout-sunrise-direction').textContent = 'N/A';
                document.getElementById('readout-sunset-direction').textContent = 'N/A';
            }
        }

        function updateScene() {
            const time = parseFloat(document.getElementById('slider-time').value);
            const latitude = parseInt(document.getElementById('slider-latitude').value);
            const day = parseInt(document.getElementById('slider-date').value);
            const view = document.getElementById('select-view').value;

            // Update sun position
            const hourAngle = (time - 12) * Math.PI / 12;
            const declination = calculateSolarDeclination(day);

            if (view === 'ground') {
                sun.position.x = Math.cos(hourAngle) * 8;
                sun.position.y = Math.sin(hourAngle) * 8 * Math.cos(latitude * Math.PI / 180);
                sun.position.z = 0;

                // Update visibility
                sunPath.visible = document.getElementById('check-sun-path').checked;
                constellations.visible = document.getElementById('check-constellations-day').checked;
                earth.visible = false;
                terminator.visible = false;
                latitudeParallel.visible = false;
                dayNightParallel.visible = false;
            } else {
                // Space view
                earth.visible = true;
                sunPath.visible = false;

                if (!document.getElementById('check-stationary-earth').checked) {
                    earth.rotation.y = hourAngle;
                }

                constellations.visible = document.getElementById('check-constellations').checked;
                terminator.visible = document.getElementById('check-day-night-division').checked;
                latitudeParallel.visible = document.getElementById('check-day-night-parallel').checked;
                dayNightParallel.visible = document.getElementById('check-day-night-parallel').checked;

                // Update latitude parallel position
                const latRad = latitude * Math.PI / 180;
                latitudeParallel.position.y = Math.sin(latRad) * 2;
                latitudeParallel.scale.set(Math.cos(latRad), 1, Math.cos(latRad));
            }

            dirty = true;
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            if (dirty) {
                renderer.render(scene, camera);
                dirty = false;
            }
        }

        // Event listeners
        document.getElementById('slider-date').addEventListener('input', function() {
