        });

        document.getElementById('select-view').addEventListener('change', function() {
            const view = this.value;
            document.getElementById('options-ground').style.display = view === 'ground' ? 'block' : 'none';
            document.getElementById('options-space').style.display = view === 'space' ? 'block' : 'none';

            if (view === 'ground') {
                camera.position.set(0, 1, 5);
                controls.target.set(0, 0, 0);
            } else {
                camera.position.set(0, 0, 30);
                controls.target.set(0, 0, 0);
            }

            updateScene();
        });

        // Checkbox event listeners
        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', updateScene);
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            camera.aspect = (window.innerWidth - 300) / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth - 300, window.innerHeight);
            dirty = true;
        });

        // Initialize
        init();
        updateReadouts();
    </script>
</body>
</html>
