    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-container">
            <div id="function-controls">
                <span>function</span>
                <button class="function-btn active" data-function="x">x</button>
                <button class="function-btn" data-function="(x-3)²">(x-3)²</button>
                <button class="function-btn" data-function="(x-3)³+27">(x-3)³+27</button>
                <button class="function-btn" data-function="√x">√x</button>
                <button class="function-btn" data-function="sin(x)+1">sin(x)+1</button>
                <button class="function-btn" data-function="eˣ/³">e^(x/3)</button>
            </div>
            <div id="type-controls">
                <span>type</span>
                <button class="type-btn" data-type="left">left</button>
                <button class="type-btn active" data-type="right">right</button>
                <button class="type-btn" data-type="midpoint">midpoint</button>
                <button class="type-btn" data-type="overestimate">overestimate</button>
                <button class="type-btn" data-type="underestimate">underestimate</button>
                <button class="type-btn" data-type="trapezoidal">trapezoidal</button>
            </div>
            <div id="slider-a-controls">
                <label for="slider-a">upper limit a</label>
                <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5">
                <span id="value-a">5</span>
            </div>
            <div id="slider-n-controls">
                <label for="slider-n">number of quadrilaterals</label>
                <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
                <span id="value-n">3</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let state = { funcKey: 'x', type: 'right', a: 5, n: 3 };
        let canvasWidth = 800;
        let canvasHeight = 500;
        let xMin = -1;
        let xMax = 8.5;
        let yMin, yMax;
        let needsRedraw = true;

        // Function definitions
        const functions = {
            'x': (x) => x,
            '(x-3)²': (x) => Math.pow(x - 3, 2),
            '(x-3)³+27': (x) => Math.pow(x - 3, 3) + 27,
            '√x': (x) => Math.sqrt(x),
            'sin(x)+1': (x) => Math.sin(x) + 1,
            'eˣ/³': (x) => Math.exp(x / 3)
        };

        // Antiderivatives for integral calculation
        const antiderivatives = {
            'x': (x) => x * x / 2,
            '(x-3)²': (x) => Math.pow(x - 3, 3) / 3,
            '(x-3)³+27': (x) => Math.pow(x - 3, 4) / 4 + 27 * x,
            '√x': (x) => (2/3) * Math.pow(x, 3/2),
            'sin(x)+1': (x) => -Math.cos(x) + x,
            'eˣ/³': (x) => 3 * Math.exp(x / 3)
        };

        // Event listeners
        document.querySelectorAll('.function-btn').forEach(btn => {
            btn.addEventListener('click', (e) => {
                document.querySelectorAll('.function-btn').forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');
                state.funcKey = e.target.getAttribute('data-function');
                needsRedraw = true;
            });
        });

        document.querySelectorAll('.type-btn').forEach(btn => {
            btn.addEventListener('click', (e) => {
                document.querySelectorAll('.type-btn').forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');
                state.type = e.target.getAttribute('data-type');
                needsRedraw = true;
            });
        });

        document.getElementById('slider-a').addEventListener('input', (e) => {
            state.a = parseFloat(e.target.value);
            document.getElementById('value-a').textContent = state.a.toFixed(2);
            needsRedraw = true;
        });

        document.getElementById('slider-n').addEventListener('input', (e) => {
            state.n = parseInt(e.target.value);
            document.getElementById('value-n').textContent = state.n;
            needsRedraw = true;
        });

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
        }

        function draw() {
            if (!needsRedraw) return;
            needsRedraw = false;

            background(248, 248, 248);

            // Calculate y-axis range
            calculateYRange();

            // Draw axes
            drawAxes();

            // Draw function curve
            drawFunction();

            // Draw estimation shapes
            let estimatedArea = drawEstimationShapes();

            // Draw upper limit marker
            stroke(255, 0, 0);
            strokeWeight(2);
            let xPixel = mapX(state.a);
            line(xPixel, mapY(yMin), xPixel, mapY(yMax));

            // Calculate true integral
            let trueIntegral = calculateTrueIntegral();

            // Display calculations
            fill(0);
            noStroke();
            textAlign(CENTER);
            textSize(16);
            text(`estimated area: ${estimatedArea.toFixed(5)}`, width/2, 30);
            text(`integral: ${trueIntegral.toFixed(5)}`, width/2, 50);
        }

        function calculateYRange() {
            let f = functions[state.funcKey];
            yMin = Infinity;
            yMax = -Infinity;

            for (let x = Math.max(0, xMin); x <= state.a; x += 0.01) {
                let y = f(x);
                if (!isNaN(y) && isFinite(y)) {
                    yMin = Math.min(yMin, y);
                    yMax = Math.max(yMax, y);
                }
            }

            // Add padding
            let padding = (yMax - yMin) * 0.1 || 1;
            yMin -= padding;
            yMax += padding;

            // Include 0 if not already
            yMin = Math.min(yMin, -0.5);
            yMax = Math.max(yMax, 0.5);
        }

        function mapX(x) {
            return map(x, xMin, xMax, 50, width - 50);
        }

        function mapY(y) {
            return map(y, yMin, yMax, height - 50, 80);
        }

        function drawAxes() {
            stroke(204, 204, 204);
            strokeWeight(1);

            // X-axis
            let y0 = mapY(0);
            line(mapX(xMin), y0, mapX(xMax), y0);

            // Y-axis
            let x0 = mapX(0);
            line(x0, mapY(yMin), x0, mapY(yMax));

            // Tick marks and labels
            fill(100);
            noStroke();
            textAlign(CENTER);
            textSize(12);

            // X-axis ticks
            for (let x = Math.ceil(xMin); x <= Math.floor(xMax); x++) {
                if (x !== 0) {
                    let xPixel = mapX(x);
                    stroke(204, 204, 204);
                    line(xPixel, y0 - 3, xPixel, y0 + 3);
                    noStroke();
                    text(x, xPixel, y0 + 20);
                }
            }

            // Y-axis ticks
            textAlign(RIGHT);
            let yStep = (yMax - yMin) / 5;
            for (let i = 1; i < 5; i++) {
                let y = yMin + i * yStep;
                let yPixel = mapY(y);
                stroke(204, 204, 204);
                line(x0 - 3, yPixel, x0 + 3, yPixel);
                noStroke();
                text(y.toFixed(1), x0 - 10, yPixel + 5);
            }
        }

        function drawFunction() {
            let f = functions[state.funcKey];
            stroke(0);
            strokeWeight(2);
            noFill();
            beginShape();

            for (let x = Math.max(0, xMin); x <= xMax; x += 0.01) {
                let y = f(x);
                if (!isNaN(y) && isFinite(y)) {
                    vertex(mapX(x), mapY(y));
                }
            }
            endShape();
        }

        function drawEstimationShapes() {
            let f = functions[state.funcKey];
            let deltaX = state.a / state.n;
            let estimatedArea = 0;

            for (let i = 0; i < state.n; i++) {
                let x1 = i * deltaX;
                let x2 = (i + 1) * deltaX;
                let shapeArea = 0;

                strokeWeight(1);
                stroke(100);

                if (state.type === 'left') {
                    let height = f(x1);
                    shapeArea = height * deltaX;
                    drawRect(x1, x2, height, shapeArea, x1, x2);
                } else if (state.type === 'right') {
                    let height = f(x2);
                    shapeArea = height * deltaX;
                    drawRect(x1, x2, height, shapeArea, x1, x2);
                } else if (state.type === 'midpoint') {
                    let height = f((x1 + x2) / 2);
                    shapeArea = height * deltaX;
                    drawRect(x1, x2, height, shapeArea, x1, x2);
                } else if (state.type === 'trapezoidal') {
                    let h1 = f(x1);
                    let h2 = f(x2);
                    shapeArea = (h1 + h2) / 2 * deltaX;
                    drawTrapezoid(x1, x2, h1, h2, shapeArea, x1, x2);
                } else if (state.type === 'overestimate' || state.type === 'underestimate') {
                    let height = state.type === 'overestimate' ? -Infinity : Infinity;
                    for (let x = x1; x <= x2; x += 0.01) {
                        let y = f(x);
                        if (state.type === 'overestimate') {
                            height = Math.max(height, y);
                        } else {
                            height = Math.min(height, y);
                        }
                    }
                    shapeArea = height * deltaX;
                    drawRect(x1, x2, height, shapeArea, x1, x2);
                }

                estimatedArea += shapeArea;
            }

            return estimatedArea;
        }

        function drawRect(x1, x2, height, shapeArea, integralX1, integralX2) {
            let trueArea = calculateTrueIntegral(integralX1, integralX2);
            if (shapeArea > trueArea) {
                fill(173, 216, 230, 178); // Light blue with 70% opacity
            } else {
                fill(144, 238, 144, 178); // Light green with 70% opacity
            }

            let x1Pixel = mapX(x1);
            let x2Pixel = mapX(x2);
            let y0Pixel = mapY(0);
            let yPixel = mapY(height);

            rect(x1Pixel, Math.min(y0Pixel, yPixel), x2Pixel - x1Pixel, Math.abs(yPixel - y0Pixel));
        }

        function drawTrapezoid(x1, x2, h1, h2, shapeArea, integralX1, integralX2) {
            let trueArea = calculateTrueIntegral(integralX1, integralX2);
            if (shapeArea > trueArea) {
                fill(173, 216, 230, 178);
            } else {
                fill(144, 238, 144, 178);
            }

            let x1Pixel = mapX(x1);
            let x2Pixel = mapX(x2);
            let y0Pixel = mapY(0);
            let y1Pixel = mapY(h1);
            let y2Pixel = mapY(h2);

            quad(x1Pixel, y0Pixel, x1Pixel, y1Pixel, x2Pixel, y2Pixel, x2Pixel, y0Pixel);
        }

        function calculateTrueIntegral(from = 0, to = state.a) {
            let F = antiderivatives[state.funcKey];
            return F(to) - F(from);
        }
    </script>
</body>
</html>
