            turns: document.getElementById('value-turns')
        };

        const initialValues = {
            current: 0.431,
            earthField: 0.0000194,
            radius: 0.5,
            turns: 25
        };

        function formatScientific(value) {
            if (value < 0.0001 && value > 0) {
                return value.toExponential(2);
            }
            return value.toString();
        }

        function updateSliderValue(slider, valueSpan, scientific = false) {
            const value = parseFloat(slider.value);
            if (scientific) {
                valueSpan.textContent = formatScientific(value);
            } else {
                valueSpan.textContent = value;
            }
            if (typeof redraw === 'function') {
                redraw();
            }
        }

        sliders.current.addEventListener('input', () => updateSliderValue(sliders.current, values.current));
        sliders.earthField.addEventListener('input', () => updateSliderValue(sliders.earthField, values.earthField, true));
        sliders.radius.addEventListener('input', () => updateSliderValue(sliders.radius, values.radius));
        sliders.turns.addEventListener('input', () => updateSliderValue(sliders.turns, values.turns));

        document.getElementById('btn-reset').addEventListener('click', () => {
            sliders.current.value = initialValues.current;
            sliders.earthField.value = initialValues.earthField;
            sliders.radius.value = initialValues.radius;
            sliders.turns.value = initialValues.turns;

            updateSliderValue(sliders.current, values.current);
            updateSliderValue(sliders.earthField, values.earthField, true);
            updateSliderValue(sliders.radius, values.radius);
            updateSliderValue(sliders.turns, values.turns);
        });

        // p5.js sketch
        let sketch = function(p) {
            const canvasWidth = 600;
            const canvasHeight = 600;
            const worldMin = -0.5;
            const worldMax = 0.5;
            const mu0 = 4 * Math.PI * 1e-7;

            p.setup = function() {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('viz-container');
                p.colorMode(p.HSB, 360, 100, 100);
            };

            p.draw = function() {
                p.background(255);

                const I = parseFloat(sliders.current.value);
                const B_earth = parseFloat(sliders.earthField.value);
                const R = parseFloat(sliders.radius.value);
                const N = parseInt(sliders.turns.value);

                drawAxes();
                drawStreamlines(I, B_earth, R, N);
            };

            function worldToCanvas(worldY, worldZ) {
                const x = p.map(worldY, worldMin, worldMax, 50, canvasWidth - 50);
                const y = p.map(worldZ, worldMax, worldMin, 50, canvasHeight - 50);
                return [x, y];
            }

            function drawAxes() {
                p.stroke(255, 0, 0);
                p.strokeWeight(2);

                // Y axis
                p.line(...worldToCanvas(worldMin, 0), ...worldToCanvas(worldMax, 0));

                // Z axis
                p.line(...worldToCanvas(0, worldMin), ...worldToCanvas(0, worldMax));

                // Ticks and labels
                p.textAlign(p.CENTER, p.TOP);
                p.fill(255, 0, 0);
                p.noStroke();

                for (let val = -0.4; val <= 0.4; val += 0.2) {
                    // Y axis ticks
                    const [x1, y1] = worldToCanvas(val, 0);
                    p.stroke(255, 0, 0);
                    p.line(x1, y1 - 5, x1, y1 + 5);
                    p.noStroke();
                    p.text(val.toFixed(1), x1, y1 + 10);

                    // Z axis ticks
                    const [x2, y2] = worldToCanvas(0, val);
                    p.stroke(255, 0, 0);
                    p.line(x2 - 5, y2, x2 + 5, y2);
                    p.noStroke();
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.text(val.toFixed(1), x2 - 10, y2);
                    p.textAlign(p.CENTER, p.TOP);
                }

                // Axis labels
                p.textAlign(p.CENTER, p.TOP);
                p.text('Y', ...worldToCanvas(worldMax - 0.05, -0.05));
                p.textAlign(p.LEFT, p.CENTER);
                p.text('Z', ...worldToCanvas(0.05, worldMax - 0.05));
            }

            function ellipticK(m) {
                const tol = 1e-10;
                let a0 = 1.0;
                let b0 = Math.sqrt(1.0 - m);
                let s0 = m;
                let i = 0;

                while (Math.abs(a0 - b0) > tol * Math.abs(a0 + b0)) {
                    const a1 = (a0 + b0) / 2.0;
                    const b1 = Math.sqrt(a0 * b0);
                    const c1 = (a0 - b0) / 2.0;
                    i++;
                    const s1 = s0 + Math.pow(2, i) * c1 * c1;
                    a0 = a1;
                    b0 = b1;
                    s0 = s1;
                }
                return Math.PI / (2.0 * a0);
            }

            function ellipticE(m) {
                const tol = 1e-10;
                let a0 = 1.0;
                let b0 = Math.sqrt(1.0 - m);
                let c0 = Math.sqrt(m);
                let s0 = m;
                let i = 0;

                while (Math.abs(a0 - b0) > tol * Math.abs(a0 + b0)) {
                    const a1 = (a0 + b0) / 2.0;
                    const b1 = Math.sqrt(a0 * b0);
                    const c1 = (a0 - b0) / 2.0;
                    i++;
                    const s1 = s0 + Math.pow(2, i) * c1 * c1;
                    a0 = a1;
                    b0 = b1;
                    c0 = c1;
                    s0 = s1;
                }
                const K = Math.PI / (2.0 * a0);
                const E = K * (1.0 - s0 / 2.0);
                return E;
            }

            function calculateCoilField(y, z, R, I, N, z_offset) {
                const zp = z - z_offset;
                const denom = (R + y) * (R + y) + zp * zp;

                if (denom === 0) return [0, 0];

                const m = (4 * R * y) / denom;

                if (m < 0 || m > 1) return [0, 0];

                const K = ellipticK(m);
                const E = ellipticE(m);

                const common_factor_1 = (mu0 * I * N) / (2 * Math.PI);
                const common_factor_2 = 1 / Math.sqrt(denom);

                let B_y = 0;
                if (y !== 0) {
                    const term1 = (R * R + y * y + zp * zp) / ((R - y) * (R - y) + zp * zp);
                    B_y = common_factor_1 * (zp / y) * common_factor_2 * (term1 * E - K);
                }

                const term2 = (R * R - y * y - zp * zp) / ((R - y) * (R - y) + zp * zp);
                const B_z = common_factor_1 * common_factor_2 * (K - term2 * E);

                return [B_y, B_z];
            }

            function calculateTotalField(y, z, I, B_earth, R, N) {
                const [B_y1, B_z1] = calculateCoilField(y, z, R, I, N, -R/2);
                const [B_y2, B_z2] = calculateCoilField(y, z, R, I, N, R/2);

                const B_total_y = B_y1 + B_y2;
                const B_total_z = B_z1 + B_z2 + B_earth;

                return [B_total_y, B_total_z];
            }

            function drawStreamlines(I, B_earth, R, N) {
                const gridSize = 25;
                const stepSize = 0.005;
                const numSteps = 100;

                // Calculate magnitude range
                let minMag = Infinity;
                let maxMag = -Infinity;

                for (let i = 0; i < gridSize; i++) {
                    for (let j = 0; j < gridSize; j++) {
                        const y = p.map(i, 0, gridSize - 1, worldMin * 0.9, worldMax * 0.9);
                        const z = p.map(j, 0, gridSize - 1, worldMin * 0.9, worldMax * 0.9);
                        const [By, Bz] = calculateTotalField(y, z, I, B_earth, R, N);
                        const mag = Math.sqrt(By * By + Bz * Bz);
                        minMag = Math.min(minMag, mag);
                        maxMag = Math.max(maxMag, mag);
                    }
                }

                // Draw streamlines
                for (let i = 0; i < gridSize; i++) {
                    for (let j = 0; j < gridSize; j++) {
                        const y0 = p.map(i, 0, gridSize - 1, worldMin * 0.9, worldMax * 0.9);
                        const z0 = p.map(j, 0, gridSize - 1, worldMin * 0.9, worldMax * 0.9);

                        // Forward direction
                        drawStreamline(y0, z0, I, B_earth, R, N, minMag, maxMag, stepSize, numSteps, 1);

                        // Backward direction
                        drawStreamline(y0, z0, I, B_earth, R, N, minMag, maxMag, stepSize, numSteps, -1);
                    }
                }
            }

            function drawStreamline(y0, z0, I, B_earth, R, N, minMag, maxMag, stepSize, numSteps, direction) {
                let y = y0;
                let z = z0;

                p.strokeWeight(1);
                p.noFill();

                for (let step = 0; step < numSteps; step++) {
                    const [By, Bz] = calculateTotalField(y, z, I, B_earth, R, N);
                    const mag = Math.sqrt(By * By + Bz * Bz);

                    if (mag === 0) break;

                    // Color mapping
                    const hue = p.map(mag, minMag, maxMag, 60, 270);
                    p.stroke(hue, 100, 100);

                    // Normalize and step
                    const dy = (By / mag) * stepSize * direction;
                    const dz = (Bz / mag) * stepSize * direction;

                    const [x1, y1] = worldToCanvas(y, z);
                    const [x2, y2] = worldToCanvas(y + dy, z + dz);

                    p.line(x1, y1, x2, y2);

                    // Draw arrow every 10 steps
                    if (step % 10 === 0 && step > 0) {
                        const arrowSize = 3;
                        const angle = Math.atan2(dz, dy);
                        const arrowAngle = 0.5;

                        p.line(x2, y2, 
                               x2 - arrowSize * Math.cos(angle - arrowAngle), 
                               y2 - arrowSize * Math.sin(angle - arrowAngle));
                        p.line(x2, y2, 
                               x2 - arrowSize * Math.cos(angle + arrowAngle), 
                               y2 - arrowSize * Math.sin(angle + arrowAngle));
                    }

                    y += dy;
                    z += dz;

                    // Check bounds
                    if (y < worldMin || y > worldMax || z < worldMin || z > worldMax) break;
                }
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
