    <script>
        // Global state
        let currentMessage = '';
        let currentCiphertext = '';

        // Word list sorted by length descending
        const WORD_LIST = ['MESSAGE', 'ATTACK', 'BRUTUS', 'CAESAR', 'CIPHER', 'SECRET', 'MARCH', 'HELLO', 'WORLD', 'IDES', 'EVEN', 'COME', 'DAWN', 'THE', 'ARE', 'YOU', 'OF', 'I', 'A'];

        // Initialize keyboards
        function initializeKeyboards() {
            const encodingKeyboard = document.getElementById('keyboard-encoding');
            const decodingKeyboard = document.getElementById('keyboard-decoding');

            // Encoding keyboard: A-Z, SPACE, BACKSPACE
            for (let i = 65; i <= 90; i++) {
                const btn = document.createElement('button');
                btn.textContent = String.fromCharCode(i);
                btn.onclick = () => handleEncodingKeyPress(String.fromCharCode(i));
                encodingKeyboard.appendChild(btn);
            }

            const spaceBtn = document.createElement('button');
            spaceBtn.textContent = 'SPACE';
            spaceBtn.className = 'wide';
            spaceBtn.onclick = () => handleEncodingKeyPress(' ');
            encodingKeyboard.appendChild(spaceBtn);

            const backspaceBtn1 = document.createElement('button');
            backspaceBtn1.textContent = '←';
            backspaceBtn1.className = 'wide';
            backspaceBtn1.onclick = () => handleEncodingKeyPress('BACKSPACE');
            encodingKeyboard.appendChild(backspaceBtn1);

            // Decoding keyboard: A-Z, BACKSPACE
            for (let i = 65; i <= 90; i++) {
                const btn = document.createElement('button');
                btn.textContent = String.fromCharCode(i);
                btn.onclick = () => handleDecodingKeyPress(String.fromCharCode(i));
                decodingKeyboard.appendChild(btn);
            }

            const backspaceBtn2 = document.createElement('button');
            backspaceBtn2.textContent = '←';
            backspaceBtn2.className = 'wide';
            backspaceBtn2.onclick = () => handleDecodingKeyPress('BACKSPACE');
            decodingKeyboard.appendChild(backspaceBtn2);
        }

        // Tab switching
        document.getElementById('btn-toggle-encoding').onclick = () => {
            document.getElementById('panel-encoding').style.display = 'block';
            document.getElementById('panel-decoding').style.display = 'none';
            document.getElementById('btn-toggle-encoding').classList.add('active');
            document.getElementById('btn-toggle-decoding').classList.remove('active');
        };

        document.getElementById('btn-toggle-decoding').onclick = () => {
            document.getElementById('panel-encoding').style.display = 'none';
            document.getElementById('panel-decoding').style.display = 'block';
            document.getElementById('btn-toggle-encoding').classList.remove('active');
            document.getElementById('btn-toggle-decoding').classList.add('active');
        };

        // Encoding panel functions
        function updateEncoding() {
            const offset = parseInt(document.getElementById('slider-encoding-offset').value);

            // Generate plaintext
            const plaintext = currentMessage.toUpperCase().replace(/ /g, 'X');
            document.getElementById('display-plaintext').textContent = plaintext;

            // Update alphabet mapping
            let shiftedAlphabet = '';
            for (let i = 0; i < 26; i++) {
                const shiftedChar = String.fromCharCode((i + offset) % 26 + 65);
                shiftedAlphabet += shiftedChar + (i < 25 ? ' ' : '');
            }
            document.getElementById('display-encoding-alphabet-to').textContent = shiftedAlphabet;

            // Generate ciphertext
            let ciphertext = '';
            for (let char of plaintext) {
                if (char >= 'A' && char <= 'Z') {
                    const charCode = char.charCodeAt(0);
                    const shiftedCode = ((charCode - 65 + offset) % 26) + 65;
                    ciphertext += String.fromCharCode(shiftedCode);
                }
            }
            currentCiphertext = ciphertext;
            document.getElementById('display-ciphertext').textContent = ciphertext;
        }

        function handleEncodingKeyPress(key) {
            if (key === 'BACKSPACE') {
                currentMessage = currentMessage.slice(0, -1);
            } else if (currentMessage.length < 32) {
                currentMessage += key;
            }
            document.getElementById('display-message').textContent = currentMessage;
            updateEncoding();
        }

        document.getElementById('select-message').onchange = (e) => {
            currentMessage = e.target.value;
            document.getElementById('display-message').textContent = currentMessage;
            updateEncoding();
        };

        document.getElementById('slider-encoding-offset').oninput = (e) => {
            document.getElementById('span-encoding-offset-value').textContent = e.target.value;
            updateEncoding();
        };

        document.getElementById('btn-clear-encoding').onclick = () => {
            currentMessage = '';
            currentCiphertext = '';
            document.getElementById('slider-encoding-offset').value = 0;
            document.getElementById('span-encoding-offset-value').textContent = '0';
            document.getElementById('display-message').textContent = '';
            document.getElementById('display-plaintext').textContent = '';
            document.getElementById('display-ciphertext').textContent = '';
            document.getElementById('select-message').value = '';
            updateEncoding();
        };

        // Decoding panel functions
        function updateDecoding() {
            const offset = parseInt(document.getElementById('slider-decoding-offset').value);
            const interceptText = document.getElementById('display-intercept').textContent;

            // Update alphabet mapping
            let fromAlphabet = '';
            for (let i = 0; i < 26; i++) {
                const shiftedChar = String.fromCharCode(((i - offset + 26) % 26) + 65);
                fromAlphabet += shiftedChar + (i < 25 ? ' ' : '');
            }
            document.getElementById('display-decoding-alphabet-from').textContent = fromAlphabet;

            // Generate decoding table
            const tbody = document.querySelector('#table-decoding-output tbody');
            tbody.innerHTML = '';

            for (let d = offset - 2; d <= offset + 2; d++) {
                const tr = document.createElement('tr');
                const tdOffset = document.createElement('td');
                tdOffset.textContent = d;

                const tdDecoding = document.createElement('td');
                const decoded = decodeText(interceptText, d);
                tdDecoding.innerHTML = highlightWords(decoded);

                tr.appendChild(tdOffset);
                tr.appendChild(tdDecoding);
                tbody.appendChild(tr);
            }
        }

        function decodeText(text, offset) {
            let result = '';
            for (let char of text) {
                if (char >= 'A' && char <= 'Z') {
                    const charCode = char.charCodeAt(0);
                    const decodedCode = ((charCode - 65 - offset + 26) % 26) + 65;
                    result += String.fromCharCode(decodedCode);
                }
            }
            return result;
        }

        function highlightWords(text) {
            let result = '';
            let i = 0;

            while (i < text.length) {
                let found = false;

                for (let word of WORD_LIST) {
                    if (text.substr(i, word.length) === word) {
                        result += `<span class="highlighted">${word}</span>`;
                        i += word.length;
                        found = true;
                        break;
                    }
                }

                if (!found) {
                    result += text[i];
                    i++;
                }
            }

            return result;
        }

        function handleDecodingKeyPress(key) {
            const display = document.getElementById('display-intercept');
            if (key === 'BACKSPACE') {
                display.textContent = display.textContent.slice(0, -1);
            } else if (display.textContent.length < 32) {
                display.textContent += key;
            }
            updateDecoding();
        }

        document.getElementById('btn-intercept').onclick = () => {
            document.getElementById('display-intercept').textContent = currentCiphertext;
            updateDecoding();
        };

        document.getElementById('slider-decoding-offset').oninput = (e) => {
            document.getElementById('span-decoding-offset-value').textContent = e.target.value;
            updateDecoding();
        };

        document.getElementById('btn-clear-decoding').onclick = () => {
            document.getElementById('display-intercept').textContent = '';
            document.getElementById('slider-decoding-offset').value = 0;
            document.getElementById('span-decoding-offset-value').textContent = '0';
            document.querySelector('#table-decoding-output tbody').innerHTML = '';
            updateDecoding();
        };

        // Initialize the page
        initializeKeyboards();
        updateEncoding();
        updateDecoding();
    </script>
