    <script>
        let beforeCanvas, afterCanvas;
        let beforeGraphics, afterGraphics;

        function setup() {
            noCanvas();

            beforeCanvas = createCanvas(200, 200);
            beforeCanvas.parent('canvas-before-container');

            afterCanvas = createGraphics(200, 200);

            updateVisualization();
        }

        function adjustSlider(sliderId, delta) {
            const slider = document.getElementById(sliderId);
            const newValue = parseInt(slider.value) + delta;
            if (newValue >= slider.min && newValue <= slider.max) {
                slider.value = newValue;
                slider.dispatchEvent(new Event('input'));
            }
        }

        function createBlurKernel(k) {
            const D = 2 * k + 1;
            const kernel = [];
            const value = 1.0 / (D * D);

            for (let i = 0; i < D; i++) {
                kernel[i] = [];
                for (let j = 0; j < D; j++) {
                    kernel[i][j] = value;
                }
            }
            return kernel;
        }

        function createShadowKernel(k) {
            const D = 2 * k + 1;
            const kernel = [];

            // Initialize with zeros
            for (let i = 0; i < D; i++) {
                kernel[i] = [];
                for (let j = 0; j < D; j++) {
                    kernel[i][j] = 0;
                }
            }

            // Fill offset region with ones
            const value = 1.0 / (k * k);
            for (let i = k + 1; i < D; i++) {
                for (let j = k + 1; j < D; j++) {
                    kernel[i][j] = value;
                }
            }

            return kernel;
        }

        function applyConvolution(grid, kernel) {
            const N = grid.length;
            const k = Math.floor(kernel.length / 2);
            const result = [];

            for (let i = 0; i < N; i++) {
                result[i] = [];
                for (let j = 0; j < N; j++) {
                    let sum = 0;

                    for (let ki = 0; ki < kernel.length; ki++) {
                        for (let kj = 0; kj < kernel[0].length; kj++) {
                            const gi = i + ki - k;
                            const gj = j + kj - k;

                            if (gi >= 0 && gi < N && gj >= 0 && gj < N) {
                                sum += grid[gi][gj] * kernel[ki][kj];
                            }
                        }
                    }

                    result[i][j] = sum;
                }
            }

            return result;
        }

        function updateVisualization() {
            const convolverValue = parseInt(document.getElementById('slider-convolver').value);
            const imageSize = parseInt(document.getElementById('slider-image-size').value);
            const letter = document.getElementById('input-letter').value || 'a';
            const filterType = document.querySelector('input[name="filter"]:checked').value;

            // Create before image grid
            const grid = [];
            for (let i = 0; i < imageSize; i++) {
                grid[i] = [];
                for (let j = 0; j < imageSize; j++) {
                    grid[i][j] = 0;
                }
            }

            // Render letter to temporary graphics
            const tempGraphics = createGraphics(imageSize, imageSize);
            tempGraphics.background(255);
            tempGraphics.fill(0);
            tempGraphics.textAlign(CENTER, CENTER);
            tempGraphics.textSize(imageSize * 0.8);
            tempGraphics.text(letter, imageSize / 2, imageSize / 2);

            // Convert to grid
            tempGraphics.loadPixels();
            for (let i = 0; i < imageSize; i++) {
                for (let j = 0; j < imageSize; j++) {
                    const idx = (i * imageSize + j) * 4;
                    grid[i][j] = tempGraphics.pixels[idx] < 128 ? 1 : 0;
                }
            }

            // Draw before image
            clear();
            noStroke();
            const pixelSize = 200 / imageSize;
            for (let i = 0; i < imageSize; i++) {
                for (let j = 0; j < imageSize; j++) {
                    fill(grid[i][j] === 1 ? 0 : 255);
                    rect(j * pixelSize, i * pixelSize, pixelSize, pixelSize);
                }
            }

            // Create kernel
            let kernel;
            if (filterType === 'blur') {
                kernel = createBlurKernel(convolverValue);
            } else {
                kernel = createShadowKernel(convolverValue);
            }

            // Apply convolution
            const result = applyConvolution(grid, kernel);

            // Draw after image
            afterCanvas.clear();
            afterCanvas.noStroke();
            for (let i = 0; i < imageSize; i++) {
                for (let j = 0; j < imageSize; j++) {
                    const value = result[i][j];
                    afterCanvas.fill(255 * (1 - value));
                    afterCanvas.rect(j * pixelSize, i * pixelSize, pixelSize, pixelSize);
                }
            }

            // Display after canvas
            const afterContainer = document.getElementById('canvas-after-container');
            afterContainer.innerHTML = '';
            const img = createImg(afterCanvas.canvas.toDataURL());
            img.parent('canvas-after-container');
        }

        // Event listeners
        document.getElementById('slider-convolver').addEventListener('input', updateVisualization);
        document.getElementById('slider-image-size').addEventListener('input', updateVisualization);
        document.getElementById('input-letter').addEventListener('input', updateVisualization);
        document.getElementById('radio-shadow').addEventListener('change', updateVisualization);
        document.getElementById('radio-blur').addEventListener('change', updateVisualization);

        // Arrow key navigation for letter input
        document.getElementById('input-letter').addEventListener('keydown', function(e) {
            if (e.key === 'ArrowUp' || e.key === 'ArrowDown') {
                e.preventDefault();
                const current = this.value.toLowerCase();
                let charCode = current.charCodeAt(0);

                if (e.key === 'ArrowUp') {
                    charCode = charCode < 122 ? charCode + 1 : 97;
                } else {
                    charCode = charCode > 97 ? charCode - 1 : 122;
                }

                this.value = String.fromCharCode(charCode);
                this.dispatchEvent(new Event('input'));
            }
        });
    </script>
</body>
</html>
