            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        // Amino acid three-letter codes mapping
        const aminoAcidMap = {
            'A': 'ALA', 'C': 'CYS', 'D': 'ASP', 'E': 'GLU', 'F': 'PHE',
            'G': 'GLY', 'H': 'HIS', 'I': 'ILE', 'K': 'LYS', 'L': 'LEU',
            'M': 'MET', 'N': 'ASN', 'P': 'PRO', 'Q': 'GLN', 'R': 'ARG',
            'S': 'SER', 'T': 'THR', 'V': 'VAL', 'W': 'TRP', 'Y': 'TYR'
        };

        // Generate protein data
        function generateProteinData(numResidues) {
            const aminoAcids = Object.keys(aminoAcidMap);
            const residues = [];
            let currentPos = { x: 0, y: 0, z: 0 };

            for (let i = 0; i < numResidues; i++) {
                const resType = aminoAcids[Math.floor(Math.random() * aminoAcids.length)];
                const resName = aminoAcidMap[resType];

                // Random walk for CA position
                currentPos.x += (Math.random() - 0.5) * 3.8;
                currentPos.y += (Math.random() - 0.5) * 3.8;
                currentPos.z += (Math.random() - 0.5) * 3.8;

                const atoms = [
                    { atomName: 'CA', x: currentPos.x, y: currentPos.y, z: currentPos.z },
                    { atomName: 'N', x: currentPos.x - 1.5, y: currentPos.y, z: currentPos.z },
                    { atomName: 'C', x: currentPos.x + 1.5, y: currentPos.y, z: currentPos.z },
                    { atomName: 'O', x: currentPos.x + 1.5, y: currentPos.y + 1.2, z: currentPos.z },
                    { atomName: 'CB', x: currentPos.x, y: currentPos.y, z: currentPos.z + 1.5 }
                ];

                residues.push({
                    resName: resName,
                    resType: resType,
                    resSeq: i + 1,
                    atoms: atoms
                });
            }

            return residues;
        }

        // Global variables
        const proteinData = generateProteinData(400);
        let currentView = 'crystallography';
        let scene, camera, renderer, controls;
        let atomMeshes = [];
        let p5Instance;
        let distanceMatrix;

        // Calculate distance matrix for contact map
        function calculateDistanceMatrix() {
            distanceMatrix = [];
            for (let i = 0; i < proteinData.length; i++) {
                distanceMatrix[i] = [];
                for (let j = 0; j < proteinData.length; j++) {
                    const ca1 = proteinData[i].atoms.find(a => a.atomName === 'CA');
                    const ca2 = proteinData[j].atoms.find(a => a.atomName === 'CA');
                    const dist = Math.sqrt(
                        Math.pow(ca1.x - ca2.x, 2) +
                        Math.pow(ca1.y - ca2.y, 2) +
                        Math.pow(ca1.z - ca2.z, 2)
                    );
                    distanceMatrix[i][j] = dist;
                }
            }
        }

        // Initialize three.js scene
        function initThreeJS() {
            const container = document.getElementById('canvas-container');
            const width = parseInt(document.getElementById('slider-plot-size').value);
            const height = width;

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 1000);
            camera.position.set(100, 100, 100);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            container.appendChild(renderer.domElement);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(50, 50, 50);
            scene.add(directionalLight);

            drawProteinStructure();
            animate();
        }

        // Draw protein structure
        function drawProteinStructure() {
            // Clear existing meshes
            atomMeshes.forEach(mesh => scene.remove(mesh));
            atomMeshes = [];

            const showLateralChains = document.getElementById('checkbox-lateral-chains').checked;
            const grayLevel = parseFloat(document.getElementById('slider-gray-level').value);
            const browseSeq = parseInt(document.getElementById('slider-browse-sequence').value) - 1;
            const resX = parseInt(document.getElementById('slider-residue-x').value) - 1;
            const resY = parseInt(document.getElementById('slider-residue-y').value) - 1;

            // Calculate color based on gray level
            const blueValue = Math.floor(grayLevel * 255);
            const mainColor = new THREE.Color(`rgb(0, 0, ${blueValue})`);

            const sphereGeometry = new THREE.SphereGeometry(0.5, 16, 16);

            // Bounding box calculation
            let minX = Infinity, minY = Infinity, minZ = Infinity;
            let maxX = -Infinity, maxY = -Infinity, maxZ = -Infinity;

            proteinData.forEach((residue, resIndex) => {
                residue.atoms.forEach(atom => {
                    if (!showLateralChains && atom.atomName !== 'CA') return;

                    let color = mainColor;

                    // Highlight logic
                    if (resIndex === browseSeq) {
                        color = new THREE.Color(0xffff00); // Yellow
                    } else if (atom.atomName === 'CA' && (resIndex === resX || resIndex === resY)) {
                        color = new THREE.Color(0xff0000); // Red
                    }

                    const material = new THREE.MeshLambertMaterial({ color: color });
                    const sphere = new THREE.Mesh(sphereGeometry, material);
                    sphere.position.set(atom.x, atom.y, atom.z);
                    scene.add(sphere);
                    atomMeshes.push(sphere);

                    // Update bounding box
                    minX = Math.min(minX, atom.x);
                    minY = Math.min(minY, atom.y);
                    minZ = Math.min(minZ, atom.z);
                    maxX = Math.max(maxX, atom.x);
                    maxY = Math.max(maxY, atom.y);
                    maxZ = Math.max(maxZ, atom.z);
                });
            });

            // Add bounding box
            const boxGeometry = new THREE.BoxGeometry(
                maxX - minX + 10,
                maxY - minY + 10,
                maxZ - minZ + 10
            );
            const boxHelper = new THREE.BoxHelper(
                new THREE.Mesh(boxGeometry),
                0x808080
            );
            boxHelper.position.set(
                (minX + maxX) / 2,
                (minY + maxY) / 2,
                (minZ + maxZ) / 2
            );
            scene.add(boxHelper);
            atomMeshes.push(boxHelper);
        }

        // Three.js animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize p5.js for contact map
        function initP5() {
            if (p5Instance) p5Instance.remove();

            const sketch = (p) => {
                p.setup = function() {
                    const size = parseInt(document.getElementById('slider-plot-size').value);
                    const canvas = p.createCanvas(size, size);
                    canvas.parent('canvas-container');
                    p.noLoop();
                };

                p.draw = function() {
                    const size = p.width;
                    const rmsd = parseFloat(document.getElementById('slider-rmsd').value);
                    const resX = parseInt(document.getElementById('slider-residue-x').value);
                    const resY = parseInt(document.getElementById('slider-residue-y').value);

                    p.background(255);
                    p.strokeWeight(1);

                    // Draw axes
                    p.stroke(128);
                    p.line(40, size - 40, size - 40, size - 40); // Bottom axis
                    p.line(40, 40, 40, size - 40); // Left axis
                    p.line(40, 40, size - 40, 40); // Top axis
                    p.line(size - 40, 40, size - 40, size - 40); // Right axis

                    // Draw tick marks and labels
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(10);
                    p.fill(0);
                    const tickPositions = [1, 100, 200, 300, 400];
                    tickPositions.forEach(pos => {
                        const pixelPos = p.map(pos, 1, 400, 40, size - 40);

                        // Bottom
                        p.line(pixelPos, size - 40, pixelPos, size - 35);
                        p.text(pos, pixelPos, size - 25);

                        // Top
                        p.line(pixelPos, 40, pixelPos, 35);
                        p.text(pos, pixelPos, 25);

                        // Left
                        p.line(40, pixelPos, 35, pixelPos);
                        p.text(pos, 20, pixelPos);

                        // Right
                        p.line(size - 40, pixelPos, size - 35, pixelPos);
                        p.text(pos, size - 20, pixelPos);
                    });

                    // Draw contact map
                    p.stroke(0);
                    p.strokeWeight(1);
                    for (let i = 0; i < proteinData.length; i++) {
                        for (let j = 0; j < proteinData.length; j++) {
                            if (distanceMatrix[i][j] <= rmsd) {
                                const x = p.map(i + 1, 1, 400, 40, size - 40);
                                const y = p.map(j + 1, 1, 400, 40, size - 40);
                                p.point(x, y);
                            }
                        }
                    }

                    // Draw crosshairs
                    p.stroke(255, 0, 0);
                    p.strokeWeight(2);
                    const xPos = p.map(resX, 1, 400, 40, size - 40);
                    const yPos = p.map(resY, 1, 400, 40, size - 40);
                    p.line(xPos, 40, xPos, size - 40); // Vertical line
                    p.line(40, yPos, size - 40, yPos); // Horizontal line
                };
            };

            p5Instance = new p5(sketch);
        }

        // Update info display
        function updateInfoDisplay() {
            const browseSeq = parseInt(document.getElementById('slider-browse-sequence').value);
            const resX = parseInt(document.getElementById('slider-residue-x').value);
            const resY = parseInt(document.getElementById('slider-residue-y').value);

            const resA = proteinData[browseSeq - 1];
            const resXData = proteinData[resX - 1];
            const resYData = proteinData[resY - 1];

            const info = `Res A: ${resA.resName}${resA.resSeq} | Res X: ${resXData.resName}${resXData.resSeq} | Res Y: ${resYData.resName}${resYData.resSeq}`;
            document.getElementById('info-display').textContent = info;
        }

        // Switch view
        function switchView(view) {
            currentView = view;
            const container = document.getElementById('canvas-container');
            container.innerHTML = '';

            if (view === 'crystallography') {
                document.getElementById('btn-crystallography').classList.add('active');
                document.getElementById('btn-contact-map').classList.remove('active');
                if (p5Instance) {
                    p5Instance.remove();
                    p5Instance = null;
                }
                initThreeJS();
            } else {
                document.getElementById('btn-crystallography').classList.remove('active');
                document.getElementById('btn-contact-map').classList.add('active');
                if (renderer) {
                    renderer.dispose();
                    renderer = null;
                }
                initP5();
            }
        }

        // Resize canvas
        function resizeCanvas() {
            const size = parseInt(document.getElementById('slider-plot-size').value);

            if (currentView === 'crystallography' && renderer) {
                camera.aspect = 1;
                camera.updateProjectionMatrix();
                renderer.setSize(size, size);
            } else if (currentView === 'contact-map' && p5Instance) {
                p5Instance.resizeCanvas(size, size);
                p5Instance.redraw();
            }
        }

        // Find next/previous residue of type
        function findNextResidueOfType(currentIndex, targetType, direction) {
            let index = currentIndex;
            const maxIterations = proteinData.length;
            let iterations = 0;

            while (iterations < maxIterations) {
                index += direction;
                if (index >= proteinData.length) index = 0;
                if (index < 0) index = proteinData.length - 1;

                if (proteinData[index].resType === targetType) {
                    return index + 1; // Return 1-based index
                }
                iterations++;
            }

            return currentIndex + 1; // Return original if none found
        }

        // Event listeners
        document.getElementById('btn-crystallography').addEventListener('click', () => switchView('crystallography'));
        document.getElementById('btn-contact-map').addEventListener('click', () => switchView('contact-map'));

        document.getElementById('checkbox-lateral-chains').addEventListener('change', () => {
            if (currentView === 'crystallography') drawProteinStructure();
        });

        document.getElementById('slider-gray-level').addEventListener('input', (e) => {
            document.getElementById('gray-level-value').textContent = e.target.value;
            if (currentView === 'crystallography') drawProteinStructure();
        });

        document.getElementById('slider-browse-sequence').addEventListener('input', (e) => {
            document.getElementById('browse-sequence-value').textContent = e.target.value;
            updateInfoDisplay();
            if (currentView === 'crystallography') drawProteinStructure();
        });

        document.getElementById('select-residue').addEventListener('change', (e) => {
            const currentSeq = parseInt(document.getElementById('slider-browse-sequence').value);
            const newSeq = findNextResidueOfType(currentSeq - 1, e.target.value, 1);
            document.getElementById('slider-browse-sequence').value = newSeq;
            document.getElementById('browse-sequence-value').textContent = newSeq;
            updateInfoDisplay();
            if (currentView === 'crystallography') drawProteinStructure();
        });

        document.getElementById('slider-residue-x').addEventListener('input', (e) => {
            document.getElementById('residue-x-value').textContent = e.target.value;
            updateInfoDisplay();
            if (currentView === 'crystallography') {
                drawProteinStructure();
            } else {
                p5Instance.redraw();
            }
        });

        document.getElementById('slider-residue-y').addEventListener('input', (e) => {
            document.getElementById('residue-y-value').textContent = e.target.value;
            updateInfoDisplay();
            if (currentView === 'crystallography') {
                drawProteinStructure();
            } else {
                p5Instance.redraw();
            }
        });

        document.getElementById('slider-rmsd').addEventListener('input', (e) => {
            document.getElementById('rmsd-value').textContent = e.target.value;
            if (currentView === 'contact-map') p5Instance.redraw();
        });

        document.getElementById('slider-plot-size').addEventListener('input', (e) => {
            document.getElementById('plot-size-value').textContent = e.target.value;
            resizeCanvas();
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            if (currentView === 'crystallography' && camera && controls) {
                camera.position.set(100, 100, 100);
                camera.lookAt(0, 0, 0);
                controls.reset();
            }
        });

        // Initialize
        calculateDistanceMatrix();
        updateInfoDisplay();
        switchView('crystallography');
    </script>
</body>
</html>
