                    currentData.forEach(item => {
                        if (!item.found && hintCount < 3) {
                            const {x, y} = mapToPixels(item.lon, item.lat, p);
                            p.text(item.name, x + 10, y);
                            hintCount++;
                        }
                    });
                }

                // Draw cursor marker
                p.fill(173, 216, 230, 150);
                p.noStroke();
                p.circle(p.mouseX, p.mouseY, 30);
            };

            p.mouseMoved = function() {
                if (p.mouseX >= 0 && p.mouseX <= p.width && p.mouseY >= 0 && p.mouseY <= p.height) {
                    const {lon, lat} = mapToGeo(p.mouseX, p.mouseY, p);
                    document.getElementById('p-position').textContent = `Current position {${Math.round(lon)}, ${Math.round(lat)}}`;
                }
            };

            p.mouseClicked = function() {
                if (p.mouseX >= 0 && p.mouseX <= p.width && p.mouseY >= 0 && p.mouseY <= p.height) {
                    for (let i = 0; i < currentData.length; i++) {
                        const item = currentData[i];
                        if (!item.found) {
                            const {x, y} = mapToPixels(item.lon, item.lat, p);
                            const distance = p.dist(p.mouseX, p.mouseY, x, y);
                            if (distance <= 15) {
                                item.found = true;
                                updateTableCell(i);
                                break;
                            }
                        }
                    }
                }
            };

            sketch = p;
        });

        function mapToPixels(lon, lat, p) {
            const x = p.map(lon, -180, 180, 0, p.width);
            const y = p.map(lat, 90, -90, 0, p.height);
            return {x, y};
        }

        function mapToGeo(x, y, p) {
            const lon = p.map(x, 0, p.width, -180, 180);
            const lat = p.map(y, 0, p.height, 90, -90);
            return {lon, lat};
        }

        function populateTable() {
            const table = document.getElementById('table-quiz');
            table.innerHTML = '';

            const headerRow = table.insertRow();
            const statusRow = table.insertRow();

            currentData.forEach((item, index) => {
                const th = document.createElement('th');
                th.textContent = item.name;
                headerRow.appendChild(th);

                const td = statusRow.insertCell();
                td.textContent = item.found ? 'True' : 'False';
                td.id = `status-${index}`;
                if (item.found) {
                    td.classList.add('found');
                }
            });
        }

        function updateTableCell(index) {
            const cell = document.getElementById(`status-${index}`);
            cell.textContent = 'True';
            cell.classList.add('found');
        }

        function resetQuiz() {
            currentData.forEach(item => {
                item.found = false;
            });
            populateTable();
        }

        // Event listeners
        document.getElementById('radio-rivers').addEventListener('change', function() {
            if (this.checked) {
                currentData = riversData;
                resetQuiz();
            }
        });

        document.getElementById('radio-lakes').addEventListener('change', function() {
            if (this.checked) {
                currentData = lakesData;
                resetQuiz();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', resetQuiz);

        document.getElementById('check-show-locations').addEventListener('change', function() {
            sketch.redraw();
        });

        document.getElementById('check-show-some').addEventListener('change', function() {
            sketch.redraw();
        });
    </script>
</body>
</html>
