            <input type="radio" id="radio-chars-no" name="chars-toggle" value="no">
            <label for="radio-chars-no">no</label>
            <input type="radio" id="radio-chars-yes" name="chars-toggle" value="yes" checked>
            <label for="radio-chars-yes">yes</label>
        </div>

        <div id="viz-container">
            <div id="header-section">
                <p id="original-text">MISSISSIPPI_RIVER</p>
                <p id="encoded-text">1100000101000101001001000011111010011011110101</p>
            </div>
            <div id="main-content">
                <div id="info-panel">
                    <p id="explanation-text">Once at the top, the total frequency becomes 17, which is the total number of characters.</p>
                    <div id="encoding-table">
                        <p>I = 00</p>
                        <p>S = 01</p>
                        <p>P = 100</p>
                        <p>R = 101</p>
                        <p>M = 1100</p>
                        <p>V = 1101</p>
                        <p>E = 1110</p>
                        <p>_ = 1111</p>
                    </div>
                </div>
                <div id="canvas-area"></div>
            </div>
        </div>
    </div>

    <script>
        // State variables
        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;

        // Tree data structure
        const nodes = [
            { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
            { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
            { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
            { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
            { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
            { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
            { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
            { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
            { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
            { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
            { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
            { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
            { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
            { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
            { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
        ];

        // Create a map for quick node lookup
        const nodeMap = {};
        nodes.forEach(node => {
            nodeMap[node.id] = node;
        });

        // Tier button event listeners
        for (let i = 1; i <= 5; i++) {
            document.getElementById(`btn-tier-${i}`).addEventListener('click', function() {
                // Update active button styling
                document.querySelectorAll('#tiers-container button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');

                // Update current tier
                currentTier = i;

                // Update explanation text
                const explanationText = document.getElementById('explanation-text');
                if (i === 1) {
                    explanationText.textContent = "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.";
                } else if (i >= 2 && i <= 4) {
                    explanationText.textContent = "We continue grouping until we get to the highest tier.";
                } else if (i === 5) {
                    explanationText.textContent = "Once at the top, the total frequency becomes 17, which is the total number of characters.";
                }

                // Update encoding table visibility
                const encodingTable = document.getElementById('encoding-table');
                if (currentTier < 2) {
                    encodingTable.style.display = 'none';
                } else {
                    encodingTable.style.display = 'block';
                }

                // Update encoded text visibility
                const encodedText = document.getElementById('encoded-text');
                if (currentTier < 5) {
                    encodedText.style.display = 'none';
                } else {
                    encodedText.style.display = 'block';
                }

                // Trigger redraw
                if (window.redraw) window.redraw();
            });
        }

        // Show binary checkbox event listener
        document.getElementById('checkbox-binary').addEventListener('change', function() {
            showBinary = this.checked;
            if (window.redraw) window.redraw();
        });

        // Characters radio button event listeners
        document.getElementById('radio-chars-yes').addEventListener('change', function() {
            if (this.checked) {
                showNodeChars = true;
                if (window.redraw) window.redraw();
            }
        });

        document.getElementById('radio-chars-no').addEventListener('change', function() {
            if (this.checked) {
                showNodeChars = false;
                if (window.redraw) window.redraw();
            }
        });

        // p5.js sketch
        let sketch = function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(800, 500);
                canvas.parent('canvas-area');
                p.textAlign(p.CENTER, p.CENTER);
            };

            p.draw = function() {
                p.background(255);

                // Draw edges
                nodes.forEach(parent => {
                    if (parent.children.length > 0 && parent.tier <= currentTier) {
                        parent.children.forEach((childId, index) => {
                            const child = nodeMap[childId];
                            if (child && child.tier <= currentTier) {
                                // Draw line
                                p.stroke('#C70000');
                                p.strokeWeight(2);
                                p.line(parent.pos.x, parent.pos.y, child.pos.x, child.pos.y);

                                // Draw binary label if enabled
                                if (showBinary) {
                                    p.push();
                                    p.noStroke();
                                    p.fill('#C70000');
                                    p.textSize(14);
                                    const midX = (parent.pos.x + child.pos.x) / 2;
                                    const midY = (parent.pos.y + child.pos.y) / 2;
                                    const label = index === 0 ? '0' : '1';
                                    p.text(label, midX, midY);
                                    p.pop();
                                }
                            }
                        });
                    }
                });

                // Draw nodes
                nodes.forEach(node => {
                    if (node.tier <= currentTier) {
                        // Draw node rectangle
                        p.fill('#FEFDD5');
                        p.stroke('#C70000');
                        p.strokeWeight(2);
                        p.rectMode(p.CENTER);

                        // Calculate rectangle size based on label
                        let label;
                        if (node.tier === 1 || showNodeChars) {
                            label = `${node.label}(${node.freq})`;
                        } else {
                            label = `(${node.freq})`;
                        }

                        const textWidth = p.textWidth(label) + 20;
                        const rectHeight = 30;

                        p.rect(node.pos.x, node.pos.y, textWidth, rectHeight, 5);

                        // Draw text
                        p.noStroke();
                        p.fill('#333');
                        p.textSize(12);
                        p.text(label, node.pos.x, node.pos.y);
                    }
                });
            };

            // Make redraw available globally
            window.redraw = function() {
                p.redraw();
            };
        };

        // Create p5 instance
        new p5(sketch);
    </script>
</body>
</html>
